INSERT INTO `blog` (`id`, `title`, `content`, `posted_by`, `posted_date`, `status`, `picture`, `ordering`) VALUES
(11, '1 BƯỚC ĐỂ TÌM THẤY ĐAM MÊ TRONG BẠN', '<p><span style=\"font-size:16px\">Đ&atilde; bao giờ bạn cảm thấy m&igrave;nh <strong>v&ocirc; dụng</strong> chưa?</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đ&atilde; lần n&agrave;o bạn nh&igrave;n v&agrave;o c&aacute;ch người kh&aacute;c l&agrave;m việc m&agrave; thấy ghen tỵ với tinh thần của họ chưa?</span></p>\r\n\r\n<p><span style=\"font-size:16px\">V&agrave;, đ&atilde; bao giờ bạn cảm gi&aacute;c muốn thay đổi nhưng lại ph&acirc;n v&acirc;n chưa?</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nếu bạn trả lời &ldquo;C&oacute;&rdquo; cho 2 tr&ecirc;n 3 đ&aacute;p &aacute;n của những c&acirc;u hỏi tr&ecirc;n, th&igrave; bạn ạ, b&agrave;i viết n&agrave;y đang d&agrave;nh cho bạn đấy. Bởi c&oacute; lẽ thứ bạn cần thực sự kh&ocirc;ng phải l&agrave; &ldquo;l&ecirc;n mood&rdquo; hay &ldquo;nghỉ ngơi&rdquo; - như c&aacute;ch m&agrave; mọi người thường khuy&ecirc;n nhủ. Điều bạn cần ch&iacute;nh l&agrave; t&igrave;m cho ra <strong>thứ bạn thực sự đam m&ecirc;</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Phải l&agrave;m thế n&agrave;o mới c&oacute; thể biết bạn th&iacute;ch g&igrave;?</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đầu ti&ecirc;n, h&atilde;y để &yacute; đến những c&ocirc;ng việc bạn tốn nhiều thời gian để thực hiện n&oacute;. C&oacute; lẽ bạn kh&ocirc;ng th&iacute;ch chuy&ecirc;n ng&agrave;nh của m&igrave;nh, kh&ocirc;ng th&iacute;ch c&ocirc;ng việc của m&igrave;nh nhưng nhất định sẽ c&oacute; việc bạn th&iacute;ch l&agrave;m. D&ugrave; việc n&agrave;y chẳng li&ecirc;n quan g&igrave; đến học tập hay c&ocirc;ng việc, nhưng bạn vẫn bằng l&ograve;ng tốn thời gian cho n&oacute;.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trong v&ocirc; thức, c&oacute; thể bạn sẽ kh&ocirc;ng để &yacute; đến việc m&igrave;nh đ&atilde; tốn thời gian cho việc ấy bao nhi&ecirc;u, bạn cũng kh&ocirc;ng để &yacute; rằng việc đ&oacute; mang lại lợi &iacute;ch g&igrave; cho m&igrave;nh. Bạn cứ l&agrave;m, cứ l&agrave;m, bạn kh&ocirc;ng gh&eacute;t n&oacute;, bạn kh&ocirc;ng cảm thấy ch&aacute;n nản khi l&agrave;m n&oacute;. Ngo&agrave;i việc n&oacute; - thứ bạn th&iacute;ch dần dần trở th&agrave;nh th&oacute;i quen h&agrave;ng ng&agrave;y của bạn, th&igrave; n&oacute; giống như một nguồn năng lượng để mỗi khi bạn căng thẳng bạn sẽ l&agrave;m n&oacute; để tiếp th&ecirc;m tinh thần cho bản th&acirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Sở th&iacute;ch</strong> thoạt nh&igrave;n th&igrave; c&oacute; thể kh&ocirc;ng c&oacute; g&igrave; đặc biệt, thậm ch&iacute; n&oacute; chỉ xếp v&agrave;o h&agrave;ng &ldquo;giải tr&iacute;&rdquo; m&agrave; th&ocirc;i. Nhưng bạn biết đấy, chẳng c&oacute; ai lại đi <strong>đam m&ecirc;</strong> thứ m&agrave; m&igrave;nh kh&ocirc;ng th&iacute;ch cả. V&iacute; như bạn kh&ocirc;ng th&iacute;ch l&agrave;m việc với những con số nhưng lại đam m&ecirc; nghề kế to&aacute;n!? Bạn thấy khập khiễng đ&uacute;ng kh&ocirc;ng?</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đồng &yacute; với nhau rằng bạn c&oacute; thể l&agrave;m giỏi những việc m&agrave; bạn kh&ocirc;ng th&iacute;ch, nhưng bạn sẽ chẳng <strong>đam m&ecirc;</strong> thứ m&agrave; bạn kh&ocirc;ng hề th&iacute;ch đ&acirc;u. Tuy nhi&ecirc;n, bạn cần hiểu rằng đam m&ecirc; của sẽ nằm ẩn m&igrave;nh trong những sở th&iacute;ch của bạn. Tức l&agrave; bạn c&oacute; thể th&iacute;ch h&aacute;t, th&iacute;ch xem phim, th&iacute;ch đi du lịch, th&iacute;ch ăn uống,...nhưng kh&ocirc;ng c&oacute; nghĩa đam m&ecirc; của bạn l&agrave; trở th&agrave;nh ca sĩ, đạo diễn hay nh&agrave; sản xuất phim ảnh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Thật nghi&ecirc;m t&uacute;c suy nghĩ, thứ khiến bạn chưa bao giờ thấy ch&aacute;n, chưa bao giờ bạn ngưng th&ocirc;i kh&ocirc;ng t&igrave;m t&ograve;i, học hỏi về vấn đề đ&oacute; v&agrave; thứ đ&oacute; khiến bạn sẵn s&agrave;ng trả bất cứ gi&aacute; n&agrave;o, sẵn s&agrave;ng học tập, nỗ lực, s&aacute;ng tạo v&igrave; n&oacute;. Gộp tất thảy c&aacute;c yếu tố tr&ecirc;n lại, bạn sẽ nh&igrave;n ra thứ m&agrave; bạn đam m&ecirc;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Đam m&ecirc; c&oacute; thể gi&uacute;p bạn kiếm tiền được kh&ocirc;ng?</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Sở th&iacute;ch</strong> sẽ m&atilde;i chỉ l&agrave; một th&uacute; vui &ldquo;giải tr&iacute;&rdquo; kh&ocirc;ng hơn kh&ocirc;ng k&eacute;m nếu bạn m&atilde;i h&agrave;i l&ograve;ng với n&oacute;, hưởng thụ n&oacute; m&agrave; kh&ocirc;ng ph&aacute;t triển n&oacute;. Chỉ cần bạn bằng l&ograve;ng cố gắng, n&oacute; ho&agrave;n to&agrave;n c&oacute; thể trở th&agrave;nh định hướng ph&aacute;t triển nghề nghiệp của bạn trong tương lai.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bạn biết đấy, sẽ c&oacute; người n&oacute;i, đ&acirc;y chỉ l&agrave;<strong> sở th&iacute;ch</strong> của t&ocirc;i, kh&ocirc;ng ph&ugrave; hợp trở th&agrave;nh nghề nghiệp. <strong><em>&ldquo;Đừng biến sở th&iacute;ch trở th&agrave;nh nghề nghiệp, bạn sẽ hủy hoại sở th&iacute;ch của ch&iacute;nh m&igrave;nh&rdquo;</em></strong> l&agrave; một trong những c&acirc;u n&oacute;i hơi mang t&iacute;nh phi l&yacute;, n&oacute; đ&atilde; cản trở bước tiến của rất nhiều thế hệ người Trung Quốc. Nếu biến sở th&iacute;ch th&agrave;nh nghề nghiệp, sở th&iacute;ch sẽ biến mất, vậy th&igrave; đ&oacute; kh&ocirc;ng phải sở th&iacute;ch, n&oacute; chỉ l&agrave; suy nghĩ nhất thời của bạn m&agrave; th&ocirc;i.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Thật ra, n&oacute;i một c&aacute;ch thẳng thắn, nếu ngay từ đầu bạn đ&atilde; l&agrave;m tốt một c&ocirc;ng việc, đ&oacute; cũng l&agrave; sở trường của bạn, vậy th&igrave; bạn sẽ c&agrave;ng ng&agrave;y c&agrave;ng th&iacute;ch n&oacute;. Ch&uacute;ng ta kh&ocirc;ng phải đều như vậy sao? Sở trường l&agrave; g&igrave; th&igrave; sẽ th&iacute;ch c&aacute;i đ&oacute;. Giống như hồi nhỏ, bạn c&oacute; th&agrave;nh t&iacute;ch học tập tốt m&ocirc;n n&agrave;o sẽ th&iacute;ch m&ocirc;n đ&oacute;. Lẽ n&agrave;o ch&uacute;ng ta lại th&iacute;ch m&ocirc;n to&aacute;n khi chỉ thi được 8/100 điểm? Ch&uacute;ng ta c&oacute; bị đi&ecirc;n đ&acirc;u? Thế n&ecirc;n thay v&igrave; mất nhiều thời gian suy nghĩ, c&acirc;n nhắc lựa chọn, tốt hơn hết l&agrave; bạn h&atilde;y cố gắng hết sức m&igrave;nh, xem giới hạn của m&igrave;nh đến đ&acirc;u.</span></p>\r\n', 'Quang Nguyễn', '2022-08-05 00:00:00', 'active', 'tim-kiem-dong-luc-fahasahg6YMe2B.jpg', 8),
(12, 'REVIEW VỀ SÁCH MẮT BIẾC', '<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\">Tuy rằng l&agrave; một t&aacute;c phẩm m&agrave; nh&agrave; văn Nguyễn Nhật &Aacute;nh viết từ những năm 90 của thế kỉ trước,&nbsp; Mắt biếc&nbsp;lại trở th&agrave;nh một &ldquo;hit&rdquo; lớn v&agrave; sau gần 30 năm, chuyện t&igrave;nh của H&agrave; Lan v&agrave; Ngạn cuối c&ugrave;ng cũng đ&atilde; được chuyển thể th&agrave;nh phim v&agrave;o ng&agrave;y 20 th&aacute;ng 12 năm 2019. Điều g&igrave; đ&atilde; gi&uacute;p cho h&igrave;nh ảnh v&agrave; c&acirc;u chuyện của Mắt Biếc sau bao năm th&aacute;ng vẫn c&ograve;n được ghi s&acirc;u trong l&ograve;ng c&aacute;c thế hệ trẻ Việt Nam cho đến tận ng&agrave;y h&ocirc;m nay?</span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ch&uacute;ng ta h&atilde;y c&ugrave;ng đi qua c&aacute;c kh&iacute;a cạnh kh&aacute;c nhau của t&aacute;c phẩm n&agrave;y để c&ugrave;ng hiểu r&otilde; hơn v&igrave; sao Mắt Biếc lại g&acirc;y một dấu ấn lớn như vậy trong l&ograve;ng c&aacute;c bạn độc giả trẻ nh&eacute;!</span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>1. C&aacute;c nh&acirc;n vật ch&iacute;nh</strong></span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ngạn: nam ch&iacute;nh của truyện - một ch&agrave;ng trai v&ugrave;ng qu&ecirc; nh&uacute;t nh&aacute;t, rụt r&egrave;, sống nội t&acirc;m nhưng c&oacute; một tr&aacute;i tim nh&acirc;n hậu v&agrave; t&iacute;nh t&igrave;nh chất ph&aacute;t. Ngạn th&iacute;ch những g&igrave; cổ điển, &acirc;m thầm v&agrave; mộc mạc của v&ugrave;ng qu&ecirc;. Ngạn đem l&ograve;ng y&ecirc;u H&agrave; Lan từ thuở b&eacute; v&agrave; c&ugrave;ng c&ocirc; l&ecirc;n th&agrave;nh thị học khi cả hai đ&atilde; lớn.</span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\">H&agrave; Lan (Mắt Biếc)&nbsp;</span><span style=\"font-family:Tahoma,Geneva,sans-serif\">: nữ ch&iacute;nh của truyện &ndash; bạn th&acirc;n của Ngạn từ hồi cởi truồng tắm mưa ở l&agrave;ng, c&oacute; xuất th&acirc;n từ v&ugrave;ng qu&ecirc; nhưng lại đem l&ograve;ng y&ecirc;u chốn th&agrave;nh thị xa hoa. C&ocirc; l&ecirc;n th&agrave;nh phố học ở độ tuổi trăng tr&ograve;n v&agrave; đ&oacute; l&agrave; sự bắt đầu của một chuỗi sự kiện m&agrave; sẽ thay đổi cuộc đời của c&ocirc; v&agrave; Ngạn m&atilde;i m&atilde;i.</span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\">Dũng: Bạn trai mới của H&agrave; Lan tại th&agrave;nh phố, một thanh ni&ecirc;n nh&agrave; gi&agrave;u c&oacute;, s&agrave;nh điệu nhưng lại lăng nhăng, người l&agrave;m cho Ngạn đau khổ trong chuyện t&igrave;nh y&ecirc;u v&agrave; l&agrave; người cho H&agrave; Lan biết được x&atilde; hội tr&ecirc;n th&agrave;nh thị phức tạp cỡ n&agrave;o.</span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>2. Cốt truyện ch&iacute;nh:</strong></span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\">C&acirc;u chuyện kể về nam nh&acirc;n vật ch&iacute;nh - Ngạn. Ngạn lớn l&ecirc;n c&ugrave;ng với H&agrave; Lan, c&ocirc; bạn h&agrave;ng x&oacute;m c&oacute; đ&ocirc;i &ldquo;mắt biếc&rdquo; tr&ograve;n, to v&agrave; long lanh như hai hột nh&atilde;n. Cả Ngạn v&agrave; H&agrave; Lan c&ugrave;ng xuất th&acirc;n từ v&ugrave;ng qu&ecirc; ngh&egrave;o v&agrave; hai người biết nhau khi cả hai mới chỉ l&agrave; những đứa trẻ ng&acirc;y thơ, trong s&aacute;ng. T&igrave;nh bạn trẻ thơ ấy dần trở th&agrave;nh đơn phương v&agrave; thầm lặng m&agrave; Ngạn d&agrave;nh cho H&agrave; Lan.</span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\">Khi cả hai lớn hơn một ch&uacute;t, cả Ngạn v&agrave; Mắt Biếc&nbsp;đều rời xa ngồi l&agrave;ng xưa của m&igrave;nh để l&ecirc;n th&agrave;nh phố học. Tuy rằng t&igrave;nh cảm của Ngạn vẫn lu&ocirc;n hướng ph&iacute;a H&agrave; Lan v&agrave; về l&agrave;ng, Mắt Biếc lại kh&ocirc;ng thể cản được những c&aacute;m dỗ xa hoa nơi th&agrave;nh thị v&agrave; lỡ sa v&agrave;o v&ograve;ng tay của Dũng. Dũng l&agrave;m H&agrave; Lan tổn thương rất nhiều, v&agrave; c&ocirc; lu&ocirc;n t&igrave;m đến Ngạn như một nơi để nương tựa v&agrave; bầu bạn t&acirc;m sự. Việc Ngạn mất H&agrave; Lan v&agrave;o tay Dũng đ&atilde; dẫn đến rất nhiều chuỗi sự kiện bất ngờ sau đ&oacute; m&agrave; c&aacute;c bạn c&oacute; thể tự m&igrave;nh kh&aacute;m ph&aacute; khi đọc truyện nh&eacute;!</span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong>3. Lời kết</strong></span></tt></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><tt><span style=\"font-family:Tahoma,Geneva,sans-serif\">Với cốt l&otilde;i l&agrave; c&aacute;c nh&acirc;n vật m&agrave; những bạn độc giả c&oacute; thể dễ d&agrave;ng tự tưởng tượng bản th&acirc;n m&igrave;nh trong c&aacute;c ho&agrave;n cảnh tương tự; cốt truyện dễ hiểu nhưng kh&ocirc;ng k&eacute;m phần li k&igrave;, bất ngờ, Mắt Biếc đ&atilde; đ&aacute;nh dấu được chỗ đứng quan trọng của m&igrave;nh trong kho t&agrave;ng chuyện của t&aacute;c giả Nguyễn Nhật &Aacute;nh n&oacute;i ri&ecirc;ng v&agrave; c&aacute;c phẩm văn học hiện đại Việt Nam n&oacute;i chung. Qua Mắt Biếc, ch&uacute;ng ta lại được nghe giọng văn tuyệt vời đầy truyền cảm nhưng mang t&iacute;nh chất hồn nhi&ecirc;n, trong s&aacute;ng, chất ph&aacute;t v&ugrave;ng n&ocirc;ng th&ocirc;n của t&aacute;c giả Nguyễn Nhật &Aacute;nh. C&aacute;c bạn h&atilde;y d&agrave;nh thời gian ra đọc để c&oacute; thể tự cảm nhận cho ch&iacute;nh m&igrave;nh nh&eacute;!</span></tt></span></p>\r\n', 'Quang Nguyễn', '2022-08-05 02:21:04', 'active', '4_32_15tksqQoE.jpg', 3),
(13, 'TÌNH YÊU LÀ VÔ TƯ DÂNG HIẾN?', '<p><span style=\"font-size:16px\"><strong>H&atilde;y y&ecirc;u thương một người v&igrave; bản th&acirc;n m&igrave;nh</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bạn lu&ocirc;n mong mỏi t&igrave;m kiếm một người y&ecirc;u m&igrave;nh hơn cả bản th&acirc;n họ, bạn tin rằng chỉ cần y&ecirc;u nhau th&igrave; cả hai sẽ l&agrave; một. Nhưng khi bạn y&ecirc;u một người, sự thật trong một kh&iacute;a cạnh của thế giới quan th&igrave; kh&ocirc;ng phải v&igrave; anh ta, m&agrave; v&igrave; ch&iacute;nh bản th&acirc;n. Ch&uacute;ng ta cảm thấy hạnh ph&uacute;c khi ngắm anh ta, c&ugrave;ng anh ta dạo bộ, ngủ c&ugrave;ng anh ta. Bạn v&igrave; người đ&agrave;n &ocirc;ng n&agrave;y m&agrave; cho đi, bởi bạn hưởng thụ qu&aacute; tr&igrave;nh cho đi n&agrave;y, trong qu&aacute; tr&igrave;nh đ&oacute;, bạn cảm thấy hạnh ph&uacute;c, cảm thấy bản th&acirc;n c&oacute; gi&aacute; trị.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trong t&igrave;nh y&ecirc;u, sẽ c&oacute; l&uacute;c người ta m&ecirc; muội, sẽ c&oacute; t&iacute;nh người ta tỉnh t&aacute;o, c&oacute; l&uacute;c bất cần, c&oacute; l&uacute;c lại t&iacute;nh to&aacute;n chi li từng bước đi. Bởi v&igrave; l&agrave; y&ecirc;u, l&agrave; y&ecirc;u n&ecirc;n chẳng c&oacute; việc g&igrave; l&agrave; giới hạn cả. Bạn cho rằng v&igrave; kh&ocirc;ng t&iacute;nh to&aacute;n với nhau, v&igrave; coi nhau như người th&acirc;n n&ecirc;n h&agrave; tất mọi chuyện phải r&otilde; r&agrave;ng. Song nếu một ng&agrave;y người ấy rời xa bạn, những mất m&aacute;t m&agrave; họ g&acirc;y ra cho bạn, ai sẽ g&aacute;nh chịu n&oacute; đ&acirc;y? - L&agrave; bạn. Đ&uacute;ng, chỉ c&oacute; duy nhất một m&igrave;nh bạn. Vậy n&ecirc;n, trong bất cứ mọi chuyện, h&atilde;y lu&ocirc;n chừa cho m&igrave;nh đường lui, t&igrave;nh y&ecirc;u cũng kh&ocirc;ng ngoại lệ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">V&igrave; bản th&acirc;n muốn được sống hạnh ph&uacute;c hơn, vui vẻ hơn, kh&aacute;m ph&aacute; nhiều kh&iacute;a cạnh cuộc sống h&ocirc;n nh&acirc;n, t&igrave;nh y&ecirc;u hơn n&ecirc;n bạn chọn y&ecirc;u. Kh&ocirc;ng ai n&oacute;i bạn &iacute;ch kỷ khi bạn y&ecirc;u ai đ&oacute; v&igrave; bạn đ&acirc;u, bởi t&igrave;nh y&ecirc;u ấy khiến trạng th&aacute;i của lu&ocirc;n ở mức tốt nhất. Thử hỏi, nếu một người bạn của bạn y&ecirc;u một người m&agrave; họ lu&ocirc;n cảm thấy kh&oacute; chịu, lu&ocirc;n cảm thấy bất an, bồn chồn, lo sợ, thậm ch&iacute; l&agrave; &ldquo;ph&aacute;t đi&ecirc;n&rdquo; th&igrave; bạn sẽ khuy&ecirc;n họ từ bỏ đ&uacute;ng kh&ocirc;ng?</span></p>\r\n', 'Quang Nguyễn', '2022-08-05 02:27:48', 'active', '56_1Ir2CJWcp.jpg', 2),
(14, 'SÁCH KINH DOANH HAY NHẤT NÊN ĐỌC', '<p><span style=\"font-size:16px\">Tr&ecirc;n thị trường hiện nay c&oacute; v&ocirc; v&agrave;n quyển s&aacute;ch kinh doanh hay&nbsp;điều n&agrave;y gi&uacute;p bạn c&oacute; nhiều sự lựa chọn. Nhưng ch&uacute;ng cũng sẽ mang đến cho bạn kh&ocirc;ng &iacute;t bối rối băn khoăn trong việc chọn s&aacute;ch. Thế n&ecirc;n h&ocirc;m nay m&igrave;nh quyết chia sẻ một v&agrave;i quyển s&aacute;ch kinh doanh được m&igrave;nh v&agrave; bạn b&egrave; cho l&agrave; hay nhất. Hiển nhi&ecirc;n ch&uacute;ng cũng được đ&aacute;nh gi&aacute; rất ti&ecirc;u cực tr&ecirc;n nhiều diễn đ&agrave;n lẫn c&aacute;c trang mạng x&atilde; hội. Qua đ&oacute; đ&acirc;y cũng l&agrave; những quyển s&aacute;ch về kinh doanh đ&aacute;nh thức tiềm năng l&agrave;m gi&agrave;u trong h&agrave;ng triệu người m&agrave; bạn c&oacute; thể tham khảo qua.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:18px\">13 Nguy&ecirc;n Tắc Nghĩ Gi&agrave;u L&agrave;m Gi&agrave;u</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">13 Nguy&ecirc;n Tắc Nghĩ Gi&agrave;u L&agrave;m Gi&agrave;u hay Think And Grow Rich thật sự phải n&oacute;i l&agrave; một tuyệt t&aacute;c, một cuốn s&aacute;ch kinh doanh n&ecirc;n đọc. Bởi những nội dung từ cuốn s&aacute;ch n&agrave;y mang lại kh&aacute; thực tế v&agrave; rất dễ h&igrave;nh dung kh&ocirc;ng mơ hồ. Đ&uacute;ng như c&aacute;i t&ecirc;n của ch&uacute;ng, t&aacute;c giả vạch ra r&otilde; r&agrave;ng 13 quy tắc thay đổi để l&agrave;m gi&agrave;u, dẫn đến sự th&agrave;nh c&ocirc;ng trong cuộc sống. Ở mỗi nguy&ecirc;n tắc cuốn s&aacute;ch sẽ cho bạn thấy v&igrave; sao ch&uacute;ng lại đ&uacute;ng v&agrave; đ&uacute;ng ở chỗ n&agrave;o.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Cuốn s&aacute;ch cũng n&oacute;i l&ecirc;n việc l&agrave;m gi&agrave;u được h&igrave;nh th&agrave;nh từ trong suy nghĩ, chỉ khi ta nghĩ gi&agrave;u ta mới c&oacute; thể l&agrave;m gi&agrave;u ( ở đ&acirc;y m&igrave;nh kh&ocirc;ng đề cập đến những con người si&ecirc;u may mắn c&oacute; thể tr&uacute;ng số với giải thưởng h&agrave;ng triệu đ&ocirc; đ&acirc;u nh&eacute; ). B&ecirc;n cạnh đ&oacute; 13 nguy&ecirc;n tắc nghĩ gi&agrave;u l&agrave;m gi&agrave;u sẽ gi&uacute;p bạn suy nghĩ t&iacute;ch cực hơn. Mặc d&ugrave; cuốn s&aacute;ch được viết từ rất l&acirc;u, nhưng những nguy&ecirc;n tắc m&agrave; cuốn s&aacute;ch đề cập tới th&igrave; vẫn c&oacute; thể &aacute;p dụng rất tốt v&agrave; ch&uacute;ng th&igrave; chưa bao giờ l&agrave; lỗi thời.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Dạy con l&agrave;m gi&agrave;u - Robert.Kiyosaki</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Được đ&aacute;nh gi&aacute; l&agrave; một trong những s&aacute;ch kinh doanh hay nhất, đ&acirc;y kh&ocirc;ng chỉ l&agrave; một cuốn s&aacute;ch m&agrave; l&agrave; bộ gồm 13 cuốn của Robert.Kiyosaki - l&agrave; một trong những người c&oacute; c&ocirc;ng lớn trong việc truyền cảm hứng cho nhiều người vượt qua kh&oacute; khăn, thay đổi cuộc sống để l&agrave;m gi&agrave;u. Nếu bạn cũng muốn đọc những quyển s&aacute;ch kinh doanh hay về c&aacute;ch chia sẻ l&agrave;m gi&agrave;u hay b&ecirc;n cạnh đ&oacute; l&agrave; truyền cảm hứng th&igrave; đ&acirc;y quả l&agrave; bộ s&aacute;ch bạn kh&ocirc;ng n&ecirc;n bỏ lỡ. Mặc d&ugrave; với tựa s&aacute;ch l&agrave; dạy con l&agrave;m gi&agrave;u nhưng thật ra đ&acirc;y ch&iacute;nh l&agrave; bộ s&aacute;ch bước đầu thay đổi tư duy lẫn c&aacute;i nh&igrave;n trong bạn.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Một điều kh&aacute; đặc biệt được m&igrave;nh để &yacute; trong quyển s&aacute;ch l&agrave; việc t&aacute;c giả chia sẻ c&aacute;ch quản l&yacute; t&agrave;i ch&iacute;nh. Điều n&agrave;y khiến m&igrave;nh nhận thức được l&yacute; do v&igrave; sao người gi&agrave;u tiền của họ lu&ocirc;n được đẻ ra. Bằng với lời văn b&igrave;nh dị, ch&acirc;n thật, với những c&acirc;u chuyện rất dễ h&igrave;nh dung sẽ khiến đọc giả dễ d&agrave;ng ch&igrave;m m&igrave;nh trong quyển s&aacute;ch.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>Nh&agrave; đầu từ th&ocirc;ng minh - Benjamin Graham</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&oacute; lẽ đ&acirc;y l&agrave; quyển s&aacute;ch một ch&uacute;t kh&oacute; hiểu với những kiến thức kh&aacute; mơ hồ đối với những người &iacute;t quan t&acirc;m đến lĩnh vực t&agrave;i ch&iacute;nh. Nhưng đ&acirc;y thật sự l&agrave; một quyển s&aacute;ch kinh tế hay về t&agrave;i ch&iacute;nh cho những bạn sinh vi&ecirc;n đang theo học hay những người l&agrave;m việc trong ng&agrave;nh. N&oacute;i sơ qua về t&aacute;c giả th&igrave; Benjamin Graham l&agrave; một người thầy vĩ đại v&agrave; đ&aacute;ng k&iacute;nh nhất của tỉ ph&uacute; gi&agrave;u thứ 3 thế giới - Warren Buffett (theo Forbes). Benjamin Graham c&ograve;n l&agrave; một nh&agrave; đầu tư đại t&agrave;i trong lĩnh vực đầu tư v&agrave;o gi&aacute; trị của c&ocirc;ng ty. Điều n&agrave;y phần n&agrave;o chứng tỏ rằng những g&igrave; được &ocirc;ng viết l&agrave; những kinh nghiệm sương m&aacute;u &ocirc;ng từng trải vượt qua nỗi sợ h&atilde;i của bản th&acirc;n để dẫn đến con đường th&agrave;nh c&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trong cuốn s&aacute;ch n&agrave;y Graham đưa ra những nguy&ecirc;n tắc về t&agrave;i ch&iacute;nh trong đầu tư từ đ&oacute; vạch ra những lối đi đ&uacute;ng đắn. Cuốn s&aacute;ch đ&atilde; được cập nhật để ph&ugrave; hợp với nền t&agrave;i ch&iacute;nh hiện tại nhưng gi&aacute; trị nguy&ecirc;n tắc Benjamin Graham vẫn được giữ vững.Với nội dung rất đ&aacute;ng để t&igrave;m hiểu đ&acirc;y sẽ l&agrave; quyển s&aacute;ch kinh doanh n&ecirc;n đọc được g&oacute;p phần trong kệ s&aacute;ch của bạn.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tr&ecirc;n đ&acirc;y l&agrave; 3 tựa s&aacute;ch về lĩnh vực kinh doanh đ&aacute;nh thức tiềm năng, thay đổi suy nghĩ trong con người bạn. Hi vọng qua những tựa s&aacute;ch được m&igrave;nh cho l&agrave; hay nhất trong lĩnh vực sẽ gi&uacute;p bạn c&oacute; động lực, nền tảng kiến thức để bắt đầu c&ocirc;ng cuộc thay đổi cuộc đời của m&igrave;nh. Kiến thực lẫn kinh nghiệm thực tiễn bạn đều c&oacute; thể r&uacute;t ra từ những tựa s&aacute;ch tr&ecirc;n c&ograve;n l&agrave;m được hay kh&ocirc;ng l&agrave; ở ch&iacute;nh bạn. H&atilde;y chia sẻ những tựa s&aacute;ch n&agrave;y đến với những người th&acirc;n, bạn b&egrave; quanh ta để để ch&uacute;ng ta c&ugrave;ng nhau ph&aacute;t triển x&acirc;y dựng một x&atilde; hội, một thế giới kh&aacute;c. Nếu bạn biết một tựa s&aacute;ch kinh doanh đ&aacute;ng đọc n&agrave;o h&atilde;y b&igrave;nh luận chia sẻ v&agrave; để lại đ&aacute;nh gi&aacute; cho quyển s&aacute;ch đ&oacute; nh&eacute;.</span></p>\r\n', 'Quang Nguyễn', '2022-08-05 02:29:41', 'active', 'Untitled-1_56WTHuijJv.png', 4),
(15, 'Điều khoản sử dụng', '<p>Ch&agrave;o mừng qu&iacute; kh&aacute;ch đến mua sắm tại BOOKSTORE. Sau khi truy cập v&agrave;o website BOOKSTORE để tham khảo hoặc mua sắm, qu&iacute; kh&aacute;ch đ&atilde; đồng &yacute; tu&acirc;n thủ v&agrave; r&agrave;ng buộc với những quy định của BOOKSTORE. Vui l&ograve;ng xem kỹ c&aacute;c quy định v&agrave; hợp t&aacute;c với ch&uacute;ng t&ocirc;i để x&acirc;y dựng 1 website BOOKSTORE ng&agrave;y c&agrave;ng th&acirc;n thiện v&agrave; phục vụ tốt những y&ecirc;u cầu của ch&iacute;nh qu&iacute; kh&aacute;ch. Ngo&agrave;i ra, nếu c&oacute; bất cứ c&acirc;u hỏi n&agrave;o về những thỏa thuận tr&ecirc;n đ&acirc;y, vui l&ograve;ng email cho ch&uacute;ng t&ocirc;i qua địa chỉ <a href=\"mailto:support@bookstore.com\">n.nquanght@gmail.com</a></p>\r\n\r\n<p>T&agrave;i khoản của kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>Khi sử dụng dịch vụ BOOKSTORE, qu&iacute; kh&aacute;ch sẽ cung cấp cho ch&uacute;ng t&ocirc;i th&ocirc;ng tin về địa chỉ email, mật khẩu v&agrave; họ t&ecirc;n để c&oacute; được 1 t&agrave;i khoản tại đ&acirc;y. Việc sử dụng v&agrave; bảo mật th&ocirc;ng tin t&agrave;i khoản l&agrave; tr&aacute;ch nhiệm v&agrave; quyền lợi của qu&iacute; kh&aacute;ch khi sử dụng BOOKSTORE. Ngo&agrave;i ra, những th&ocirc;ng tin kh&aacute;c trong t&agrave;i khoản như t&ecirc;n tuổi, địa chỉ.... l&agrave; những th&ocirc;ng tin sẽ gi&uacute;p BOOKSTORE phục vụ qu&iacute; kh&aacute;ch tốt nhất. Trong trường hợp th&ocirc;ng tin do qu&iacute; kh&aacute;ch cung cấp kh&ocirc;ng đầy đủ hoặc sai dẫn đến việc kh&ocirc;ng thể giao h&agrave;ng cho qu&iacute; kh&aacute;ch, ch&uacute;ng t&ocirc;i c&oacute; quyền đ&igrave;nh chỉ hoặc từ chối phục vụ, giao h&agrave;ng m&agrave; kh&ocirc;ng phải chịu bất cứ tr&aacute;ch nhiệm n&agrave;o đối với qu&iacute; kh&aacute;ch. Khi c&oacute; những thay đổi th&ocirc;ng tin của qu&iacute; kh&aacute;ch, vui l&ograve;ng cập nhật lại th&ocirc;ng tin trong t&agrave;i khoản tại BOOKSTORE. Qu&iacute; kh&aacute;ch phải giữ k&iacute;n mật khẩu v&agrave; t&agrave;i khoản, ho&agrave;n to&agrave;n chịu tr&aacute;ch nhiệm đối với tất cả c&aacute;c hoạt động diễn ra th&ocirc;ng qua việc sử dụng mật khẩu hoặc t&agrave;i khoản của m&igrave;nh. Qu&iacute; kh&aacute;ch n&ecirc;n đảm bảo tho&aacute;t khỏi t&agrave;i khoản tại BOOKSTORE sau mỗi lần sử dụng để bảo mật th&ocirc;ng tin của m&igrave;nh</p>\r\n\r\n<p>Quyền lợi bảo mật th&ocirc;ng tin của kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>Khi sử dụng dịch vụ tại website BOOKSTORE, qu&iacute; kh&aacute;ch được đảm bảo rằng những th&ocirc;ng tin cung cấp cho ch&uacute;ng t&ocirc;i sẽ chỉ được d&ugrave;ng để n&acirc;ng cao chất lượng dịch vụ d&agrave;nh cho kh&aacute;ch h&agrave;ng của BOOKSTORE v&agrave; sẽ kh&ocirc;ng được chuyển giao cho 1 b&ecirc;n thứ ba n&agrave;o kh&aacute;c v&igrave; mục đ&iacute;ch thương mại. Th&ocirc;ng tin của qu&iacute; kh&aacute;ch tại BOOKSTORE sẽ được ch&uacute;ng t&ocirc;i bảo mật v&agrave; chỉ trong trường hợp ph&aacute;p luật y&ecirc;u cầu, ch&uacute;ng t&ocirc;i sẽ buộc phải cung cấp những th&ocirc;ng tin n&agrave;y cho c&aacute;c cơ quan ph&aacute;p luật.</p>\r\n\r\n<p>Tr&aacute;ch nhiệm của kh&aacute;ch h&agrave;ng khi sử dụng dịch vụ của BOOKSTORE</p>\r\n\r\n<p>Qu&iacute; kh&aacute;ch tuyệt đối kh&ocirc;ng được sử dụng bất kỳ c&ocirc;ng cụ, phương ph&aacute;p n&agrave;o để can thiệp, x&acirc;m nhập bất hợp ph&aacute;p v&agrave;o hệ thống hay l&agrave;m thay đổi cấu tr&uacute;c dữ liệu tại website BOOKSTORE. Qu&iacute; kh&aacute;ch kh&ocirc;ng được c&oacute; những h&agrave;nh động ( thực hiện, cổ vũ) việc can thiệp, x&acirc;m nhập dữ liệu của BOOKSTORE cũng như hệ thống m&aacute;y chủ của ch&uacute;ng t&ocirc;i Ngo&agrave;i ra, xin vui l&ograve;ng th&ocirc;ng b&aacute;o cho quản trị web của BOOKSTORE ngay khi qu&iacute; kh&aacute;ch ph&aacute;t hiện ra lỗi hệ thống theo số điện thoại 0356809728 hoặc email&nbsp;<a href=\"mailto:support@bookstore.com\">n.nquanght@gmail.com</a></p>\r\n\r\n<p>Qu&iacute; kh&aacute;ch kh&ocirc;ng được đưa ra những nhận x&eacute;t, đ&aacute;nh gi&aacute; c&oacute; &yacute; x&uacute;c phạm, quấy rối, l&agrave;m phiền hoặc c&oacute; bất cứ h&agrave;nh vi n&agrave;o thiếu văn h&oacute;a đối với người kh&aacute;c. Kh&ocirc;ng n&ecirc;u ra những nhận x&eacute;t c&oacute; t&iacute;nh ch&iacute;nh trị ( tuy&ecirc;n truyền, chống ph&aacute;, xuy&ecirc;n tạc ch&iacute;nh quyền), kỳ thị t&ocirc;n gi&aacute;o, giới t&iacute;nh, sắc tộc.... Tuyệt đối cấm mọi h&agrave;nh vi mạo nhận, cố &yacute; tạo sự nhầm lẫn m&igrave;nh l&agrave; một kh&aacute;ch h&agrave;ng kh&aacute;c hoặc l&agrave; th&agrave;nh vi&ecirc;n Ban Quản Trị BOOKSTORE</p>\r\n\r\n<p>Tr&aacute;ch nhiệm v&agrave; quyền lợi của BOOKSTORE</p>\r\n\r\n<p>Trong trường hợp c&oacute; những ph&aacute;t sinh ngo&agrave;i &yacute; muốn hoặc tr&aacute;ch nhiệm của m&igrave;nh, BOOKSTORE sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm về mọi tổn thất ph&aacute;t sinh. Ngo&agrave;i ra, ch&uacute;ng t&ocirc;i kh&ocirc;ng cho ph&eacute;p c&aacute;c tổ chức, c&aacute; nh&acirc;n kh&aacute;c quảng b&aacute; sản phẩm tại website BOOKSTORE m&agrave; chưa c&oacute; sự đồng &yacute; bằng văn bản từ BOOKSTORE Corporation. C&aacute;c thỏa thuận v&agrave; quy định trong Điều khoản sử dụng c&oacute; thể thay đổi v&agrave;o bất cứ l&uacute;c n&agrave;o nhưng sẽ được BOOKSTORE Corporation th&ocirc;ng b&aacute;o cụ thể tr&ecirc;n website BOOKSTORE&nbsp;</p>\r\n', 'Quang Nguyễn', '2022-08-05 02:53:04', 'active', '', 1),
(16, 'Chính sách bảo mật', '<p><strong>I. Th&ocirc;ng tin bảo mật.</strong></p>\r\n\r\n<p>Bảo mật th&ocirc;ng tin kh&aacute;ch h&agrave;ng l&agrave; một trong những ưu ti&ecirc;n h&agrave;ng đầu nhằm tạo điều kiện mua sắm tốt nhất cho qu&yacute; kh&aacute;ch tại BOOKSTORE. Ch&uacute;ng t&ocirc;i hiểu sử dụng hợp l&yacute; v&agrave; bảo mật th&ocirc;ng tin sẽ thể hiện sự quan t&acirc;m của BOOKSTORE d&agrave;nh cho qu&iacute; kh&aacute;ch. V&igrave; thế, BOOKSTORE cam kết việc sử dụng th&ocirc;ng tin tr&ecirc;n sẽ chỉ nhằm n&acirc;ng cao chất lượng dịch vụ kh&aacute;ch h&agrave;ng v&agrave; tạo m&ocirc;i trường mua sắm an t&ograve;an, tiện lợi tại BOOKSTORE. Cụ thể, th&ocirc;ng tin c&aacute; nh&acirc;n của qu&yacute; kh&aacute;ch chỉ d&ugrave;ng với c&aacute;c mục đ&iacute;ch sau:</p>\r\n\r\n<p>&nbsp; 1. Số điện thoại v&agrave; địa chỉ Email của qu&yacute; kh&aacute;ch được d&ugrave;ng nhằm&nbsp;n&acirc;ng cao chất lượng dịch vụ hỗ trợ kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>&nbsp; 2. Địa chỉ của qu&yacute; kh&aacute;ch chỉ được d&ugrave;ng để BOOKSTORE thực thiện việc giao h&agrave;ng m&agrave; qu&yacute; kh&aacute;ch đ&atilde; đặt tr&ecirc;n website BOOKSTORE.COM.</p>\r\n\r\n<p>&nbsp; 3. H&agrave;nh vị mua h&agrave;ng của qu&yacute; kh&aacute;ch chỉ gi&uacute;p BOOKSTORE tạo một trải nghiệm mua sắm tốt hơn cho qu&yacute; kh&aacute;ch.&nbsp;</p>\r\n\r\n<p><strong>II.&nbsp;</strong>BOOKSTORE&nbsp;<strong>cam kết bảo vệ th&ocirc;ng tin của kh&aacute;ch h&agrave;ng</strong></p>\r\n\r\n<ul>\r\n	<li>BOOKSTORE&nbsp;cam đoan sẽ kh&ocirc;ng b&aacute;n, chia sẻ dẫn đến l&agrave;m lộ th&ocirc;ng tin c&aacute; nh&acirc;n của qu&yacute; kh&aacute;ch v&igrave; bất cứ mục đ&iacute;ch g&igrave;. Tất cả th&ocirc;ng tin giao dịch giữa qu&yacute; kh&aacute;ch v&agrave; BOOKSTORE sẽ được bảo mật qua phần mềm Secure Sockets Layer (SSL) bằng c&aacute;ch m&atilde; h&oacute;a tất cả th&ocirc;ng tin qu&yacute; kh&aacute;ch nhập v&agrave;o.</li>\r\n	<li>&nbsp;</li>\r\n	<li>BOOKSTORE&nbsp;cam kết kh&ocirc;ng lưu lại bất kỳ th&ocirc;ng tin t&agrave;i khoản ng&acirc;n h&agrave;ng, th&ocirc;ng tin thẻ t&iacute;n dụng v&agrave; th&ocirc;ng tin t&agrave;i ch&iacute;nh của qu&yacute; kh&aacute;ch.</li>\r\n</ul>\r\n\r\n<p><strong>III. Tr&aacute;ch nhiệm của kh&aacute;ch h&agrave;ng.</strong></p>\r\n\r\n<ul>\r\n	<li>Qu&yacute; kh&aacute;ch kh&ocirc;ng n&ecirc;n trao đổi những th&ocirc;ng tin t&agrave;i khoản, thanh to&aacute;n, giao nhận của m&igrave;nh cho 1 b&ecirc;n thứ 3 n&agrave;o kh&aacute;c để tr&aacute;nh r&ograve; rỉ th&ocirc;ng tin. Khi sử dụng chung m&aacute;y t&iacute;nh với nhiều người, vui l&ograve;ng tho&aacute;t khỏi t&agrave;i khoản mỗi khi kh&ocirc;ng sử dụng dịch vụ của BOOKSTORE nữa để tự bảo vệ th&ocirc;ng tin về mật khẩu truy cập của m&igrave;nh.</li>\r\n	<li>&nbsp;</li>\r\n	<li>Ngo&agrave;i ra, qu&yacute; kh&aacute;ch tuyệt đối kh&ocirc;ng được sử dụng bất kỳ h&igrave;nh thức n&agrave;o để can thiệp v&agrave;o hệ thống hay l&agrave;m thay đổi cấu tr&uacute;c dữ liệu. Ch&uacute;ng t&ocirc;i nghi&ecirc;m cấm việc ph&aacute;t t&aacute;n, truyền b&aacute; hay cổ vũ cho bất kỳ hoạt động n&agrave;o nhằm can thiệp, ph&aacute; hoại hay x&acirc;m nhập v&agrave;o dữ liệu của hệ thống website. Mọi vi phạm sẽ bị tước bỏ mọi quyền lợi cũng như sẽ bị truy tố trước ph&aacute;p luật nếu cần thiết. Mọi th&ocirc;ng tin của qu&iacute; kh&aacute;ch tại BOOKSTORE sẽ được ch&uacute;ng t&ocirc;i bảo mật nhưng trong trường hợp ph&aacute;p luật y&ecirc;u cầu, ch&uacute;ng t&ocirc;i buộc phải cung cấp th&ocirc;ng tin n&agrave;y cho cơ quan ph&aacute;p luật</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>BOOKSTORE&nbsp;hiểu rằng quyền lợi của qu&yacute; kh&aacute;ch trong việc bảo vệ th&ocirc;ng tin c&aacute; nh&acirc;n cũng ch&iacute;nh l&agrave; tr&aacute;ch nhiệm của BOOKSTORE, n&ecirc;n trong bất kỳ trường hợp c&oacute; thắc mắc, g&oacute;p &yacute; n&agrave;o li&ecirc;n quan đến ch&iacute;nh s&aacute;ch bảo mật của BOOKSTORE , vui l&ograve;ng li&ecirc;n hệ:</p>\r\n\r\n<p><strong>Tel:</strong>&nbsp;0356809728<br />\r\n<strong>Email:</strong>&nbsp;<a href=\"mailto:info@bookstore.com\">n.nquanght@gmail.com</a></p>\r\n', 'Quang Nguyễn', '2022-08-05 02:55:08', 'active', '', 1),
(17, 'Hợp tác phát hành', '<p>Chương Tr&igrave;nh Đại L&yacute;</p>\r\n\r\n<p>Bookstore.com hiện nay l&agrave; đại l&yacute; cấp 1 của tất cả c&aacute;c Nh&agrave; ph&aacute;t h&agrave;nh v&agrave; Nh&agrave; Xuất Bản tại Việt Nam v&agrave; nước ngo&agrave;i.&nbsp;</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch h&agrave;ng l&agrave; c&aacute;c nh&agrave; s&aacute;ch, c&ocirc;ng ty, trường học, cơ quan... c&oacute; nhu cầu đặt h&agrave;ng số lượng lớn hoặc đặt nhiều tựa s&aacute;ch, vui l&ograve;ng li&ecirc;n hệ với tổng đ&agrave;i của Bookstore.com&nbsp;<strong>0356809728</strong>&nbsp;hoặc email:&nbsp;<a href=\"mailto:training@zend.vn\">n.nquanght@gmail.c</a>om<strong>&nbsp;</strong>để được hỗ trợ đặt h&agrave;ng.</p>\r\n\r\n<p>Hợp T&aacute;c Ph&aacute;t H&agrave;nh V&agrave; Ph&acirc;n Phối S&aacute;ch</p>\r\n\r\n<p>L&agrave; nh&agrave; s&aacute;ch trực tuyến được y&ecirc;u th&iacute;ch nhất 5 năm liền do sở C&ocirc;ng thương Tp Hồ Ch&iacute; Minh Trao tặng, Bookstore.com c&oacute; khả năng đ&aacute;p ứng v&agrave; ph&acirc;n phối s&aacute;ch đến h&agrave;ng triệu độc giả trong v&agrave; ngo&agrave;i nước, đến c&aacute;c đơn vị b&aacute;n lẻ như c&aacute;c nh&agrave; s&aacute;ch ở c&aacute;c địa phương, trường học...</p>\r\n\r\n<p>Ngo&agrave;i ra, Bookstore c&oacute; hệ thống ph&aacute;t h&agrave;nh s&aacute;ch ebook với thư viện lớn nhất hiện nay, ứng dụng tốt nhất với nhiều t&iacute;nh năng ưu việt, dự kiến lượng kh&aacute;ch h&agrave;ng l&ecirc;n đến v&agrave;i triệu người thường xuy&ecirc;n trong v&agrave; ngo&agrave;i nước, sẵn s&agrave;ng đưa c&aacute;c t&aacute;c phẩm của bạn đến với h&agrave;ng triệu người đọc ở Việt Nam v&agrave; tr&ecirc;n to&agrave;n thế giới.</p>\r\n\r\n<p>Nếu bạn l&agrave; t&aacute;c giả s&aacute;ch hoặc l&agrave; đơn vị ph&aacute;t h&agrave;nh s&aacute;ch mới, vui l&ograve;ng li&ecirc;n hệ với bộ phận kinh doanh với đối t&aacute;c qua số điện thoại: <strong>0356809728</strong>&nbsp;để được hỗ trợ th&ocirc;ng tin v&agrave; x&uacute;c tiến đưa s&aacute;ch l&ecirc;n hệ thống.</p>\r\n', 'Quang Nguyễn', '2022-08-05 02:56:10', 'active', '', 1),
(18, 'Phương thức vận chuyển', '<p>Miễn Ph&iacute; Vận Chuyển</p>\r\n\r\n<ul>\r\n	<li>&Aacute;p dụng đơn h&agrave;ng tại nội th&agrave;nh&nbsp;<strong>Hồ Ch&iacute; Minh</strong>&nbsp;v&agrave; nội th&agrave;nh&nbsp;<strong>H&agrave; Nội&nbsp;</strong>gi&aacute; trị&nbsp;<strong>từ 150.000 VNĐ.</strong>&nbsp;Thời gian nhận h&agrave;ng từ 1-3 ng&agrave;y l&agrave;m việc&nbsp;(nội th&agrave;nh Hồ ch&iacute; Minh), 3-7 ng&agrave;y l&agrave;m việc (ngoại th&agrave;nh Hồ Ch&iacute; Minh v&agrave; H&agrave; Nội)&nbsp;kể từ ng&agrave;y c&oacute; đủ s&aacute;ch.</li>\r\n	<li>&nbsp;</li>\r\n	<li>&Aacute;p dụng đơn h&agrave;ng tại ngoại th&agrave;nh&nbsp;<strong>Hồ Ch&iacute; Minh</strong>,<strong>H&agrave; Nội&nbsp;</strong>v&agrave;<strong>&nbsp;</strong><strong>tỉnh th&agrave;nh kh&aacute;c</strong>&nbsp;gi&aacute; trị&nbsp;<strong>từ 250.000 VNĐ.</strong>&nbsp;Thời gian nhận h&agrave;ng từ 3-7 ng&agrave;y l&agrave;m việc theo quy định về thời gian giao h&agrave;ng của bưu điện.&nbsp;</li>\r\n</ul>\r\n\r\n<p><em>* C&aacute;c quận nội th&agrave;nh Hồ Ch&iacute; Minh: 1,2,3,4,5,6,7,8,10,11,12, T&acirc;n B&igrave;nh, T&acirc;n Ph&uacute;, Ph&uacute; Nhuận, B&igrave;nh T&acirc;n, B&igrave;nh Thạnh</em></p>\r\n\r\n<p><em>* C&aacute;c quận nội th&agrave;nh H&agrave; Nội: Đống Đa, Thanh Xu&acirc;n, T&acirc;y Hồ, Ho&agrave;ng Mai, Ho&agrave;ng Kiếm, Hai B&agrave; Trưng, Cầu Giấy, Ba Đ&igrave;nh</em></p>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Giao H&agrave;ng Nhanh Ch&oacute;ng V&agrave; Bảo Đảm</p>\r\n\r\n<p>&nbsp;1. Tr&aacute;ch nhiệm xử l&yacute; đơn h&agrave;ng v&agrave; vận chuyển sản phẩm đến kh&aacute;ch h&agrave;ng do Bookstore.com đảm nhận v&agrave; phụ thuộc v&agrave;o h&igrave;nh thức thanh to&aacute;n v&agrave; vận chuyển cho c&aacute;c sản phẩm m&agrave; kh&aacute;ch h&agrave;ng khai b&aacute;o khi tạo đơn h&agrave;ng.</p>\r\n\r\n<p>&nbsp;2. Bookstore.com sẽ chịu tr&aacute;ch nhiệm đảm bảo kh&aacute;ch h&agrave;ng nhận được h&agrave;ng h&oacute;a đ&uacute;ng như đ&atilde; đặt mua trong thời gian giao h&agrave;ng như cam kết. Trường hợp h&agrave;ng h&oacute;a bị lỗi, hư hại trong qu&aacute; tr&igrave;nh vận chuyển, Bookstore sẽ chấp nhận việc đổi h&agrave;ng mới cho kh&aacute;ch.</p>\r\n\r\n<p>&nbsp;3. Trong trường hợp kh&aacute;ch h&agrave;ng tự &yacute; hủy đơn h&agrave;ng hoặc đơn h&agrave;ng ho&agrave;n th&agrave;nh nhưng kh&ocirc;ng giao cho kh&aacute;ch h&agrave;ng được v&igrave; l&yacute; do th&ocirc;ng tin li&ecirc;n lạc kh&ocirc;ng ch&iacute;nh x&aacute;c, Bookstore.com sẽ hủy đơn h&agrave;ng đ&oacute;.</p>\r\n\r\n<p>&nbsp;4. Với những đơn h&agrave;ng trả tiền trước như:</p>\r\n\r\n<p>&nbsp;- Thanh to&aacute;n bằng thẻ ATM</p>\r\n\r\n<p>&nbsp;- Chuyển tiền Western Union</p>\r\n\r\n<p>&nbsp;- Chuyển khoản ng&acirc;n h&agrave;ng</p>\r\n\r\n<p>&nbsp;Bookstore sẽ x&aacute;c nhận v&agrave; tiến h&agrave;nh gởi h&agrave;ng đi sau khi nhận được thanh to&aacute;n từ ph&iacute;a qu&yacute; kh&aacute;ch. V&igrave; vậy để sớm nhận được h&agrave;ng, qu&yacute; kh&aacute;ch vui l&ograve;ng tiến h&agrave;nh thủ tục chuyển tiền sớm.</p>\r\n\r\n<p>&nbsp;5. Trong trường hợp v&igrave; l&yacute; do bất khả kh&aacute;ng, thời gian giao h&agrave;ng bị chậm so với thời gian quy định, Bookstore sẽ th&ocirc;ng b&aacute;o đến kh&aacute;ch h&agrave;ng. Trong trường hợp kh&aacute;ch h&agrave;ng muốn hủy đơn h&agrave;ng, Bookstore sẽ ho&agrave;n trả tiền lại cho kh&aacute;ch h&agrave;ng (nếu đ&atilde; thanh to&aacute;n trước)</p>\r\n', 'Quang Nguyễn', '2022-08-05 02:57:04', 'active', '', 1),
(19, '5 THỰC TẾ PHŨ PHÀNG BẠN PHẢI CHẤP NHẬN', '<p><span style=\"font-size:14px\"><strong>1. Xinh đẹp l&agrave; một t&iacute;nh từ &aacute;m chỉ mức độ phấn đấu về ngoại h&igrave;nh</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ch&uacute;ng ta thường tưởng rằng, xinh đẹp đều do may mắn, nhờ &ocirc;ng trời thi&ecirc;n vị, thật ra kh&ocirc;ng phải. Dung mạo kh&ocirc;ng chỉ l&agrave; t&agrave;i năng m&agrave; c&ograve;n l&agrave; sự phấn đấu. Nếu c&oacute; một c&ocirc; g&aacute;i đẹp n&oacute;i với bạn, c&ocirc; ấy chưa từng giảm b&eacute;o, chưa từng dưỡng da, chưa từng ch&uacute; &yacute; đến khẩu phần ăn, đ&oacute; chỉ c&oacute; thể l&agrave; do c&ocirc; ấy c&ograve;n trẻ, hoặc c&ocirc; ấy n&oacute;i dối.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trước 20 tuổi, bạn c&oacute; thể tr&aacute;ch bố mẹ v&igrave; m&igrave;nh xấu x&iacute;, nhưng sau 20 tuổi, bạn vẫn xấu, bạn đ&aacute;ng đời! Bởi v&igrave;, nguy&ecirc;n nh&acirc;n phần lớn l&agrave; do bạn lười. Những người xinh đẹp đều đang nỗ lực như vậy, ch&uacute;ng ta xấu x&iacute; l&agrave;m g&igrave; c&oacute; quyền lười. Nhanh ch&oacute;ng đi đắp mặt nạ, xuống lầu chạy hai v&ograve;ng đi. Bạn kh&ocirc;ng thể ngộ nhận, kh&ocirc;ng thể ph&oacute; mặc số phận như vậy! Tr&ecirc;n thế giới n&agrave;y lu&ocirc;n c&oacute; những người xinh đẹp, lu&ocirc;n c&oacute; người c&agrave;ng ng&agrave;y c&agrave;ng xinh đẹp, tại sao người đ&oacute; kh&ocirc;ng thể l&agrave; bạn?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&quot;Đừng n&oacute;i đến chuy&ecirc;n m&ocirc;n n&agrave;y chuy&ecirc;n m&ocirc;n nọ, chỉ ri&ecirc;ng chuyện đẹp th&ocirc;i, cũng l&agrave; một dạng chuy&ecirc;n m&ocirc;n rồi.&quot;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>2. Đừng cố gắng, h&atilde;y nỗ lực hết m&igrave;nh</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Những người lu&ocirc;n phấn đấu hết m&igrave;nh khiến ch&uacute;ng ta cảm động ch&iacute;nh bởi điều đ&oacute;. N&oacute; cũng giải th&iacute;ch l&yacute; do tại sao thi&ecirc;n t&agrave;i thường được ngưỡng mộ, chứ kh&ocirc;ng thể truyền cảm hứng cho người kh&aacute;c. Chỉ những người b&igrave;nh thường kh&ocirc;ng nổi bật vẫn kh&ocirc;ng chịu khuất phục sự tầm thường của bản th&acirc;n, biết r&otilde; l&agrave; kh&ocirc;ng thể nhưng vẫn l&agrave;m, cuối c&ugrave;ng chiến thắng thi&ecirc;n t&agrave;i kiệt xuất, những người đ&oacute; mới khiến ch&uacute;ng ta k&iacute;ch động s&ocirc;i tr&agrave;o.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">So với thi&ecirc;n t&agrave;i, ch&uacute;ng ta muốn thấy kỳ t&iacute;ch hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bạn c&oacute; muốn trở th&agrave;nh một kỳ t&iacute;ch mới kh&ocirc;ng? Nếu muốn, bạn đừng cố gắng nữa, h&atilde;y nỗ lực hết m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>3. D&ugrave; c&oacute; đại gia &lsquo;bao&rsquo;, bạn nhất định vẫn phải độc lập về t&agrave;i ch&iacute;nh</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi c&oacute; người hỏi, tại sao c&ocirc; vẫn chưa y&ecirc;u, chưa kết h&ocirc;n, chưa sinh con. Li&ecirc;n quan g&igrave; đến anh chứ, t&ocirc;i đ&acirc;y c&oacute; tiền.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi c&oacute; người hỏi, c&ocirc; kh&ocirc;ng sợ tương lai kh&ocirc;ng lấy được chồng, kh&ocirc;ng c&oacute; con, về gi&agrave; kh&ocirc;ng c&oacute; chỗ dựa dẫm sao? Sợ g&igrave; chứ, t&ocirc;i đ&acirc;y c&oacute; tiền.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi c&oacute; người hỏi, chồng bạn lạc lối, bạn n&ecirc;n nhẫn nhịn, kh&ocirc;ng sợ ly h&ocirc;n xong sẽ gặp kh&oacute; khăn sao. Kh&ocirc;ng sao cả, t&ocirc;i đ&acirc;y c&oacute; tiền.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ch&uacute;ng ta độc lập về kinh tế, ch&uacute;ng ta mới c&oacute; quyền tự do: lựa chọn, thậm ch&iacute; cả tự do nh&acirc;n c&aacute;ch. Cho n&ecirc;n những người kh&ocirc;ng nỗ lực cố gắng, những người chỉ sống tạm bợ qua ng&agrave;y, d&ugrave; c&oacute; bị nhắc nhở, c&oacute; bị chỉ tr&iacute;ch, c&oacute; bị k&igrave; thị. Cũng l&agrave; đ&aacute;ng đời.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>4. M&acirc;y tầng n&agrave;o sẽ bầu bạn với m&acirc;y tầng đ&oacute;</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đừng cố &yacute; hiểu sai c&acirc;u tr&ecirc;n,n&oacute; kh&ocirc;ng chỉ c&oacute; &yacute; muốn n&oacute;i người gi&agrave;u th&igrave; gặp người gi&agrave;u, người h&egrave;n sẽ chơi với kẻ yếu, người thất học sẽ chẳng thể l&agrave;m bạn với một vị gi&aacute;o sư. Suy nghĩ đ&oacute; ho&agrave;n to&agrave;n sai! Sai b&eacute;t!</span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&acirc;u tr&ecirc;n &aacute;m chỉ một điều rằng: cốt c&aacute;ch, nh&acirc;n phẩm của bạn như thế n&agrave;o, th&igrave; những người xung quanh b&ecirc;n bạn sẽ l&agrave; những người như thế. Bởi vậy người ta n&oacute;i rằng: &ldquo;Thế giới n&agrave;y l&agrave;m g&igrave; c&oacute; đưa than ng&agrave;y tuyết rơi, chỉ c&oacute; dệt hoa l&ecirc;n gấm th&ocirc;i&rdquo;. Bạn muốn được người kh&aacute;c dệt hoa l&ecirc;n gấm, trước ti&ecirc;n bạn phải trở th&agrave;nh gấm đ&atilde;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sự thật lu&ocirc;n t&agrave;n khốc như vậy. X&atilde; hội cũng lu&ocirc;n thực tế như thế. Nhưng c&oacute; một ng&agrave;y t&ocirc;i cũng hiểu ra, thực dụng th&igrave; c&oacute; g&igrave; kh&ocirc;ng tốt?</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>5. Thay đổi c&aacute;ch nh&igrave;n - thay đổi cuộc sống</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tr&ecirc;n thế giới n&agrave;y, tất cả mọi việc, tất cả những người l&agrave;m cho bạn vui vẻ hay kh&oacute; chịu đều do gi&aacute; trị quan của bạn quyết định.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">H&atilde;y thay đổi tư duy, cuộc sống sẽ thoải m&aacute;i hơn rất nhiều</span></p>\r\n', 'Quang Nguyễn', '2022-08-05 18:54:36', 'active', '9uz8QsUKY.jpg', 8),
(20, 'TRIỂN LÃM SÁCH VIỆT NAM ĐẦU TIÊN TẠI HÀN QUỐC', '<p><span style=\"font-size:14px\">Hội s&aacute;ch Việt Nam v&agrave; Lễ Khai Trương gian h&agrave;ng S&aacute;ch Việt Nam tại Nh&agrave; s&aacute;ch Kyobo Gwanghwamun &ndash; Seoul &ndash; H&agrave;n Quốc!</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nhằm g&oacute;p phần giới thiệu văn h&oacute;a Việt Nam, giới thiệu h&igrave;nh ảnh, con người đất nước Việt Nam v&agrave; đ&aacute;p ứng nhu cầu học tập, giải tr&iacute; của đ&ocirc;ng đảo bạn đọc H&agrave;n Quốc, đặc biệt l&agrave; nhu cầu đọc s&aacute;ch, học tập của đ&ocirc;ng đảo người Việt, sinh vi&ecirc;n đang sinh sống v&agrave; l&agrave;m việc tại Seoul &ndash; H&agrave;n Quốc, C&ocirc;ng ty FAHASA phối hợp c&ugrave;ng tập đo&agrave;n Kyobo -H&agrave;n Quốc tổ chức: Hội s&aacute;ch Việt Nam v&agrave; Khai Trương gian h&agrave;ng S&aacute;ch Việt Nam tại Nh&agrave; s&aacute;ch Kyobo Gwanghwamun &ndash; Seoul &ndash; H&agrave;n Quốc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Gian h&agrave;ng s&aacute;ch Việt Nam c&oacute; diện t&iacute;ch hơn 50m, tọa lạc tại nh&agrave; s&aacute;ch lớn nhất của Kyobo - nh&agrave; s&aacute;ch Kyobo Gwanghwamun &ndash; Seoul &ndash; H&agrave;n Quốc. C&ocirc;ng ty FAHASA xuất khẩu hơn 2.000 tựa s&aacute;ch Việt Nam với tổng số lượng 5.000 bản s&aacute;ch để trưng b&agrave;y v&agrave; b&aacute;n trong đợt n&agrave;y với c&aacute;c loại: s&aacute;ch học tiếng Việt Nam d&agrave;nh cho người H&agrave;n, Từ điển Tiếng Việt, S&aacute;ch về Văn h&oacute;a, Lịch sử, Danh Nh&acirc;n Việt Nam, s&aacute;ch Thiếu nhi, s&aacute;ch Văn học, s&aacute;ch Nấu ăn, s&aacute;ch Du lịch, v&agrave; nhiều chủ đề phong ph&uacute; kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Để đ&aacute;nh dấu sự kiện quan trọng n&agrave;y, C&ocirc;ng ty FAHASA v&agrave; tập đo&agrave;n Kyobo sẽ tổ chức lễ khai mạc Hội s&aacute;ch Việt Nam v&agrave; khai trương gian h&agrave;ng s&aacute;ch Tiếng Việt tại Seoul &ndash; H&agrave;n Quốc:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">Thời gian khai mạc sự kiện: 11h00 am ng&agrave;y Thứ 5/ 21-11-2019.</span></li>\r\n	<li><span style=\"font-size:14px\">Địa điểm:&nbsp;</span>\r\n	<ul>\r\n		<li><span style=\"font-size:14px\">Nh&agrave; s&aacute;ch Kyobo Gwanghwamun &ndash; Seoul &ndash; H&agrave;n Quốc.</span></li>\r\n		<li><span style=\"font-size:14px\">Số 1, Jongno 1-ga, Jongno-gu, Seoul, H&agrave;n Quốc</span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\">Hội s&aacute;ch Việt Nam sẽ diễn ra từ 01-11-2019 đến 30-11-2019. Sau thời gian diển ra hội s&aacute;ch, gian h&agrave;ng s&aacute;ch Việt Nam sẽ được C&ocirc;ng ty FAHASA v&agrave; tập đo&agrave;n Kyobo duy tr&igrave; hoạt động v&agrave; sẽ bổ sung thường xuy&ecirc;n s&aacute;ch mới nhằm phục vụ một c&aacute;ch tốt nhất nhu cầu của độc giả tại Seoul &ndash; H&agrave;n Quốc.</span></p>\r\n', 'Quang Nguyễn', '2022-08-05 19:01:31', 'active', 'thumbnail-100Cwy8rqQu.jpg', 5);
INSERT INTO `blog` (`id`, `title`, `content`, `posted_by`, `posted_date`, `status`, `picture`, `ordering`) VALUES
(21, 'SÁCH HỌC TIẾNG NHẬT CƠ BẢN CHO NGƯỜI MỚI BẮT ĐẦU', '<p><span style=\"font-size:16px\"><strong>Tự học tiếng Nhật 200 chữ Kanji căn bản<strong style=\"font-size:14px\">&nbsp;- Eriko Sato</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nếu bạn l&agrave; người đ&atilde; t&igrave;m hiểu qua về tiếng Nhật th&igrave; chắc hẳn sẽ biết đến kiểu chữ Kanji của Nhật Bản. Kanji l&agrave; kiểu chữ m&agrave; hầu hết những người bắt đầu học tiếng Nhật gặp kh&ocirc;ng &iacute;t kh&oacute; khăn trong việc viết v&agrave; ghi nhớ ch&uacute;ng. V&igrave; thế tựa s&aacute;ch Tự Học Viết Tiếng Nhật 200 chữ Kanji căn bản được ra đời, để gi&uacute;p những người mới bắt đầu c&oacute; thể dễ d&agrave;ng hơn trong việc học v&agrave; ghi nhớ mặt chữ Kanji căn bản - nền tảng để học tiếng Nhật.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đ&acirc;y l&agrave; quyển s&aacute;ch viết cho những người bắt đầu học tiếng Nhật bắt đầu, từ c&aacute;ch học những chữ kanji th&ocirc;ng dụng nhất trong cuộc sống đ&atilde; được chọn lọc kĩ c&agrave;ng nhằm đ&aacute;p ứng nhu cầu học nhanh v&agrave; hiệu quả của đọc giả. Tại quyển s&aacute;ch n&agrave;y mỗi chữ Kanji đều c&oacute; phi&ecirc;n &acirc;m tương ứng bằng chữ latinh. B&ecirc;n cạnh đ&oacute; những n&eacute;t ch&iacute;nh trong từng chữ được in đậm những n&eacute;t phụ được in nhạt hơn để đọc giả dễ h&igrave;nh dung v&agrave; dễ nhớ.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Ngữ ph&aacute;p tiếng Nhật căn bản</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Từ vựng lu&ocirc;n l&agrave; điều quan trọng để ta học một ng&ocirc;n ngữ n&oacute; quyết định yếu tốt th&agrave;nh bại trong qu&aacute; tr&igrave;nh học của ta. B&ecirc;n cạnh đ&oacute; ngữ ph&aacute;p cũng l&agrave; phần kh&ocirc;ng quan trọng kh&ocirc;ng k&eacute;m từ vựng. Nếu ta biết rất nhiều từ vựng nhưng sử dụng ngữ ph&aacute;p kh&ocirc;ng đ&uacute;ng sẽ khiến c&acirc;u từ bị hiểu sai. Việc hiểu sai l&agrave; rất quan trọng th&agrave; kh&ocirc;ng hiểu chứ đừng hiểu sai. Vậy n&ecirc;n nếu bạn l&agrave; người mới bắt đầu trong qu&aacute; tr&igrave;nh học tiếng Nhật của m&igrave;nh th&igrave; đ&acirc;y sẽ l&agrave; quyển s&aacute;ch bạn đừng n&ecirc;n bỏ lỡ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Quyển s&aacute;ch được viết với 27 b&agrave;i học về những cấu tr&uacute;c ngữ ph&aacute;p căn bản trong cuộc sống như: c&acirc;u nghi vấn, c&acirc;u đề nghị, c&acirc;u so s&aacute;nh,... với c&aacute;ch tr&igrave;nh b&agrave;y mạch lạc dễ hiểu, k&egrave;m theo đ&oacute; l&agrave; những v&iacute; dụ thiết thực, sinh động, gần gũi với đời sống khiến ta c&oacute; thể h&igrave;nh dung một c&aacute;ch tốt nhất</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:16px\">Tự học 1800 c&acirc;u đ&agrave;m thoại tiếng Nhật</span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\">Học những c&acirc;u đ&agrave;m thoại th&ocirc;ng dụng l&agrave; một trong c&aacute;ch để ta c&oacute; thể ph&aacute;t triển kĩ năng sử dụng ng&ocirc;n ngữ đ&oacute; một c&aacute;ch tiến bộ nhất. Thay v&igrave; những cuốn s&aacute;ch dạy thuần từ vựng v&agrave; ngữ ph&aacute;p, th&igrave; c&oacute; lẽ Tự Học 1800 C&acirc;u Đ&agrave;m Thoại Trong Tiếng Nhật Th&ocirc;ng Dụng sẽ l&agrave; cuốn s&aacute;ch mang đến cho bạn nhiều phấn khởi v&agrave; hứng th&uacute; hơn. Ch&uacute;ng &iacute;t nhiều c&oacute; thể gi&uacute;p bạn c&oacute; thể &quot;mở miệng&quot; trong một v&agrave;i cuộc giao tiếp với người kh&aacute;c bằng Tiếng Nhật. B&ecirc;n cạnh đ&oacute; nếu bạn l&agrave; một người chuẩn bị nhập cư tại Nhật m&agrave; vốn tiếng Nhật của bản th&acirc;n lại &iacute;t, th&igrave; đ&acirc;y sẽ l&agrave; cuốn s&aacute;ch học tiếng Nhật nhất định bản phải đọc qua bởi ch&uacute;ng như m&oacute;n bảo bối gi&uacute;p bạn trong cuộc sống h&agrave;ng ng&agrave;y.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Tiếng Nhật cho mọi người - tr&igrave;nh độ sơ cấp 1 &amp; 2</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đ&uacute;ng như tựa s&aacute;ch tiếng Nhật cho mọi người- tr&igrave;nh độ sơ cấp 1 &amp; 2 l&agrave; m&ocirc;t quyển s&aacute;ch về gi&aacute;o tr&igrave;nh học tiếng Nhật ở mức độ sơ cấp với những từ vựng v&agrave; ngữ ph&aacute;p sơ cấp b&ecirc;n cạnh đ&oacute; l&agrave; những b&agrave;i tập k&egrave;m theo trong từng phần. Học bất cứ điều g&igrave; việc đi từ sơ cấp l&ecirc;n lu&ocirc;n l&agrave; nền tảng vững chắc nhất, dễ d&agrave;ng nhất. Đừng hấp tấp với những cuốn s&aacute;ch học tiếng Nhật ở mức độ kh&oacute; hơn khi ch&iacute;nh bạn c&ograve;n chưa vượt qua được những mức độ dễ nhất. Điều đ&oacute; kh&ocirc;ng những khiến bạn tốn tiền v&agrave;o những quyển s&aacute;ch m&agrave; ch&uacute;ng c&ograve;n khiến bạn mất rất nhiều thời gian cho ch&uacute;ng nhưng kết quả lại kh&ocirc;ng đ&aacute;ng mong đợi</span></p>\r\n', 'Quang Nguyễn', '2022-08-05 19:19:14', 'active', 'sach-day-giao-tiep-tieng-nhat3l0tARVC.jpg', 7),
(22, 'NHỮNG TRANG WEB MUA SÁCH ONLINE UY TÍN', '<p dir=\"ltr\"><span style=\"font-size:14px\">Chắc hẳn bạn l&agrave; một trong những t&iacute;n đồ th&iacute;ch mua sắm m&agrave; lại th&iacute;ch đọc s&aacute;ch , kh&aacute;m ph&aacute; những đều mới lạ từ những cuốn s&aacute;ch kh&aacute;c nhau. Với thời buổi hiện đại như ng&agrave;y nay việc lựa chọn s&aacute;ch mang về nh&agrave; kh&ocirc;ng c&ograve;n l&agrave; đều kh&oacute; khăn nữa đối với c&aacute;c bạn trẻ. Chỉ cần nh&igrave;n xem cuốn s&aacute;ch với những ti&ecirc;u đề mang &yacute; nghĩa ri&ecirc;ng k&egrave;m theo những phần m&ocirc; tả th&igrave; bạn đ&atilde; c&oacute; thể bấm chọn đặt h&agrave;ng v&agrave; đợi ng&agrave;y giao th&ocirc;i kh&ocirc;ng phải mất thời gian trước đ&acirc;y ! Lợi thế l&agrave; việc sử dụng điện thoại di động rộng r&atilde;i v&agrave; thời đại 4.0 c&ocirc;ng nghệ cao việc đặt h&agrave;ng online đ&atilde; v&agrave; đang rất ph&aacute;t triển V&igrave; thế m&agrave; c&aacute;c trang mạng điện tử, c&aacute;c trang b&aacute;n s&aacute;ch online&nbsp;ra đời.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Hiện nay c&oacute; rất nhiều c&aacute;c trang web b&aacute;n s&aacute;ch được b&aacute;n rầm rộ tr&ecirc;n mạng x&atilde; hội hay qua c&aacute;c phương tiện truyền th&ocirc;ng. Để lựa được một quyển s&aacute;ch uy t&iacute;n v&agrave; chất lượng gi&aacute; tiền m&igrave;nh bỏ ra th&igrave; sau đ&acirc;y l&agrave; TOP c&aacute;c web b&aacute;n s&aacute;ch&nbsp;được lựa chọn nhất:</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\"><strong>1. Shopee</strong></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Shopee.vn l&agrave; nền tảng thương mại điện tử h&agrave;ng đầu ở Đ&ocirc;ng Nam &Aacute; v&agrave; Đ&agrave;i Loan. Đ&acirc;y l&agrave; một nền tảng ph&ugrave; hợp cho khu vực, cung cấp cho kh&aacute;ch h&agrave;ng những trải nghiệm mua sắm trực tuyến dễ d&agrave;ng, an to&agrave;n v&agrave; nhanh ch&oacute;ng th&ocirc;ng qua sự hỗ trợ mạnh mẽ trong việc thanh to&aacute;n v&agrave; vận chuyển từ Shopee.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Shopee lu&ocirc;n hướng tới việc kh&ocirc;ng ngừng n&acirc;ng cao nền tảng v&agrave; được lựa chọn để trở th&agrave;nh điểm đến thương mại điện tử của khu vực.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\"><strong>2. Tiki</strong></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Tiki.vn Th&agrave;nh lập từ th&aacute;ng 3/2010, từ một website b&aacute;n s&aacute;ch trực tuyến, đến nay Tiki.vn l&agrave; một trong những website thương mại điện tử h&agrave;ng đầu tại Việt Nam, cung cấp c&aacute;c sản phẩm thuộc 10 ng&agrave;nh như: S&aacute;ch, m&aacute;y t&iacute;nh, thiết bị số, điện gia dụng, l&agrave;m đẹp, thiết bị văn ph&ograve;ng, mẹ v&agrave; b&eacute;, thể thao.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Một trong những nh&agrave; s&aacute;ch online đầu ti&ecirc;n tại Việt Nam.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Cho đến thời điểm hiện tại c&oacute; thể n&oacute;i, Tiki l&agrave; một trang web b&aacute;n s&aacute;ch trực tuyến h&agrave;ng đầu tại Việt Nam. Tiki đang sở hữu tr&ecirc;n 43.000 đầu s&aacute;ch kh&aacute;c nhau: Bao gồm khoảng 2.500 đầu s&aacute;ch ngoại văn, 34.000 đầu s&aacute;ch Tiếng Việt, 1.800 Ebooks v&agrave; c&aacute;c sản phẩm văn ph&ograve;ng phẩm.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Theo thống k&ecirc; trong năm 2016, Tiki.vn đ&atilde; b&aacute;n gần 12 triệu cuốn s&aacute;ch tới độc giả, một con số ấn tượng m&agrave; rất nhiều website kinh doanh s&aacute;ch online ao ước.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\"><strong>3. Fahasa</strong></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Fahasa&nbsp;l&agrave; hệ thống nh&agrave; s&aacute;ch rất nổi tiếng v&agrave; phổ biến, đặc biệt l&agrave; trong c&aacute;c tỉnh miền Nam sau đ&oacute; đ&atilde; ph&aacute;t triển hệ thống ra ngo&agrave;i Bắc, hiện nay với hệ thống cửa h&agrave;ng trải d&agrave;i c&aacute;c th&agrave;nh phố lớn, thương hiệu Fahasa c&agrave;ng thu h&uacute;t được đ&ocirc;ng đảo sự quan t&acirc;m từ người ti&ecirc;u d&ugrave;ng hơn.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Fahasa chuy&ecirc;n kinh doanh: s&aacute;ch quốc văn, ngoại văn, văn h&oacute;a phẩm, văn ph&ograve;ng phẩm, dụng cụ học tập, qu&agrave; lưu niệm, đồ chơi d&agrave;nh cho trẻ em&hellip; Một số Nh&agrave; s&aacute;ch trực thuộc C&ocirc;ng ty Fahasa c&ograve;n kinh doanh c&aacute;c mặt h&agrave;ng si&ecirc;u thị như: h&agrave;ng ti&ecirc;u d&ugrave;ng, h&agrave;ng gia dụng, h&oacute;a mỹ phẩm&hellip;</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">C&ugrave;ng với việc ph&aacute;t h&agrave;nh độc quyền nhiều ấn bản c&aacute;c loại của c&aacute;c Nh&agrave; xuất bản l&agrave; năng lực in ấn, sản xuất cung ứng nguồn h&agrave;ng của X&iacute; nghiệp in Fahasa, đ&atilde; gi&uacute;p C&ocirc;ng ty lu&ocirc;n chủ động được nguồn h&agrave;ng, nhất l&agrave; c&aacute;c mặt h&agrave;ng độc quyền như: lịch bloc, tập học sinh, sổ tay cao cấp, agenda, văn ph&ograve;ng phẩm, dụng cụ học tập&hellip;</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">S&aacute;ch ngoại văn bao gồm: từ điển, gi&aacute;o tr&igrave;nh, tham khảo, truyện tranh thiếu nhi , s&aacute;ch học ngữ, từ vựng, ngữ ph&aacute;p, luyện thi TOEFL, TOEIC, IELS&hellip;được nhập từ c&aacute;c NXB nước ngo&agrave;i như: Cambridge, Mc Graw-Hill, Pearson Education, Oxford, Macmillan, Cengage Learning&hellip;</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\"><strong>4. Vinabook</strong></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Bắt đầu tại Việt Nam từ năm 2004, Vinabook l&agrave; c&ocirc;ng ty thương mại điện tử cũng như l&agrave; trang web b&aacute;n s&aacute;ch online uy t&iacute;n , l&agrave; Nh&agrave; s&aacute;ch tr&ecirc;n mạng h&agrave;ng đầu v&agrave; được h&agrave;ng triệu kh&aacute;ch h&agrave;ng tin tưởng gh&eacute; thăm thường xuy&ecirc;n. Nhờ v&agrave;o đội ngũ nh&acirc;n vi&ecirc;n trẻ tuổi, nhiệt huyết, với sứ mệnh l&agrave; &lsquo;x&oacute;a bỏ kh&ocirc;ng gian trong việc truyền tải kiến thức&rsquo;, trang web b&aacute;n s&aacute;ch n&agrave;y đ&atilde; th&uacute;c đẩy văn h&oacute;a đọc của người Việt.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Hiện tại Vinabook đang l&agrave; nh&agrave; s&aacute;ch c&oacute; số lượng s&aacute;ch &lsquo;khủng&rsquo; nhất với hơn 60.000 đầu s&aacute;ch, với dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng 7 ng&agrave;y trong tuần kể cả thứ bảy v&agrave; chủ nhật, c&aacute;c dịch vụ cũng v&ocirc; c&ugrave;ng đa dạng như giao h&agrave;ng tiết kiệm, chuyển s&aacute;ch nước ngo&agrave;i, bọc s&aacute;ch, g&oacute;i qu&agrave; l&agrave;m cho người d&ugrave;ng v&ocirc; c&ugrave;ng h&agrave;i l&ograve;ng. Kh&ocirc;ng chỉ c&oacute; thế, Vinabook thường xuy&ecirc;n c&oacute; những chương tr&igrave;nh khuyến mại hấp dẫn v&agrave; những combo ưu đ&atilde;i giảm gi&aacute; tới 50% hay tặng qu&agrave; khi mua s&aacute;ch, miễn ph&iacute; tiền vận chuyển&hellip;</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:14px\">Sau l&agrave; những web b&aacute;n s&aacute;ch online được nhiều người tin d&ugrave;ng nhất , c&ugrave;ng với những danh mục s&aacute;ch đa dạng ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c danh mục sản phẩm kh&aacute;c đi k&egrave;m như văn ph&ograve;ng phẩm, dụng cụ học tập, mua sắm thời trang...Để cho bạn thỏa sức lựa chọn đ&uacute;ng với gi&aacute; tiền m&agrave; m&igrave;nh bỏ ra.</span></p>\r\n', 'Quang Nguyễn', '2022-08-05 19:42:08', 'active', 'sach-tieng-anh-hay_2IJ9kOvxs.jpg', 8);

