INSERT INTO `book` (`id`, `name`, `description`, `price`, `special`, `sale_off`, `picture`, `created`, `created_by`, `modified`, `modified_by`, `status`, `ordering`, `category_id`) VALUES
(17, 'Nhà giả kim', '<p>Tất cả những trải nghiệm trong chuyến phi&ecirc;u du theo đuổi vận mệnh của m&igrave;nh đ&atilde; gi&uacute;p Santiago thấu hiểu được &yacute; nghĩa s&acirc;u xa nhất của hạnh ph&uacute;c, h&ograve;a hợp với vũ trụ v&agrave; con người.</p>\r\n\r\n<p>Tiểu thuyết Nh&agrave; giả kim của Paulo Coelho như một c&acirc;u chuyện cổ t&iacute;ch giản dị, nh&acirc;n &aacute;i, gi&agrave;u chất thơ, thấm đẫm những minh triết huyền b&iacute; của phương Đ&ocirc;ng.</p>\r\n\r\n<p>Trong lần xuất bản đầu ti&ecirc;n tại Brazil v&agrave;o năm 1988, s&aacute;ch chỉ b&aacute;n được 900 bản. Nhưng, với số phận đặc biệt của cuốn s&aacute;ch d&agrave;nh cho to&agrave;n nh&acirc;n loại, vượt ra ngo&agrave;i bi&ecirc;n giới quốc gia, Nh&agrave; giả kim đ&atilde; l&agrave;m rung động h&agrave;ng triệu t&acirc;m hồn, trở th&agrave;nh một trong những cuốn s&aacute;ch b&aacute;n chạy nhất mọi thời đại, v&agrave; c&oacute; thể l&agrave;m thay đổi cuộc đời người đọc. &ldquo;Nhưng nh&agrave; luyện kim đan kh&ocirc;ng quan t&acirc;m mấy đến những điều ấy. &Ocirc;ng đ&atilde; từng thấy nhiều người đến rồi đi, trong khi ốc đảo v&agrave; sa mạc vẫn l&agrave; ốc đảo v&agrave; sa mạc.</p>\r\n\r\n<p>&Ocirc;ng đ&atilde; thấy vua ch&uacute;a v&agrave; kẻ ăn xin đi qua biển c&aacute;t n&agrave;y, c&aacute;i biển c&aacute;t thường xuy&ecirc;n thay h&igrave;nh đổi dạng v&igrave; gi&oacute; thổi nhưng vẫn m&atilde;i m&atilde;i l&agrave; biển c&aacute;t m&agrave; &ocirc;ng đ&atilde; biết từ thuở nhỏ. Tuy vậy, tự đ&aacute;y l&ograve;ng m&igrave;nh, &ocirc;ng kh&ocirc;ng thể kh&ocirc;ng cảm thấy vui trước hạnh ph&uacute;c của mỗi người lữ kh&aacute;ch, sau bao ng&agrave;y chỉ c&oacute; c&aacute;t v&agrave;ng với trời xanh nay được thấy ch&agrave; l&agrave; xanh tươi hiện ra trước mắt. &lsquo;C&oacute; thể Thượng đế tạo ra sa mạc chỉ để cho con người biết qu&yacute; trọng c&acirc;y ch&agrave; l&agrave;,&rsquo; &ocirc;ng nghĩ.&rdquo; - Tr&iacute;ch Nh&agrave; giả kim Nhận định &ldquo;Sau Garcia M&aacute;rquez, đ&acirc;y l&agrave; nh&agrave; văn Mỹ Latinh được đọc nhiều nhất thế giới.&rdquo; - The Economist, London, Anh &ldquo;Santiago c&oacute; khả năng cảm nhận bằng tr&aacute;i tim như Ho&agrave;ng tử b&eacute; của Saint-Exup&eacute;ry.&rdquo; - Frankfurter Allgemeine Zeitung, Đức M&atilde; h&agrave;ng 8935235226272 T&ecirc;n Nh&agrave; Cung Cấp Nh&atilde; Nam T&aacute;c giả Paulo Coelho Người Dịch L&ecirc; Chu Cầu NXB NXB Hội Nh&agrave; Văn Năm XB 2020 Trọng lượng (gr) 220 K&iacute;ch Thước Bao B&igrave; 20.5 x 13 cm Số trang 227 H&igrave;nh thức B&igrave;a Mềm Sản phẩm hiển thị trong Đồ Chơi Cho B&eacute; - Gi&aacute; Cực Tốt Nh&atilde; Nam PNJ Top s&aacute;ch được phi&ecirc;n dịch nhiều nhất VNPAY Sản phẩm b&aacute;n chạy nhất Top 100 sản phẩm Tiểu thuyết b&aacute;n chạy của th&aacute;ng Tất cả những trải nghiệm trong chuyến phi&ecirc;u du theo đuổi vận mệnh của m&igrave;nh đ&atilde; gi&uacute;p Santiago thấu hiểu được &yacute; nghĩa s&acirc;u xa nhất của hạnh ph&uacute;c, h&ograve;a hợp với vũ trụ v&agrave; con người. Tiểu thuyết Nh&agrave; giả kim của Paulo Coelho như một c&acirc;u chuyện cổ t&iacute;ch giản dị, nh&acirc;n &aacute;i, gi&agrave;u chất thơ, thấm đẫm những minh triết huyền b&iacute; của phương Đ&ocirc;ng. Trong lần xuất bản đầu ti&ecirc;n tại Brazil v&agrave;o năm 1988, s&aacute;ch chỉ b&aacute;n được 900 bản.</p>\r\n\r\n<p>Nhưng, với số phận đặc biệt của cuốn s&aacute;ch d&agrave;nh cho to&agrave;n nh&acirc;n loại, vượt ra ngo&agrave;i bi&ecirc;n giới quốc gia, Nh&agrave; giả kim đ&atilde; l&agrave;m rung động h&agrave;ng triệu t&acirc;m hồn, trở th&agrave;nh một trong những cuốn s&aacute;ch b&aacute;n chạy nhất mọi thời đại, v&agrave; c&oacute; thể l&agrave;m thay đổi cuộc đời người đọc. &ldquo;Nhưng nh&agrave; luyện kim đan kh&ocirc;ng quan t&acirc;m mấy đến những điều ấy. &Ocirc;ng đ&atilde; từng thấy nhiều người đến rồi đi, trong khi ốc đảo v&agrave; sa mạc vẫn l&agrave; ốc đảo v&agrave; sa mạc. &Ocirc;ng đ&atilde; thấy vua ch&uacute;a v&agrave; kẻ ăn xin đi qua biển c&aacute;t n&agrave;y, c&aacute;i biển c&aacute;t thường xuy&ecirc;n thay h&igrave;nh đổi dạng v&igrave; gi&oacute; thổi nhưng vẫn m&atilde;i m&atilde;i l&agrave; biển c&aacute;t m&agrave; &ocirc;ng đ&atilde; biết từ thuở nhỏ. Tuy vậy, tự đ&aacute;y l&ograve;ng m&igrave;nh, &ocirc;ng kh&ocirc;ng thể kh&ocirc;ng cảm thấy vui trước hạnh ph&uacute;c của mỗi người lữ kh&aacute;ch, sau bao ng&agrave;y chỉ c&oacute; c&aacute;t v&agrave;ng với trời xanh nay được thấy ch&agrave; l&agrave; xanh tươi hiện ra trước mắt. &lsquo;C&oacute; thể Thượng đế tạo ra sa mạc chỉ để cho con người biết qu&yacute; trọng c&acirc;y ch&agrave; l&agrave;,&rsquo; &ocirc;ng nghĩ.&rdquo;</p>\r\n\r\n<p>- Tr&iacute;ch Nh&agrave; giả kim</p>\r\n', 55000, 'yes', 30, 'image_195509_1_36793FToQdJyN.jpg', '2022-06-30 16:14:11', 'Quang Nguyễn', '2022-07-21 17:24:35', 'Quang Nguyễn', 'active', 7, 33),
(19, 'Chiến binh cầu vồng', '<p>&ldquo;Thầy Harfan v&agrave; c&ocirc; Mus ngh&egrave;o khổ đ&atilde; mang đến cho t&ocirc;i tuổi thơ đẹp nhất, t&igrave;nh bạn đẹp nhất, v&agrave; t&acirc;m hồn phong ph&uacute;, một thứ g&igrave; đ&oacute; v&ocirc; gi&aacute;, thậm ch&iacute; c&ograve;n c&oacute; gi&aacute; trị hơn những khao kh&aacute;t mơ ước. C&oacute; thể t&ocirc;i lầm, nhưng theo &yacute; t&ocirc;i, đ&acirc;y thật sự l&agrave; hơi thở của gi&aacute;o dục v&agrave; linh hồn của một chốn được gọi l&agrave; trường học.&rdquo;</p>\r\n\r\n<p>- (Tr&iacute;ch t&aacute;c phẩm)</p>\r\n\r\n<p>Trong ng&agrave;y khai giảng, nhờ sự xuất hiện v&agrave;o ph&uacute;t ch&oacute;t của cậu b&eacute; thiểu năng tr&iacute; tuệ Harun, trường Muhammadiyah may mắn tho&aacute;t khỏi nguy cơ đ&oacute;ng cửa. Nhưng ước mơ dạy v&agrave; học trong ng&ocirc;i trường Hồi gi&aacute;o ấy liệu sẽ đi về đ&acirc;u, khi ng&ocirc;i trường xập xệ dường như sẵn s&agrave;ng sụp xuống bất cứ l&uacute;c n&agrave;o, khi lời đe dọa đ&oacute;ng cửa từ vi&ecirc;n thanh tra gi&aacute;o dục lu&ocirc;n lơ lửng tr&ecirc;n đầu, khi những cỗ m&aacute;y x&uacute;c hung dữ đang chực chờ xới tung ng&ocirc;i trường để d&ograve; mạch thiếc&hellip;?</p>\r\n\r\n<p>V&agrave; liệu niềm đam m&ecirc; học tập của những Chiến binh Cầu vồng đ&oacute; c&oacute; đủ sức chinh phục qu&atilde;ng đường ng&agrave;y ng&agrave;y đạp xe bốn mươi c&acirc;y số, rồi đầm c&aacute; sấu l&uacute;c nh&uacute;c bọn ăn thịt người, chưa kể sự m&ecirc; hoặc từ những chuyến phi&ecirc;u lưu chết người theo tiếng gọi của ng&agrave;i ph&aacute;p sư b&iacute; ẩn tr&ecirc;n đảo Hải Tặc, c&ugrave;ng sức c&aacute;m dỗ kh&ocirc;n cưỡng từ những đồng tiền c&ograve;m kiếm được nhờ c&ocirc;ng việc cu li to&agrave;n thời gian ...?</p>\r\n\r\n<p>Chiến binh Cầu vồng c&oacute; cả t&igrave;nh y&ecirc;u trong s&aacute;ng tuổi học tr&ograve; lẫn những tr&ograve; đ&ugrave;a tinh qu&aacute;i, cả nước mắt lẫn tiếng cười &ndash; một bức tranh ch&acirc;n thực về hố s&acirc;u ngăn c&aacute;ch gi&agrave;u ngh&egrave;o, một t&aacute;c phẩm văn học cảm động truyền tải s&acirc;u sắc nhất &yacute; nghĩa đ&iacute;ch thực của việc l&agrave;m thầy, việc l&agrave;m tr&ograve; v&agrave; việc học.</p>\r\n\r\n<p>T&aacute;c phẩm đ&atilde; b&aacute;n được tr&ecirc;n năm triệu bản, được dịch ra 26 thứ tiếng, l&agrave; một trong những đại diện xuất sắc nhất của văn học Indonesia hiện đại.</p>\r\n', 92000, 'yes', 30, 'image_195509_1_36366X8Gc0KCT.jpg', '2022-06-30 16:19:08', 'Quang Nguyễn', '2022-07-21 17:27:23', 'Quang Nguyễn', 'active', 50, 33),
(20, 'Hoàng tử bé', '<p>Ho&agrave;ng tử b&eacute; được viết ở New York trong những ng&agrave;y t&aacute;c giả sống lưu vong v&agrave; được xuất bản lần đầu ti&ecirc;n tại New York v&agrave;o năm 1943, rồi đến năm 1946 mới được xuất bản tại Ph&aacute;p. Kh&ocirc;ng nghi ngờ g&igrave;, đ&acirc;y l&agrave; t&aacute;c phẩm nổi tiếng nhất, được đọc nhiều nhất v&agrave; cũng được y&ecirc;u mến nhất của Saint-Exup&eacute;ry.</p>\r\n\r\n<p>Cuốn s&aacute;ch được b&igrave;nh chọn l&agrave; t&aacute;c phẩm hay nhất thế kỉ 20 ở Ph&aacute;p, đồng thời cũng l&agrave; cuốn s&aacute;ch Ph&aacute;p được dịch v&agrave; được đọc nhiều nhất tr&ecirc;n thế giới. Với 250 ng&ocirc;n ngữ dịch kh&aacute;c nhau kể cả phương ngữ c&ugrave;ng hơn 200 triệu bản in tr&ecirc;n to&agrave;n thế giới, Ho&agrave;ng tử b&eacute; được coi l&agrave; một trong những t&aacute;c phẩm b&aacute;n chạy nhất của nh&acirc;n loại.</p>\r\n', 52000, 'yes', 31, 'image_1870105rj61ysc.jpg', '2022-06-30 16:20:38', 'Quang Nguyễn', '2022-07-21 17:25:09', 'Quang Nguyễn', 'active', 21, 35),
(21, 'Những Câu Chuyện Truyền Cảm Hứng: Con Sẽ Tự Giác', '<p>&ldquo;Con sẽ tự gi&aacute;c&rdquo; kể những c&acirc;u chuyện về tinh thần học tập miệt m&agrave;i. Mỗi nh&acirc;n vật trong cuốn s&aacute;ch n&agrave;y đều kể những c&acirc;u chuyện rất ri&ecirc;ng về qu&aacute; tr&igrave;nh học tập của m&igrave;nh. Tr&ecirc;n con đường trưởng th&agrave;nh, nếu biết tự nhận thức bản th&acirc;n, hiểu r&otilde; mục ti&ecirc;u ph&aacute;t triển của m&igrave;nh, bồi đắp l&ograve;ng tự tin, d&aacute;m đối mặt với thử th&aacute;ch v&agrave; ki&ecirc;n tr&igrave; ph&aacute;t huy năng lực bản th&acirc;n, ch&uacute;ng ta đều c&oacute; thể thực hiện được ước mơ v&agrave; trở n&ecirc;n tốt đẹp hơn, th&agrave;nh c&ocirc;ng hơn.</p>\r\n\r\n<p>Bộ s&aacute;ch &ldquo;Những c&acirc;u chuyện truyền cảm hứng&rdquo; được chia th&agrave;nh c&aacute;c chủ đề thiết thực, gi&uacute;p bạn đọc nhỏ tuổi t&igrave;m hiểu v&agrave; r&egrave;n những th&oacute;i quen, đức t&iacute;nh tốt. Mỗi cuốn gồm nhiều c&acirc;u chuyện nhỏ, trong đ&oacute; c&oacute; cả những hồi ức đ&aacute;ng nhớ của c&aacute;c nh&acirc;n vật nổi tiếng tr&ecirc;n thế giới... Mong rằng bộ s&aacute;ch sẽ trở th&agrave;nh bạn đồng h&agrave;nh t&iacute;ch cực, c&ugrave;ng c&aacute;c em bước v&agrave;o tương lai rực rỡ</p>\r\n', 45000, 'yes', 10, 'image_195509_1_7128ZBAwIbYc.jpg', '2022-06-30 16:22:12', 'Quang Nguyễn', '2022-07-21 17:25:38', 'Quang Nguyễn', 'active', 36, 35),
(22, 'Giải Thích Ngữ Pháp Tiếng Anh (Tái Bản 2022)', '<p>S&aacute;ch Giải Th&iacute;ch Ngữ Ph&aacute;p Tiếng Anh, t&aacute;c Mai Lan Hương &ndash; H&agrave; Thanh Uy&ecirc;n, l&agrave; cuốn s&aacute;ch ngữ ph&aacute;p đ&atilde; được ph&aacute;t h&agrave;nh v&agrave; t&aacute;i bản rất nhiều lần trong những năm qua.</p>\r\n\r\n<p>Giải Th&iacute;ch Ngữ Ph&aacute;p Tiếng Anh được bi&ecirc;n soạn th&agrave;nh 9 chương, đề cập đến những vấn đề ngữ ph&aacute;p từ cơ bản đến n&acirc;ng cao, ph&ugrave; hợp với mọi tr&igrave;nh độ. C&aacute;c chủ điểm ngữ ph&aacute;p trong từng chương được bi&ecirc;n soạn chi tiết, giải th&iacute;ch cặn kẽ c&aacute;c c&aacute;ch d&ugrave;ng v&agrave; quy luật m&agrave; người học cần nắm vững.</p>\r\n\r\n<p>Sau mỗi chủ điểm ngữ ph&aacute;p l&agrave; phần b&agrave;i tập đa dạng nhằm gi&uacute;p người học củng cố l&yacute; thuyết. Hy vọng Giải Th&iacute;ch Ngữ Ph&aacute;p Tiếng Anh sẽ l&agrave; một quyển s&aacute;ch thiết thực, đ&aacute;p ứng y&ecirc;u cầu học, &ocirc;n tập v&agrave; n&acirc;ng cao tr&igrave;nh độ ngữ ph&aacute;p cho người học v&agrave; l&agrave; quyển s&aacute;ch tham khảo bổ &iacute;ch d&agrave;nh cho gi&aacute;o vi&ecirc;n.</p>\r\n', 154000, 'yes', 35, 'z3097453775918_7ea22457f168a4de92d0ba8178a2257bDW6h8K9J.jpg', '2022-06-30 16:23:18', 'Quang Nguyễn', '2022-07-21 17:24:29', 'Quang Nguyễn', 'active', 5, 36),
(23, 'Lập trình IoT với Arduino', '<p>Lập Tr&igrave;nh Iot Với Arduino ARDUINO v&agrave; lập tr&igrave;nh ToT c&oacute; 3 phần với 10 chương.</p>\r\n\r\n<p>Phần 1: Giới thiệu Chương 1: Tổng quan về hệ thống nh&uacute;ng, về IoT, về Arduino, Arduino v&agrave; Raspberry, Serial monitor, c&aacute;c link kiện thực h&agrave;nh.</p>\r\n\r\n<p>Phần 2: ARDUINO v&agrave; Cảm biến Chương 2: Tổng quan về cảm biến Chương 3: Thực h&agrave;nh với Qrduino 1) Đọc gi&aacute; trị điện &aacute;p ng&otilde; v&agrave;o Analog 2) Điều khiển tốc độ s&aacute;ng tắt với biến trở 3) Lập tr&igrave;nh với ng&otilde; v&agrave;o (Input) 4) Mạch sử dụng hai n&uacute;t nhấn v&agrave; một Led 5) Mạch sử dụng chiết &aacute;p 6) Điều khiển Led RGB 7) Điều khiển Led cầu v&ograve;ng 8) Điều khiển Led s&aacute;ng dạng thanh Chương 4: Arduino v&agrave; cảm biến Arduino v&agrave; cảm biến Nhiệt độ-Độ ẩm. Cảm biến tiệm cận. Arduino v&agrave; cảm biến hồng ngoại. Cảm biến quang. Cảm biến kh&oacute;i-đầu d&ograve; kh&oacute;i. Arduino v&agrave; cảm biến dịch chuyển PIR. Đo nhiệt độ,độ ẩm từ xa với bo thu ph&aacute;t cao tần . Phần 3: Lập tr&igrave;nh IOT với ARDUINO V&Agrave; ESP8266 Chương 5: Truyền th&ocirc;ng c&oacute; d&acirc;y v&agrave; kh&ocirc;ng d&acirc;y Chương 6: Cấu h&igrave;nh cho ESP8266 Chương 7: C&aacute;c b&agrave;i tập cơ bản kh&aacute;c của ESP8266 Chương 8: Một số t&iacute;nh năng kh&aacute;c của ESP8266 Chương 9: Sử dụng Micro Python trong ESP8266 Chương 10: Lập tr&igrave;nh với GPS, GSM v&agrave; GPRS Hệ thống định vị to&agrave;n cầu (GPS). Bảng th&ocirc;ng b&aacute;o kh&ocirc;ng d&acirc;y sử dụng GSM v&agrave; Arduino. Gủi dữ liệu qua GPRS Sim800I đến Thingspeak. Khắc phục sự cố khi sử dụng Sim800I. Lập tr&igrave;nh GPS với STM32</p>\r\n', 137500, 'yes', 20, 'image_191567TcsN4tvE.jpg', '2022-06-30 16:27:14', 'Quang Nguyễn', '2022-07-21 17:26:46', 'Quang Nguyễn', 'active', 41, 37),
(24, 'Lập trình với C#', '<p>Hướng dẫn người học từng bước lập tr&igrave;nh với C#. X&acirc;y dựng ứng dụng tr&ecirc;n Window Form. X&acirc;y dựng ứng dụng, tạo hai trang web quản l&yacute; b&aacute;n h&agrave;ng v&agrave; quản l&yacute; tuyển sinh với c&aacute;c hướng dẫn từng bước l&agrave;m cơ sở cho việc thiết kế c&aacute;c trang web phức tạp hơn. Nội dung ch&iacute;nh: - Phần 1: Lập Tr&igrave;nh Với C# - Ứng dụng Windows form- Phần 2: Lập Tr&igrave;nh Với C# - Quản l&yacute; b&aacute;n h&agrave;ng- Phần 3: Lập Tr&igrave;nh Với C# - Quản l&yacute; tuyển sinh</p>\r\n', 106000, 'yes', 45, 'image_191566onab1gl8.jpg', '2022-06-30 16:29:11', 'Quang Nguyễn', '2022-07-21 17:25:03', 'Quang Nguyễn', 'active', 34, 37),
(25, 'Hành Trang Lập Trình - Những Kỹ Năng Lập Trình Viên Chuyên Nghiệp Cần Có', '<p>Cuốn s&aacute;ch gồm 2 nội dung ch&iacute;nh:</p>\r\n\r\n<p>Chương I: Phần n&agrave;y tr&igrave;nh b&agrave;y những suy nghĩ v&agrave; kĩ năng mềm cần thiết để bạn c&oacute; thể h&ograve;a nhập v&agrave; tiến xa được trong lĩnh vực c&ocirc;ng nghệ phần mềm. Nội dung trong phần n&agrave;y lại được chia th&agrave;nh những mục nhỏ hơn tương ứng với từng giai đoạn học tập v&agrave; l&agrave;m việc: o Giai đoạn 1 &ndash; Học nghề: Tr&igrave;nh b&agrave;y những th&aacute;i độ v&agrave; kĩ năng học tập cần thiết để c&oacute; thể học tập hiệu quả ở trường, cũng như chuẩn bị h&agrave;nh trang cho qu&aacute; tr&igrave;nh l&agrave;m việc sau n&agrave;y. o Giai đoạn 2 &ndash; V&agrave;o nghề: Cung cấp một v&agrave;i th&ocirc;ng tin hữu &iacute;ch li&ecirc;n quan tới việc t&igrave;m kiếm v&agrave; ứng tuyển v&agrave;o những c&ocirc;ng ty c&ocirc;ng nghệ, phần n&agrave;y cũng đưa ra v&agrave;i g&oacute;c nh&igrave;n li&ecirc;n quan tới việc đ&aacute;nh gi&aacute; v&agrave; so s&aacute;nh nơi l&agrave;m việc để bạn t&igrave;m ra được c&ocirc;ng ty ph&ugrave; hợp. o Giai đoạn 3 &ndash; Ph&aacute;t triển trong nghề: Sau khi đ&atilde; t&igrave;m được nơi l&agrave;m việc, phần n&agrave;y sẽ tr&igrave;nh b&agrave;y những suy nghĩ v&agrave; c&aacute;ch tư duy l&agrave;m việc để gi&uacute;p bạn c&oacute; thể tiến bộ hơn trong sự nghiệp lập tr&igrave;nh vi&ecirc;n.</p>\r\n\r\n<p>Chương II: Phần n&agrave;y sẽ đi s&acirc;u v&agrave;o một v&agrave;i kiến thức kĩ thuật nền tảng m&igrave;nh nghĩ l&agrave; cần thiết cho qu&aacute; tr&igrave;nh ph&aacute;t triển sau n&agrave;y của một lập tr&igrave;nh vi&ecirc;n. o Phần 1 &ndash; Clean Code (M&atilde; sạch): Những d&ograve;ng code được viết ra kh&ocirc;ng phải chỉ d&agrave;nh cho m&aacute;y t&iacute;nh, m&agrave; c&ograve;n l&agrave; để cho con người (bảo tr&igrave;, ph&aacute;t triển&hellip;), đ&acirc;y l&agrave; điều cực k&igrave; quan trọng nhưng thường &iacute;t được dạy kĩ c&agrave;ng khi ở trường. Phần n&agrave;y sẽ hướng dẫn c&aacute;c bạn c&aacute;ch để viết m&atilde; sạch. o Phần 2 &ndash; Những nguy&ecirc;n l&iacute; lập tr&igrave;nh n&acirc;ng cao (SOLID): Viết code chạy được chỉ l&agrave; bước đầu ti&ecirc;n, để trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n giỏi ch&uacute;ng ta cần phải biết c&aacute;ch viết code dễ bảo tr&igrave;, dễ mở rộng v&agrave; linh hoạt hơn. Phần n&agrave;y sẽ c&ugrave;ng bạn b&agrave;n luận về những nguy&ecirc;n l&iacute; lập tr&igrave;nh n&acirc;ng cao m&agrave; mọi lập tr&igrave;nh vi&ecirc;n c&oacute; kinh nghiệm cần phải biết. o Phần 3 &ndash; Đơn giản h&oacute;a c&aacute;c kh&aacute;i niệm kĩ thuật phức tạp: Lập tr&igrave;nh kh&ocirc;ng dễ, những cũng kh&ocirc;ng thật sự kh&oacute;, phần n&agrave;y sẽ cố gắng giải th&iacute;ch những kh&aacute;i niệm v&agrave; kĩ thuật phức tạp nhằm gi&uacute;p bạn c&oacute; thể nhanh ch&oacute;ng n&acirc;ng cao kĩ năng của bản th&acirc;n. Lời khuy&ecirc;n của Bi&ecirc;n tập vi&ecirc;n d&agrave;nh cho việc đọc cuốn s&aacute;ch Đ&acirc;y l&agrave; một cuốn s&aacute;ch về kĩ thuật, nhưng bạn đừng qu&aacute; lo lắng nếu như chưa c&oacute; nhiều kiến thức chuy&ecirc;n m&ocirc;n trong ng&agrave;nh. Cuốn s&aacute;ch n&agrave;y được thiết kế v&agrave; tr&igrave;nh b&agrave;y đơn giản h&oacute;a theo những c&aacute;ch dễ hiểu nhất để c&aacute;c bạn sinh vi&ecirc;n hoặc c&aacute;c bạn vừa mới đi l&agrave;m cũng c&oacute; thể hiểu một c&aacute;ch dễ d&agrave;ng. C&aacute;c bạn cũng c&oacute; thể tham khảo qua bảng danh s&aacute;ch thuật ngữ ở cuối s&aacute;ch để c&oacute; thể hiểu những kh&aacute;i niệm được đề cập tới trong s&aacute;ch. T&aacute;c giả Vũ C&ocirc;ng Tấn T&agrave;i T&aacute;c giả Vũ C&ocirc;ng Tấn T&agrave;i hiện đang l&agrave;m việc như một lập tr&igrave;nh vi&ecirc;n full-stack to&agrave;n thời gian trong lĩnh vực ph&aacute;t triển ứng dụng Web v&agrave; tham gia v&agrave;o c&aacute;c dự &aacute;n triển khai hệ thống CI/CD phục vụ cho c&aacute;c y&ecirc;u cầu n&acirc;ng cao chất lượng sản phẩm. B&ecirc;n cạnh c&ocirc;ng việc ch&iacute;nh, t&aacute;c giả cũng thường tham gia hướng dẫn c&aacute;c lớp học lập tr&igrave;nh cũng như tổ chức c&aacute;c buổi chia sẻ kinh nghiệm l&agrave;m việc cho c&aacute;c bạn sinh vi&ecirc;n. Trong suốt qu&aacute; tr&igrave;nh l&agrave;m việc v&agrave; t&igrave;m kiếm th&ocirc;ng tin, t&aacute;c giả Vũ C&ocirc;ng Tấn T&agrave;i nhận ra rằng lập tr&igrave;nh vi&ecirc;n ở Việt Nam kh&aacute; c&ocirc; đơn v&agrave; thiệt th&ograve;i: kh&ocirc;ng c&oacute; nhiều nguồn th&ocirc;ng tin bằng tiếng Việt, nếu c&oacute; th&igrave; cũng nằm rải r&aacute;c ở nhiều nơi, g&acirc;y ra kh&ocirc;ng &iacute;t kh&oacute; khăn cho nhiều người. Với mong muốn chia sẻ thật nhiều kiến thức, trong cuốn s&aacute;ch n&agrave;y, t&aacute;c giả sẽ đem đến cho c&aacute;c bạn nhiều điều về nghề lập tr&igrave;nh cũng như c&ocirc;ng việc của những nh&agrave; ph&aacute;t triển phần mềm &ndash; hay ch&uacute;ng ta vẫn hay gọi l&agrave; nghề &ldquo;lập tr&igrave;nh vi&ecirc;n&rdquo;.</p>\r\n', 145000, 'yes', 50, 'image_195509_1_19543h4MRTLVg.jpg', '2022-06-30 16:31:27', 'Quang Nguyễn', '2022-07-21 17:27:46', 'Quang Nguyễn', 'active', 69, 37),
(26, 'Mặt trái của công nghệ', '<p>Kỹ thuật, c&ocirc;ng nghệ đ&oacute;ng vai tr&ograve; quan trọng, tạo n&ecirc;n những bước ph&aacute;t triển đột ph&aacute; của x&atilde; hội lo&agrave;i người, từ h&agrave;ng ngh&igrave;n năm trước đ&acirc;y cũng như trong bối cảnh C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư hiện nay. Những ph&aacute;t minh của con người từ vi&ecirc;n đ&aacute; lửa cho đến c&aacute;c c&ocirc;ng cụ bằng kim loại, động cơ hơi nước, năng lượng điện, b&oacute;ng b&aacute;n dẫn cho đến m&aacute;y t&iacute;nh điện tử, mạng Internet, tr&iacute; tuệ nh&acirc;n tạo,... l&agrave; nền m&oacute;ng, trụ cột cho sự ph&aacute;t triển của tất cả c&aacute;c ng&agrave;nh v&agrave; lĩnh vực.</p>\r\n\r\n<p>Nhờ c&oacute; tiến bộ khoa học - c&ocirc;ng nghệ m&agrave; năng suất lao động tăng nhanh, người d&acirc;n ở nhiều quốc gia trở n&ecirc;n gi&agrave;u c&oacute;, sung t&uacute;c, khỏe mạnh v&agrave; sống l&acirc;u hơn. Tuy nhi&ecirc;n, c&aacute;c ph&aacute;t minh, s&aacute;ng chế c&oacute; thể c&oacute; những t&aacute;c động phụ, ti&ecirc;u cực đến đời sống con người cũng như m&ocirc;i trường sinh th&aacute;i tự nhi&ecirc;n. Để gi&uacute;p bạn đọc c&oacute; th&ecirc;m t&agrave;i liệu tham khảo về những vấn đề tr&ecirc;n, Nh&agrave; xuất bản Ch&iacute;nh trị quốc gia Sự thật v&agrave; Th&aacute;i H&agrave; Books tổ chức bi&ecirc;n dịch v&agrave; xuất bản cuốn s&aacute;ch Mặt tr&aacute;i của c&ocirc;ng nghệ của t&aacute;c giả Peter Townsend do Nh&agrave; xuất bản Oxford ấn h&agrave;nh năm 2016. Với 15 chương, nội dung cuốn s&aacute;ch đ&atilde; đề cập rất cụ thể về những t&aacute;c động ti&ecirc;u cực, mặt tr&aacute;i của c&ocirc;ng nghệ như: việc con người biết sử dụng than đ&aacute;, ph&aacute;t triển mạnh khai th&aacute;c kho&aacute;ng sản, nhưng lại l&agrave;m thay đổi điều kiện tự nhi&ecirc;n v&agrave; biến đổi kh&iacute; hậu, nhiều thi&ecirc;n tai g&acirc;y ra cho nh&acirc;n loại; những ph&aacute;t minh, s&aacute;ng chế li&ecirc;n quan đến chữ viết, giấy, da, thuốc kh&aacute;ng sinh, phim ảnh, mạng x&atilde; hội... ngo&agrave;i những lợi &iacute;ch v&ocirc; c&ugrave;ng to lớn mang đến cho con người th&igrave; cũng c&oacute; những ảnh hưởng kh&ocirc;ng tốt đến sức khỏe, sự an to&agrave;n, những kỹ năng sống v&agrave; văn h&oacute;a của c&aacute;c th&agrave;nh vi&ecirc;n trong x&atilde; hội...</p>\r\n\r\n<p>T&aacute;c giả Peter Townsend cũng cảnh b&aacute;o, c&ocirc;ng nghệ c&oacute; thể đem lại nhiều lợi &iacute;ch cho sản xuất v&agrave; đời sống nhưng kh&ocirc;ng n&ecirc;n ho&agrave;n to&agrave;n phụ thuộc v&agrave; bị động trước c&ocirc;ng nghệ, cần dự b&aacute;o những rủi ro v&agrave; c&oacute; biện ph&aacute;p đề ph&ograve;ng những th&aacute;ch thức, mặt tr&aacute;i m&agrave; c&ocirc;ng nghệ mang lại cả do l&ograve;ng tham cũng như sự hiểu biết chưa đầy đủ của con người về c&ocirc;ng nghệ.</p>\r\n', 99800, 'yes', 65, 'image_182009xYtQVAn2.jpg', '2022-06-30 16:33:12', 'Quang Nguyễn', '2022-07-21 17:24:51', 'Quang Nguyễn', 'active', 11, 37),
(28, 'Kỳ Lân Công Nghệ - Giấc Mơ, Hiện Thực & Sự Tan Biến...', '<p>Kỳ L&acirc;n C&ocirc;ng Nghệ - Giấc Mơ, Hiện Thực &amp; Sự Tan Biến... Đ&acirc;y l&agrave; tuyển tập c&aacute;c b&agrave;i b&aacute;o về c&ocirc;ng nghệ của một c&acirc;y b&uacute;t chuy&ecirc;n về c&ocirc;ng nghệ. Nhưng ch&uacute;ng kh&ocirc;ng phải những b&agrave;i b&aacute;o c&ocirc;ng nghệ mang t&iacute;nh học thuật, chuy&ecirc;n m&ocirc;n m&agrave; l&agrave; viết về thời sự c&ocirc;ng nghệ, c&ocirc;ng nghệ giữa d&ograve;ng đời.</p>\r\n\r\n<p>Sức hấp dẫn để đọc của những b&agrave;i viết về c&ocirc;ng nghệ của Thẩm Hồng Thụy l&agrave; sự đan xen, h&ograve;a quyện giữa chuyện đời thường v&agrave; c&ocirc;ng nghệ. Như chuyện kỳ &aacute;n về &ldquo;hợp đồng t&igrave;nh &aacute;i&rdquo; của một c&ocirc; hoa hậu được d&ugrave;ng để so s&aacute;nh với vụ người d&ugrave;ng di động ở Việt Nam bị một c&ocirc;ng ty kinh doanh dịch vụ di động Hongkong &ldquo;trấn lột&rdquo; h&agrave;ng trăm tỷ đồng trong c&aacute;i mối li&ecirc;n minh đầy d&iacute;ch dắc với nh&agrave; mạng, nh&agrave; cung cấp nội dung xứ Việt.</p>\r\n\r\n<p>Như kỳ &aacute;n &ldquo;con ruồi Number 1&rdquo; được d&ugrave;ng để lẩy về những game di động của người Việt c&oacute; thể hốt bạc to&agrave;n cầu. C&acirc;u chuyện, vấn đề, sự kiện được đề cập trong b&agrave;i viết c&oacute; t&iacute;nh thời gian, c&oacute; thể kh&ocirc;ng c&ograve;n t&iacute;nh thời sự nhưng c&ograve;n t&iacute;nh lịch sử, song c&aacute;c kiến thức, những b&igrave;nh luận v&agrave; x&uacute;c cảm m&agrave; t&aacute;c giả truyền tải trong từng b&agrave;i viết lại c&oacute; gi&aacute; trị phi thời gian.</p>\r\n\r\n<p>- Tr&iacute;ch lời giới thiệu của Nh&agrave; b&aacute;o Phạm Hồng Phước</p>\r\n', 120000, 'yes', 25, '9786043354263nBrsXN30.jpg', '2022-06-30 16:35:58', 'Quang Nguyễn', '2022-07-21 17:27:50', 'Quang Nguyễn', 'active', 83, 37),
(29, 'Công Nghệ Nhận Dạng Bằng Sóng Vô Tuyến RFID', '<p>Ng&agrave;y nay với những ứng dụng của khoa học kỹ thuật ti&ecirc;n tiến v&agrave;o đời sống, thế giới đ&atilde; v&agrave; đang ng&agrave;y một thay đổi, văn minh, hiện đại hơn.</p>\r\n\r\n<p>Sự ph&aacute;t triển của c&aacute;c c&ocirc;ng nghệ điện tử mới tạo ra h&agrave;ng loạt c&aacute;c thiết bị với c&aacute;c đặc điểm nổi bật như: Độ ch&iacute;nh x&aacute;c cao, tốc độ nhanh, gọn nhẹ, khả năng ứng dụng cao g&oacute;p phần n&acirc;ng cao năng suất lao động của con người, mang đến sự thỏa m&atilde;n, chất lượng cuộc sống ch&uacute;ng ta ng&agrave;y một tốt hơn.</p>\r\n\r\n<p>Sự ra đời của c&ocirc;ng nghệ RFID (Radio Frequency Identification - c&ocirc;ng nghệ nhận dạng đối tượng bằng s&oacute;ng radio) l&agrave; một &yacute; tưởng độc đ&aacute;o. Tr&ecirc;n thế giới c&ocirc;ng nghệ RFID đ&atilde; được &aacute;p dụng v&agrave; ph&aacute;t triển ở rất nhiều lĩnh vực như: An ninh, qu&acirc;n sự, y học, giải tr&iacute;, thương mại, bưu ch&iacute;nh viễn th&ocirc;ng&hellip; v&agrave; đem lại nhiều lợi &iacute;ch to lớn.</p>\r\n\r\n<p>Nhiều tập đo&agrave;n h&agrave;ng đầu thế giới như: H&atilde;ng sản xuất m&aacute;y bay Airbus, Tập đo&agrave;n điện tử Samsung, Sony, Motorola, &hellip; cũng như c&aacute;c hệ thống si&ecirc;u thị, thu ph&iacute; giao th&ocirc;ng cũng &aacute;p dụng c&ocirc;ng nghệ n&agrave;y. C&ocirc;ng nghệ RFID được xem như c&aacute;nh tay phải đắc lực trong lĩnh vực kinh doanh.</p>\r\n', 70000, 'yes', 16, '9786046703327vOtqITVH.jpg', '2022-07-02 16:55:12', 'Quang Nguyễn', '2022-07-21 17:27:27', 'Quang Nguyễn', 'active', 54, 37),
(30, 'Thuyết Tiến Hóa Công Nghệ Số', '<p>C&ocirc;ng nghệ số kh&ocirc;ng phải l&agrave; một thứ, n&oacute; l&agrave; mọi thứ. Thuyết Tiến H&oacute;a C&ocirc;ng Nghệ Số ch&iacute;nh l&agrave; tiếng chu&ocirc;ng đ&aacute;nh thức để bạn nhận ra rằng sự thay đổi t&iacute;ch lũy về mặt kỹ thuật số l&agrave; kh&ocirc;ng đủ. Đối mặt với nhiều c&ocirc;ng nghệ mang t&iacute;nh cạnh tranh, tốc độ thay đổi chưa từng xảy ra trước đ&acirc;y c&ugrave;ng sự trỗi dậy của những c&ocirc;ng ty si&ecirc;u hiện đại đầu ti&ecirc;n, c&ocirc;ng ty bạn sẽ bị bỏ lại ph&iacute;a sau v&agrave; sẽ tuyệt chủng nếu bạn kh&ocirc;ng chịu h&agrave;nh động.</p>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y c&oacute; một c&aacute;i nh&igrave;n rất th&uacute; vị về &yacute; tưởng ph&acirc;n r&atilde; để truyền cảm hứng cho c&aacute;c c&ocirc;ng ty muốn trở th&agrave;nh người tốt nhất trong phong tr&agrave;o đổi mới về mặt kỹ thuật số v&agrave; muốn tồn tại được trong những giai đoạn bất ổn định n&agrave;y. Với hiện trạng ng&agrave;y c&agrave;ng c&oacute; nhiều lựa chọn, qu&aacute; nhiều dữ liệu để xử l&yacute;, C&ograve;n c&ocirc;ng nghệ th&igrave; đe dọa g&acirc;y ra sự ph&acirc;n r&atilde; cho ngay cả những m&ocirc; h&igrave;nh kinh doanh ổn định nhất, c&aacute;c C&ocirc;ng ty đang đối mặt với nhiều nguồn lực, nh&acirc;n tố c&oacute; thể hủy diệt họ. Nhưng với chiến lược đ&uacute;ng đắn, c&aacute;c nguồn lực n&agrave;y c&oacute; thể gi&uacute;p bạn biến c&ocirc;ng ty m&igrave;nh trở th&agrave;nh người dẫn đầu tr&ecirc;n thương trường.</p>\r\n\r\n<p>Thuyết Tiến H&oacute;a C&ocirc;ng Nghệ Số đ&oacute;ng vai tr&ograve; như c&aacute;nh tay dẫn dắt bạn vượt qua thời khắc hỗn mang n&agrave;y, cung cấp cho bạn những chiến lược c&ugrave;ng lời k&ecirc;u gọi h&agrave;nh động gi&uacute;p thổi b&ugrave;ng l&ecirc;n ngọn lửa thi&ecirc;u ch&aacute;y sự tự m&atilde;n để tạo ra sự thay đổi đầy s&aacute;ng tạo.</p>\r\n\r\n<p>Tom Goodwin đ&atilde; soi s&aacute;ng đường đến tương lai bằng c&aacute;ch nghi&ecirc;n cứu về c&ocirc;ng nghệ, x&atilde; hội, c&ugrave;ng nhiều b&agrave;i học trong qu&aacute; khứ để gi&uacute;p bạn biết c&aacute;ch th&iacute;ch nghi như thế n&agrave;o, n&ecirc;n tận dụng c&aacute;i g&igrave;, bỏ qua c&aacute;i g&igrave;. Để gi&uacute;p bạn thay đổi tư duy của m&igrave;nh, Goodwin đ&atilde; chứng minh những giả định, dự đo&aacute;n m&agrave; giới kinh doanh trước đ&acirc;y từng đưa ra về c&ocirc;ng nghệ số l&agrave; sai ra sao. Nếu muốn c&ocirc;ng ty m&igrave;nh th&agrave;nh c&ocirc;ng trong thời kỳ hậu kỹ thuật số, bạn cần được khai s&aacute;ng bởi Thuyết Tiến H&oacute;a C&ocirc;ng Nghệ Số</p>\r\n', 120000, 'yes', 18, 'image_241309EhgBjHay.jpg', '2022-07-02 16:57:27', 'Quang Nguyễn', '2022-07-21 17:25:30', 'Quang Nguyễn', 'active', 34, 37),
(31, 'Chạy Đua Với Robot', '<p>Nh&acirc;n loại đang trải qua một cuộc c&aacute;ch mạng kh&aacute;c trong c&aacute;ch lo&agrave;i người l&agrave;m việc v&agrave; kiếm sống &ndash; v&agrave; một lần nữa, cuộc c&aacute;ch mạng n&agrave;y lại đốt bỏ những điều tưởng chừng hiển nhi&ecirc;n của qu&aacute; khứ trong đống tro t&agrave;n lịch sử. Một lần nữa, c&ocirc;ng nghệ mới đ&atilde; tạo tiền đề cho c&aacute;ch mạng. Nhưng thay v&igrave; l&agrave; hạt giống, l&agrave; m&aacute;y dệt, hay l&agrave; đầu m&aacute;y hơi nước; động cơ của cuộc c&aacute;ch mạng n&agrave;y l&agrave; c&ocirc;ng nghệ kỹ thuật số v&agrave; robot.</p>\r\n\r\n<p>Cuộc c&aacute;ch mạng kỹ thuật số hiện tại r&otilde; r&agrave;ng kh&aacute;c biệt với c&aacute;c bước nhảy vọt về c&ocirc;ng nghệ trước đ&oacute;, bởi giờ đ&acirc;y khả năng xử l&yacute; dữ liệu &ndash; hay tr&iacute; th&ocirc;ng minh &ndash; của m&aacute;y m&oacute;c dường như kh&ocirc;ng c&ograve;n giới hạn. Ở bất cứ c&ocirc;ng việc đ&atilde; được lập tr&igrave;nh sẵn n&agrave;o, m&aacute;y t&iacute;nh cũng c&oacute; ưu thế về nhận thức hơn con người. V&agrave; bởi việc sao ch&eacute;p phần mềm cũng kh&ocirc;ng mấy kh&oacute; khăn n&ecirc;n bất cứ tiến bộ kỹ thuật số n&agrave;o cũng c&oacute; thể được nh&acirc;n bản ngay lập tức tr&ecirc;n khắp to&agrave;n cầu. Nếu như đ&acirc;y quả thực l&agrave; hướng đi của c&ocirc;ng nghệ trong tương lai gần &ndash; v&agrave; cũng c&oacute; nhiều l&yacute; do để tin v&agrave;o điều đ&oacute; &ndash; th&igrave; sắp tới c&oacute; khả năng việc trả c&ocirc;ng cho người lao động sẽ trở th&agrave;nh điều dị thường. Trong một viễn cảnh tương lai ảm đạm, những cỗ m&aacute;y si&ecirc;u th&ocirc;ng minh sẽ vượt qua khả năng hiểu biết v&agrave; kiểm so&aacute;t của con người. Nếu m&aacute;y t&iacute;nh c&oacute; thể nắm quyền điều khiển nhiều hệ thống quan trọng, hậu quả sẽ rất t&agrave;n khốc. Nhẹ th&igrave; lo&agrave;i người sẽ mất đi quyền kiểm so&aacute;t vận mệnh của m&igrave;nh, c&ograve;n nặng th&igrave; m&aacute;y sẽ đưa người đến bờ tuyệt chủng.</p>\r\n\r\n<p>Tốc độ cải tiến của robot được t&iacute;nh bằng gi&acirc;y, tương lai tăm tối đang lăm le ph&iacute;a trước, vậy con người phải l&agrave;m g&igrave;? C&acirc;u trả lời ch&iacute;nh l&agrave;: học, học kh&ocirc;ng ngừng. Học tập giờ đ&acirc;y l&agrave; một h&agrave;nh tr&igrave;nh kh&ocirc;ng ngừng nghỉ, với rất nhiều điểm để gh&eacute; qua song kh&ocirc;ng c&oacute; một điểm dừng n&agrave;o cả. H&agrave;nh tr&igrave;nh li&ecirc;n tục n&agrave;y c&oacute; t&aacute;c động vượt rất xa phạm vi của khu&ocirc;n vi&ecirc;n trường đại học, v&agrave;o tận nh&agrave;, tận chỗ l&agrave;m, v&agrave; tận c&aacute;c startup của ch&uacute;ng ta. Những t&aacute;c động n&agrave;y sẽ định h&igrave;nh c&aacute;c tham vọng hay thậm ch&iacute; cả luật ph&aacute;p của ch&uacute;ng ta.</p>\r\n\r\n<p>Sau c&ugrave;ng, ch&uacute;ng sẽ t&aacute;c động đến tất cả mọi người, được lan tỏa ra khắp thế giới bằng c&aacute;c mạng lưới đa đại học được x&acirc;y dựng để vượt qua c&aacute;c giới hạn về mức độ, về thời gian, về địa điểm. Sự s&aacute;ng tạo kết hợp với sự linh hoạt tr&iacute; tuệ khiến ch&uacute;ng ta trở n&ecirc;n đặc biệt v&agrave; trở th&agrave;nh sinh vật th&agrave;nh c&ocirc;ng nhất tr&ecirc;n h&agrave;nh tinh n&agrave;y. Ch&uacute;ng sẽ tiếp tục l&agrave; phương c&aacute;ch để mỗi c&aacute; nh&acirc;n tạo n&ecirc;n dấu ấn ri&ecirc;ng trong nền kinh tế.</p>\r\n\r\n<p>D&ugrave; trong lĩnh vực hay ng&agrave;nh nghề n&agrave;o, c&ocirc;ng việc quan trọng nhất m&agrave; con người thực hiện sẽ lu&ocirc;n l&agrave; c&ocirc;ng việc s&aacute;ng tạo. Đ&oacute; l&agrave; l&yacute; do tại sao hệ thống gi&aacute;o dục n&ecirc;n dạy ch&uacute;ng ta c&aacute;ch s&aacute;ng tạo hiệu quả.</p>\r\n', 83000, 'yes', 15, 'image_188351CncRbSNg.jpg', '2022-07-02 16:58:22', 'Quang Nguyễn', '2022-07-21 17:26:17', 'Quang Nguyễn', 'active', 39, 37),
(32, 'Luyện Thi Tiếng Pháp Delf - B2', '<p>Quyển &ldquo;Luyện thi tiếng Ph&aacute;p &ndash; DELF B2&rdquo;, tập hợp c&aacute;c b&agrave;i luyện tập nhằm trang bị cho bạn kiến thức v&agrave; kỹ năng trước kỳ thi DELF B2 của Bộ Gi&aacute;o dục Ph&aacute;p. Bằng DELF B2 x&aacute;c nhận tr&igrave;nh độ tiếng Ph&aacute;p của bạn ở bậc 4 theo Khung tham chiếu 6 bậc về tr&igrave;nh độ ng&ocirc;n ngữ chung của Ch&acirc;u &Acirc;u (CECRL).</p>\r\n', 150000, 'yes', 25, 'image_196874Xz2oqJLU.jpg', '2022-07-02 16:59:57', 'Quang Nguyễn', '2022-07-21 17:26:54', 'Quang Nguyễn', 'active', 47, 36),
(33, 'Ngôi Làng Cổ Mộ', '<p><span style=\"font-size:14px\">Nếu l&agrave; một người y&ecirc;u th&iacute;ch tiểu thuyết kinh dị, bạn sẽ kh&ocirc;ng thể bỏ qua&nbsp;<strong><em>Ng&ocirc;i l&agrave;ng cổ mộ&nbsp;</em></strong>của t&aacute;c giả trẻ Thục Linh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Những chi tiết r&ugrave;ng rợn, những t&igrave;nh huống gay cấn, căng thẳng, sợ h&atilde;i, g&oacute;c nh&igrave;n trong cuộc của người đang phải chạy trốn hay đương đầu với những thế lực huyền b&iacute; ở&nbsp;<strong><em>Ng&ocirc;i l&agrave;ng cổ mộ</em></strong>&nbsp;sẽ l&ocirc;i cuốn bạn kh&ocirc;ng ngừng. Từng c&acirc;u chữ của Thục Linh sẽ dẫn dắt bạn đọc như lạc v&agrave;o ch&iacute;nh xứ sở u linh trong c&acirc;u chuyện, trải nghiệm cảm gi&aacute;c gh&ecirc; rợn một c&aacute;ch ch&acirc;n thực nhất như ch&iacute;nh c&aacute;c nh&acirc;n vật.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng chỉ dừng lại ở yếu tố kinh dị, điều đặc sắc hơn cả l&agrave; đằng sau bức m&agrave;n c&acirc;u chuyện huyền b&iacute; t&acirc;m linh, Thục Linh sẽ kể cho độc giả một bức tranh đời thực trần trụi nhất, sự đương đầu giữa c&aacute;i &aacute;c v&agrave; c&aacute;i thiện trong cuộc sống, t&igrave;nh y&ecirc;u thương v&agrave; x&uacute;c cảm bản năng nhất của con người, những gi&aacute; trị nh&acirc;n văn m&agrave; đ&ocirc;i l&uacute;c dường như ta đ&atilde; v&ocirc; t&igrave;nh l&atilde;ng qu&ecirc;n&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">---</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>&ldquo;T&ocirc;i vội chạy ra xem th&igrave; chợt nhận ra những l&agrave;n kh&oacute;i mờ đục đang lẩn khuất khắp con đường l&agrave;ng nứt nẻ. Đ&oacute; l&agrave; sương m&ugrave;. Sương m&ugrave; d&acirc;ng cao v&agrave; ng&agrave;y c&agrave;ng d&agrave;y đặc hơn...&rdquo;</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>&ldquo;T&ocirc;i bước theo sau mẹ, tiến gần đến ng&ocirc;i nh&agrave; mới của ch&uacute;ng t&ocirc;i m&agrave; kh&ocirc;ng biết rằng t&ocirc;i đang từng bước lại gần những cơn &aacute;c mộng dai dẳng sau n&agrave;y...&rdquo;</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>&ldquo;T&ocirc;i &ocirc;m chặt hai c&aacute;nh tay rồi đi gần như chạy qua cầu, l&ograve;ng dấy l&ecirc;n ch&uacute;t sợ h&atilde;i. C&acirc;y cầu khẽ rung l&ecirc;n trong từng bước ch&acirc;n. Khi t&ocirc;i tiến lại gần ph&iacute;a thằng b&eacute; th&igrave; &aacute;nh mắt v&ocirc; t&igrave;nh đảo xuống dưới ch&acirc;n. Qua một chiếc lỗ nứt nẻ dưới tấm v&aacute;n gỗ l&oacute;t cầu đ&atilde; mục, một con mắt trắng d&atilde; đục ngầu đang nh&igrave;n l&ecirc;n t&ocirc;i chằm chằm&hellip;&rdquo;</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>T&aacute;c giả Thục Linh</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc; tốt nghiệp khoa Sư phạm của trường Đại học Quốc gia H&agrave; Nội v&agrave; hiện đang l&agrave; gi&aacute;o vi&ecirc;n bộ m&ocirc;n Ngữ Văn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thục Linh bắt đầu viết truyện từ năm 2017 v&agrave; nhận được sự quan t&acirc;m rất lớn từ c&aacute;c độc giả y&ecirc;u th&iacute;ch truyện t&acirc;m linh tr&ecirc;n mạng x&atilde; hội. Mỗi t&aacute;c phẩm của c&ocirc; đều được độc giả đ&aacute;nh gi&aacute; cao về độ li k&igrave;, ma mị v&agrave; t&iacute;nh nh&acirc;n văn. Ngo&agrave;i sở th&iacute;ch viết l&aacute;ch, c&ocirc; c&ograve;n c&oacute; niềm đam m&ecirc; với bộ m&ocirc;n huyền học Tarot v&agrave; đ&agrave;n h&aacute;t, vẽ tranh.</span></p>\r\n', 170000, 'yes', 15, '8936107812180_1_1HoOEYgNv.jpg', '2022-08-06 01:32:40', 'Quang Nguyễn', NULL, NULL, 'active', 5, 34),
(34, 'Khế Ước Bán Dâu', '<p><span style=\"font-size:14px\">Bước ch&acirc;n v&agrave;o l&agrave;m d&acirc;u của một gia đ&igrave;nh ph&uacute; hộ nức tiếng khắp v&ugrave;ng Kinh Bắc, Nh&agrave;i &ndash; con g&aacute;i của một nh&agrave; nho ngh&egrave;o &ndash; mong ước sẽ được đổi đời trong th&acirc;n phận mới: mợ hai nh&agrave; họ Vũ. Thế nhưng, Nh&agrave;i đ&acirc;u thể ngờ được, điều chờ đ&oacute;n c&ocirc; trong căn nh&agrave; uy nghi bề thế đ&oacute; lại chẳng phải cuộc sống ấm &ecirc;m lụa l&agrave;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Từ những ng&agrave;y đầu về l&agrave;m d&acirc;u, Nh&agrave;i đ&atilde; cảm nhận được bầu kh&ocirc;ng kh&iacute; quỷ dị bao tr&ugrave;m lấy ng&ocirc;i nh&agrave; mỗi khi m&agrave;n đ&ecirc;m bu&ocirc;ng xuống. Những cơn &aacute;c mộng k&igrave; lạ rượt đuổi c&ocirc; h&agrave;ng đ&ecirc;m, những b&oacute;ng ma đ&ograve;i đoạt mạng cứ xuất hiện trở đi trở lại&hellip; v&agrave; cả những người bốc hơi kh&ocirc;ng một dấu vết nữa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lần m&ograve; theo những điềm b&aacute;o qu&aacute;i gở, Nh&agrave;i v&ocirc; t&igrave;nh v&eacute;n l&ecirc;n bức m&agrave;n kinh ho&agrave;ng, đẫm m&aacute;u ẩn nấp sau vẻ bề ngo&agrave;i gi&agrave;u sang, quyền thế của gia m&ocirc;n họ Vũ. Đ&oacute; cũng l&agrave; l&uacute;c cuộc đời c&ocirc; l&uacute;n s&acirc;u xuống vực thẳm kh&ocirc;ng lối tho&aacute;t...</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Về t&aacute;c giả</strong>:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thục Linh l&agrave; t&aacute;c giả quen thuộc với độc giả của d&ograve;ng s&aacute;ch t&acirc;m linh &ndash; kinh dị Việt qua nhiều tựa s&aacute;ch nổi tiếng đ&atilde; được xuất bản trong nước từ năm 2020 đến nay.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bắt đầu sự nghiệp viết l&aacute;ch từ năm 2017, c&ocirc; đ&atilde; nhận được rất nhiều sự ủng hộ từ cộng đồng y&ecirc;u s&aacute;ch cũng như kh&ocirc;ng &iacute;t đ&aacute;nh gi&aacute; t&iacute;ch cực từ giới ph&ecirc; b&igrave;nh chuy&ecirc;n m&ocirc;n. C&aacute;c t&aacute;c phẩm của Thục Linh kh&ocirc;ng những khắc họa ấn tượng m&agrave;u sắc văn h&oacute;a d&acirc;n gian Việt m&agrave; c&ograve;n nắm bắt được hơi thở, tinh thần của đời sống đương đại..</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Tr&iacute;ch dẫn hay trong t&aacute;c phẩm</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">1. Một cảm x&uacute;c vừa đau đớn, vừa c&oacute; ch&uacute;t ghen tị, tủi th&acirc;n &ugrave;a v&agrave;o l&ograve;ng Nh&agrave;i như một ngọn s&oacute;ng. Đ&acirc;y chẳng nhẽ l&agrave; vợ trước của chồng Nh&agrave;i ư? Sao anh ấy vẫn giữ ảnh của c&ocirc; ta trong ph&ograve;ng? Điều bất thường ấy c&oacute; lẽ l&agrave; điểm đ&aacute;ng ch&uacute; &yacute; đầu ti&ecirc;n b&aacute;o hiệu những sự kiện k&igrave; dị v&agrave; gh&ecirc; rợn sắp cuốn lấy Nh&agrave;i kể từ gi&acirc;y ph&uacute;t c&ocirc; ch&iacute;nh thức trở th&agrave;nh mợ hai nh&agrave; họ Vũ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">2. Nh&agrave;i nhắm mắt, ch&igrave;m dần v&agrave;o giấc ngủ. Chỉ &iacute;t l&acirc;u sau, một &acirc;m thanh k&igrave; lạ, đầy gh&ecirc; rợn lọt v&agrave;o trong cơn mơ của c&ocirc;, nửa hư nửa thực: &ldquo;Huhuhu... huhu... K&eacute;t... k&eacute;t... k&eacute;t... cạch cạch. Huhu... K&eacute;t... k&eacute;t... cạch cạch...&rdquo; Tiếng r&ecirc;n rỉ, nỉ non kh&oacute;c l&oacute;c vang l&ecirc;n, c&ugrave;ng với tiếng thứ g&igrave; đ&oacute; đập xuống nền đ&aacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">3. &ldquo;Huhu&hellip;huhuhu&hellip; G&aacute;i c&oacute; c&ocirc;ng&hellip; chồng vẫn phụ&hellip; Huhuhu&hellip; G&aacute;i c&oacute;&hellip; c&ocirc;ng&hellip; th&igrave;&hellip; chồng vẫn&hellip;phụ&hellip; huhu&hellip;huhu.&rdquo; Tiếng cười khanh kh&aacute;ch pha tạp từ trước đang ng&agrave;y một lớn hơn, vang ra từ khắp ph&iacute;a. Nh&agrave;i kh&ocirc;ng hiểu sao m&igrave;nh kh&ocirc;ng thể chạy ra khỏi đ&acirc;y được. B&agrave; gi&agrave; kia đ&atilde; b&ograve; tới rất s&aacute;t ph&iacute;a Nh&agrave;i, c&ocirc; c&agrave;ng nh&igrave;n r&otilde; th&acirc;n thể b&agrave; ta hơn. C&aacute;nh tay b&agrave; ta d&agrave;i ngoẵng, đen s&igrave;, kh&ocirc; đ&eacute;t cả lại, mười đầu m&oacute;ng tay d&agrave;i g&atilde;y mủn, nhuốm m&aacute;u v&igrave; c&agrave;o xuống nền đ&aacute; n&atilde;y giờ. Xương vai b&agrave; ta nh&ocirc; l&ecirc;n v&igrave; phải mang v&aacute;c thanh gỗ nặng. B&agrave; ta mặc một bộ quần &aacute;o r&aacute;ch rưới, vương đầy m&aacute;u tươi. B&agrave; ta ngẩng mặt l&ecirc;n, một gương mặt khiến Nh&agrave;i khiếp h&atilde;i: hai g&ograve; m&aacute; lồ lộ, m&aacute; h&oacute;p v&agrave;o như kh&ocirc;ng c&ograve;n ch&uacute;t thịt, hai tr&ograve;ng mắt đục ngầu, h&agrave;m răng ố v&agrave;ng nham nhở, quanh m&eacute;p vẫn c&ograve;n vương m&agrave;u m&aacute;u... &ldquo;Đ&acirc;y rồi! Đ&acirc;y rồi...&rdquo; B&agrave; ta hằm h&egrave; rồi cười l&ecirc;n khinh kh&iacute;ch. Bất chợt b&agrave; ta lao bổ v&agrave;o người Nh&agrave;i.</span></p>\r\n', 155000, 'yes', 13, '8936066693820IQ50f9nC.jpg', '2022-08-06 01:35:22', 'Quang Nguyễn', '2022-08-06 01:56:37', 'Quang Nguyễn', 'active', 7, 34),
(35, 'Tứ Trấn Huyền Linh', '<p><span style=\"font-size:14px\">Bỗng một ng&agrave;y tỉnh dậy, bạn ph&aacute;t hiện một bản sao giống y hệt m&igrave;nh. Người đ&oacute; kh&ocirc;ng phải anh chị em sinh đ&ocirc;i, lại c&agrave;ng kh&ocirc;ng phải họ h&agrave;ng th&acirc;n th&iacute;ch của bạn? Thế nhưng từ trang phục, kiểu t&oacute;c đến c&aacute;ch h&agrave;nh xử lại giống bạn đến lạ thường. Nếu bất chợt bị rơi v&agrave;o t&igrave;nh cảnh đ&oacute;, bạn sẽ chọn c&aacute;ch xử tr&iacute; như thế n&agrave;o?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đ&oacute; cũng ch&iacute;nh l&agrave; t&igrave;nh huống &eacute;o le m&agrave; ch&agrave;ng nh&acirc;n vật ch&iacute;nh trong&nbsp;<strong>Tứ Trấn Huyền Linh</strong>&nbsp;gặp phải. Tĩnh &ndash; ch&agrave;ng sinh vi&ecirc;n trường Y với những ước mơ v&agrave; ho&agrave;i b&atilde;o lớn về tương lai v&agrave; sự nghiệp bỗng bị &aacute;m bởi lời nguyền từ kẻ nhị tr&ugrave;ng, một bản sao y hệt của ch&iacute;nh anh. L&agrave; một người tin v&agrave;o khoa học, tin v&agrave;o thực tế, thế nhưng trải qua biết bao sự kiện dị thường, ma mị, ch&iacute;nh Tĩnh cũng phải thừa nhận về sự hiện diện của một thế giới t&acirc;m linh k&igrave; ảo v&agrave; phải nương nhờ những thần linh ở c&aacute;c trấn Thăng Long để gi&uacute;p h&oacute;a giải lời nguyền b&iacute; ẩn kia.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mỗi v&ugrave;ng đất, mỗi con người trong&nbsp;<strong>Tứ Trấn Huyền Linh</strong>&nbsp;đều chứa đựng những c&acirc;u chuyện kh&oacute; đo&aacute;n ph&iacute;a sau n&oacute;. C&ugrave;ng theo ch&acirc;n Tĩnh v&agrave;o những chuyến &ldquo;du kh&ocirc;ng&rdquo;, ta biết được v&ocirc; v&agrave;n những sự kiện lịch sử v&agrave; những ẩn t&igrave;nh đầy đau thương. Đ&oacute; l&agrave; c&ocirc; c&ocirc;ng ch&uacute;a dốc hết t&acirc;m can, b&agrave;y đủ mưu kế để chiếm được tr&aacute;i tim người m&igrave;nh y&ecirc;u. Một vị vua lỗi lạc kh&ocirc;ng may sập bẫy gian t&agrave; dẫn đến họa vong quốc. Hay l&agrave; kẻ &ocirc;m mộng lớn, đến l&igrave;a đời vẫn c&ograve;n m&atilde;i tiếc thương&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">H&agrave;nh tr&igrave;nh h&oacute;a giải lời nguyền của Tĩnh c&ograve;n c&oacute; sự gi&uacute;p đỡ v&ocirc; c&ugrave;ng nhiệt t&igrave;nh từ V&acirc;n với biệt danh &ldquo;b&agrave; c&ocirc; lịch sử&rdquo; c&ugrave;ng với Duy &ndash; anh bạn c&ugrave;ng qu&ecirc; l&eacute;m lỉnh, lu&ocirc;n ở b&ecirc;n cạnh v&agrave; sẵn s&agrave;ng gi&uacute;p đỡ Tĩnh kh&ocirc;ng ngại kh&oacute; khăn. Những c&acirc;u chuyện xoay quanh ba người bạn n&agrave;y kh&ocirc;ng chỉ khiến ch&uacute;ng ta ngạc nhi&ecirc;n về c&aacute;ch họ c&ugrave;ng nhau t&igrave;m ra giải ph&aacute;p cho những vấn đề t&acirc;m linh b&iacute; ẩn m&agrave; c&ograve;n khiến ch&uacute;ng ta v&ocirc; c&ugrave;ng ngưỡng mộ về t&igrave;nh bạn đẹp, trong s&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với lối viết v&ocirc; c&ugrave;ng l&ocirc;i cuốn, kh&eacute;o l&eacute;o lồng gh&eacute;p c&aacute;c sự kiện lịch sử trong mạch truyện. T&aacute;c giả Thục Linh đ&atilde; viết n&ecirc;n những chương truyện đầy hấp dẫn v&agrave; kh&oacute; đo&aacute;n được c&aacute;i kết cho tới tận những trang cuối c&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Lời khuy&ecirc;n của Bi&ecirc;n tập vi&ecirc;n d&agrave;nh cho việc đọc cuốn s&aacute;ch:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nếu bạn đọc đang muốn t&igrave;m hiểu về những b&iacute; ẩn t&acirc;m linh, hay muốn biết về những sự kiện lịch sử c&ugrave;ng những giai thoại ph&iacute;a sau n&oacute;, nhưng những cuốn s&aacute;ch thi&ecirc;n về lịch sử lại qu&aacute; h&agrave;n l&acirc;m, kh&oacute; hiểu th&igrave;&nbsp;<strong>Tứ Trấn Huyền Linh</strong>&nbsp;ch&iacute;nh l&agrave; một sự lựa chọn tuyệt vời. T&aacute;c giả kh&eacute;o l&eacute;o lồng gh&eacute;p những chi tiết lịch sử về c&aacute;c triều đại, c&aacute;c vị vua, quan hay cả những danh lam thắng cảnh những đền, trấn cổ k&iacute;nh v&agrave;o h&agrave;nh tr&igrave;nh h&oacute;a giải lời nguyền của nh&acirc;n vật ch&iacute;nh. Ch&iacute;nh sự t&agrave;i t&igrave;nh ấy đ&atilde; tạo n&ecirc;n một c&acirc;u chuyện t&acirc;m linh &ndash; lịch sử vừa chứa đựng kiến thức, vừa ma mị, hấp dẫn kh&oacute; l&ograve;ng rời mắt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&acirc;u n&oacute;i hay d&agrave;nh tặng bạn:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- &ldquo;Thế gian n&agrave;y thật nực cười, kẻ đau đớn nhất lại thường lặng thinh.&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- &ldquo;Nặn đất s&eacute;t, cũng l&agrave; một c&aacute;ch để thư gi&atilde;n nhiều đ&oacute; cậu trai. Cần sự tỉ mẩn, ch&iacute;nh x&aacute;c, kh&eacute;o l&eacute;o, v&agrave; cả t&iacute;nh ki&ecirc;n nhẫn nữa, thứ m&agrave; cậu chưa c&oacute; đấy.&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- &ldquo;Tức l&agrave;&hellip; T&ocirc;i đến đ&oacute; cầu xin được bảo hộ th&ocirc;i hả?&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ldquo;Haha&hellip; ở đời l&agrave;m g&igrave; c&oacute; chuyện dễ d&agrave;ng thế. Nếu thế th&igrave; ai đi cầu xin cũng sung sướng hết cả rồi. Muốn được gi&uacute;p đỡ th&igrave; phải đi gi&uacute;p đỡ trước.&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Th&ocirc;ng tin t&aacute;c giả</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Nguyễn Thục Linh</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&uacute;t danh: Thục Linh</span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc; tốt nghiệp khoa Sư phạm của trường Đại Học Quốc Gia H&agrave; Nội v&agrave; hiện đang l&agrave; gi&aacute;o vi&ecirc;n bộ m&ocirc;n Ngữ văn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thục Linh bắt đầu viết truyện từ năm 2017 v&agrave; nhận được sự quan t&acirc;m rất lớn từ c&aacute;c độc giả y&ecirc;u th&iacute;ch truyện t&acirc;m linh tr&ecirc;n mạng x&atilde; hội. Mỗi t&aacute;c phẩm của c&ocirc; đều được độc giả đ&aacute;nh gi&aacute; cao về đồ li k&igrave;, ma mị v&agrave; t&iacute;nh nh&acirc;n văn. Ngo&agrave;i sở th&iacute;ch viết l&aacute;ch, c&ocirc; c&ograve;n c&oacute; niềm đam m&ecirc; với bộ m&ocirc;n huyền học Tarot v&agrave; đ&agrave;n h&aacute;t, vẽ tranh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ldquo;Ng&ocirc;i l&agrave;ng cổ mộ&rdquo; l&agrave; s&aacute;ch xuất bản đầu tay của t&aacute;c giả Thục Linh. Ấn phẩm đ&atilde; b&aacute;n hết 1000 bản sau 1 th&aacute;ng ra mắt.</span></p>\r\n', 240000, 'yes', 5, 'image_2218597BGMNJ2g.jpg', '2022-08-06 01:37:24', 'Quang Nguyễn', NULL, NULL, 'active', 9, 34);
INSERT INTO `book` (`id`, `name`, `description`, `price`, `special`, `sale_off`, `picture`, `created`, `created_by`, `modified`, `modified_by`, `status`, `ordering`, `category_id`) VALUES
(36, 'Ngôi Làng Linh Thiêng', '<p><span style=\"font-size:14px\">Ng&ocirc;i L&agrave;ng Linh Thi&ecirc;ng</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ở v&ugrave;ng đồng bằng Bắc Bộ c&oacute; một ng&ocirc;i l&agrave;ng cổ t&ecirc;n Đ&igrave;nh Long, ng&ocirc;i l&agrave;ng với nhiều di t&iacute;ch lịch sử, nhiều c&ocirc;ng tr&igrave;nh cổ k&iacute;nh v&agrave; cũng nhiều điều kỳ lạ xảy ra. Trong l&agrave;ng Đ&igrave;nh Long c&oacute; một cậu con trai t&ecirc;n l&agrave; Nguy&ecirc;n mồ c&ocirc;i cha từ nhỏ, Nguy&ecirc;n căn cao số nặng lại mang nghiệp &acirc;m v&igrave; thế cậu đ&atilde; trải qua rất nhiều s&oacute;ng gi&oacute; mới c&oacute; thể sống s&oacute;t được. Lớn l&ecirc;n Nguy&ecirc;n lu&ocirc;n gặp trắc trở cả về cuộc sống lẫn t&igrave;nh cảm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Từ những trải nghiệm t&acirc;m linh, Nguy&ecirc;n dần dần hiểu được số phận của cậu, cậu bắt đầu x&acirc;m nhập v&agrave;o thế giới T&acirc;m Linh huyền b&iacute;. Một ng&agrave;y nọ Nguy&ecirc;n gặp một người con g&aacute;i rất xinh đẹp nhưng v&ocirc; c&ugrave;ng b&iacute; ẩn, Nguy&ecirc;n kh&ocirc;ng r&otilde; c&ocirc; g&aacute;i l&agrave; ai, xuất hiện với mục đ&iacute;ch g&igrave;, c&ocirc; ta thoắt ẩn thoắt hiện như một b&oacute;ng ma lu&ocirc;n &aacute;m ảnh cậu...</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng chỉ l&agrave; những c&acirc;u chuyện về cậu thiếu ni&ecirc;n t&ecirc;n Nguy&ecirc;n b&ecirc;n cạnh đ&oacute; &quot;Ng&ocirc;i L&agrave;ng Linh Thi&ecirc;ng&quot; c&ograve;n đưa ch&uacute;ng ta trở về qu&aacute; khứ, về tuổi thơ qua những ng&ocirc;n từ gần gũi th&acirc;n thuộc. Cuộc sống hiện đại qu&aacute; bận rộn khiến nhiều người qu&ecirc;n đi qu&atilde;ng thời gian ấu thơ, qu&ecirc;n đi những c&acirc;u chuyện được nghe từ b&agrave; từ mẹ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đọc truyện n&agrave;y sẽ l&agrave;m ch&uacute;ng ta sống lại k&yacute; ức, những kỷ niệm ấu thơ tưởng chừng như bị l&atilde;ng qu&ecirc;n. Nhiều người từng n&oacute;i nếu c&oacute; cơ hội trở về tuổi thơ th&igrave; ai cũng muốn được trở về một lần. Vậy c&ograve;n chờ đợi g&igrave; nữa m&agrave; kh&ocirc;ng kh&aacute;m ph&aacute; bộ truyện &quot;Ng&ocirc;i L&agrave;ng Linh Thi&ecirc;ng&quot;, một bộ truyện ma l&agrave;ng qu&ecirc; hấp dẫn đầy t&iacute;nh nh&acirc;n văn v&agrave; chứa đựng một phần k&yacute; ức tuổi thơ đang chờ bạn kh&aacute;m ph&aacute;.</span></p>\r\n', 157000, 'yes', 10, 'bia_ngnglinhthiengo5xazHAw.jpg', '2022-08-06 01:40:26', 'Quang Nguyễn', NULL, NULL, 'active', 11, 34),
(37, 'Trái Tim Của Quỷ', '<p><span style=\"font-size:14px\">Tr&aacute;i tim của quỷ - B&iacute; mật kinh ho&agrave;ng đằng sau bốn cuốn nhật k&yacute;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ryosuke l&agrave; một ch&agrave;ng trai với gia cảnh b&igrave;nh thường, vốn dĩ anh tưởng rằng cuộc đời m&igrave;nh sẽ tr&ocirc;i qua &ecirc;m đềm như vậy m&atilde;i. Cho đến một ng&agrave;y mọi sự đen đủi tr&ecirc;n đời theo nhau ập đến cuộc đời anh. Vị h&ocirc;n th&ecirc; sắp cưới đột nhi&ecirc;n biến mất, người mẹ anh y&ecirc;u qu&yacute; gặp tai nạn giao th&ocirc;ng qua đời, bố anh mắc phải căn bệnh ung thư kh&oacute; l&ograve;ng cứu chữa, b&agrave; ngoại th&igrave; bệnh tật nằm trong viện dưỡng l&atilde;o.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Những tưởng những sự việc kỳ qu&aacute;i chỉ dừng lại ở đ&oacute;, th&igrave; trong một lần về thăm bố Ryosuke t&igrave;nh cờ ph&aacute;t hiện 4 cuốn sổ cũ nằm trong tủ &acirc;m tường. Những cuốn sổ với ti&ecirc;u đề&nbsp;<em>Yurigokoro&nbsp;</em>c&ugrave;ng với lời lẽ v&ocirc; c&ugrave;ng dị thường.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>&ldquo;Phải chăng cấu tr&uacute;c bộ n&atilde;o của một kẻ c&oacute; thể b&igrave;nh thản xuống tay giết người như t&ocirc;i c&oacute; g&igrave; đ&oacute; kh&aacute;c biệt so với người b&igrave;nh thường?&rdquo;</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bất gi&aacute;c đọc tiếp, anh nhận ra đ&acirc;y l&agrave; những lời th&uacute; tội của một người kẻ lu&ocirc;n dửng dưng l&agrave;m thinh trước c&aacute;i chết của người kh&aacute;c, đồng thời đang t&acirc;m xuống tay giết kh&ocirc;ng biết bao nhi&ecirc;u sinh mạng ngay từ khi c&ograve;n nhỏ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&ugrave;ng l&uacute;c đ&oacute;, điều dội về trong k&yacute; ức của Ryosuke l&agrave; th&aacute;i độ nơm nớp lo sợ của mẹ m&igrave;nh tầm một th&aacute;ng trước khi b&agrave; qua đời. Anh c&ograve;n nhớ lại chuyện l&uacute;c l&agrave; một cậu b&eacute;, anh trở về nh&agrave; sau thời gian d&agrave;i nằm viện v&agrave; cảm thấy mẹ đ&atilde; bị tr&aacute;o đổi th&agrave;nh người kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Liệu những k&yacute; ức ấy c&ugrave;ng mấy cuốn sổ n&agrave;y c&oacute; li&ecirc;n quan g&igrave; đến nhau hay kh&ocirc;ng?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Người viết ra ch&uacute;ng l&agrave; bố anh, người mẹ đ&atilde; mất của anh hay một ai đ&oacute; kh&aacute;c?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">V&agrave; những điều được viết b&ecirc;n trong l&agrave; sự thật hay chỉ l&agrave; hư cấu?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Rốt cuộc trong qu&aacute; khứ đ&atilde; c&oacute; chuyện g&igrave; xảy ra trong gia đ&igrave;nh n&agrave;y?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dự cảm kh&ocirc;ng l&agrave;nh nhộn nhạo khắp c&otilde;i l&ograve;ng anh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mở đầu bằng một thế giới u tối t&aacute;ch biệt v&agrave; kết th&uacute;c bằng t&igrave;nh thương s&acirc;u nặng, dưới ng&ograve;i b&uacute;t đầy thực tế c&ugrave;ng c&aacute;ch xử l&yacute; cốt truyện t&agrave;i t&igrave;nh v&agrave; l&ocirc;i cuốn của t&aacute;c giả Mahokura Numata,&nbsp;<em>Tr&aacute;i tim của quỷ</em>&nbsp;- cuốn tiểu thuyết huyền b&iacute; đề cập đến c&aacute;c t&igrave;nh tiết r&ugrave;ng rợn v&agrave; mặt tối của bản chất con người sẽ khiến bạn đọc cuốn theo v&agrave; kh&ocirc;ng thể n&agrave;o qu&ecirc;n.</span></p>\r\n', 135000, 'yes', 6, '8935325006234YeB20c5F.jpg', '2022-08-06 01:51:27', 'Quang Nguyễn', NULL, NULL, 'active', 13, 34),
(38, 'Truyện Ma Không Hồi Kết', '<p><span style=\"font-size:14px\">Sau khi &#39;&#39;rải&#39;&#39; CV khắp nơi, Dương Nguy&ecirc;n Nhất được nhận v&agrave;o l&agrave;m tại c&ocirc;ng ty th&aacute;m tử Monster. Mang tiếng l&agrave; c&ocirc;ng ty th&aacute;m tử, nhưng thực chất đ&acirc;y l&agrave; nơi tiếp nhận v&agrave; xử l&yacute; c&aacute;c thảm &aacute;n li&ecirc;n quan đến những truyền thuyết đ&ocirc; thị r&ugrave;ng rợn, đ&aacute;ng sợ vốn đ&atilde; được lưu truyền từ đời n&agrave;y sang sang đời kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&aacute;c nh&acirc;n vi&ecirc;n trong c&ocirc;ng ty n&agrave;y đều v&ocirc; c&ugrave;ng qu&aacute;i gở v&agrave; b&iacute; ẩn, nhưng người khiến anh t&ograve; m&ograve; nhất ch&iacute;nh l&agrave; vị sếp tổng Ngụy Di&ecirc;n Khanh lạnh l&ugrave;ng, kiệm lời m&agrave; anh chưa từng nh&igrave;n r&otilde; mặt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thế rồi một ng&agrave;y nọ, anh v&ocirc; t&igrave;nh ph&aacute;t hiện ra dung mạo của sếp tổng giống hệt di ảnh tr&ecirc;n b&agrave;i vị của &ldquo;người chồng qu&aacute; cố&rdquo; m&agrave; m&igrave;nh vẫn lu&ocirc;n b&aacute;i lạy. Từ đ&oacute;, những b&iacute; ẩn trong qu&aacute; khứ cũng lần lượt bị b&oacute;c trần&hellip;</span></p>\r\n', 169000, 'yes', 25, '6001truyen-ma-khong-hoi-ketOQfSPLDp.jpg', '2022-08-06 01:55:26', 'Quang Nguyễn', NULL, NULL, 'active', 14, 34),
(39, 'Quan Tài Đầu Đông', '<p><span style=\"font-size:14px\">Ng&agrave;y xưa c&oacute; một ng&ocirc;i l&agrave;ng, ở đ&acirc;y đ&agrave;n &ocirc;ng rất kh&oacute; lấy vợ, v&igrave; nh&agrave; ai cũng chỉ sinh con trai. Thật ra kh&ocirc;ng phải do huyết thống d&acirc;n l&agrave;ng như vậy, m&agrave; l&agrave; do truyền thống, hễ sinh con g&aacute;i th&igrave; phải đem vứt xuống c&aacute;i th&aacute;p rỗng ngo&agrave;i l&agrave;ng. O&aacute;n kh&iacute; t&iacute;ch tụ nhiều đời, một đ&ecirc;m nọ c&aacute;c thai nhi rữa n&aacute;t b&ograve; ra khỏi th&aacute;p, v&agrave;o l&agrave;ng t&agrave;n s&aacute;t kh&ocirc;ng chừa một ai.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ng&agrave;y nay ở chỗ ng&ocirc;i l&agrave;ng cũ, c&aacute;ch đ&ocirc; thị phồn hoa chỉ tầm một tiếng l&aacute;i xe, c&oacute; một c&aacute;i hồ h&igrave;nh thai nhi. B&ecirc;n hồ c&oacute; một ng&ocirc;i nh&agrave; lớn, trong ng&ocirc;i nh&agrave; lớn l&agrave; một gia đ&igrave;nh nhiều thế hệ chỉ sinh con trai, kh&ocirc;ng phải do huyết thống, m&agrave; do truyền thống: cứ sinh con g&aacute;i l&agrave; đem vứt xuống hồ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&ugrave;a đ&ocirc;ng năm nay, trong nh&agrave; họ tuần tự xảy ra &aacute;n mạng, người trong nh&agrave; bị giết: l&uacute;c ở dưới đất, l&uacute;c ở b&ecirc;n nước, khi ở tr&ecirc;n trời&hellip; Giống nhau ở chỗ tất cả c&aacute;c vụ đều l&agrave; ph&ograve;ng k&iacute;n, v&agrave; c&aacute;c hiện trường đều c&oacute; d&acirc;y rốn trẻ sơ sinh. Phải chăng o&aacute;n kh&iacute; t&iacute;ch tụ nhiều đời đ&atilde; khiến người trong nh&agrave; cũng phải chịu quả b&aacute;o, như trong truyền thuyết ng&ocirc;i l&agrave;ng t&agrave;n bạo năm n&agrave;o?</span></p>\r\n', 95000, 'yes', 13, 'quan-tai-dau-dong---bia-1hkZHlI4d.jpg', '2022-08-06 01:58:42', 'Quang Nguyễn', NULL, NULL, 'active', 16, 34),
(40, 'Bogiwan - Quỷ Săn Người', '<p><span style=\"font-size:14px\"><em>Nếu&nbsp;n&oacute;&nbsp;t&igrave;m đến g&otilde; cửa, đừng mở ra, cũng đừng trả lời&hellip;</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đ&oacute; l&agrave; b&iacute; quyết trốn tr&aacute;nh bogiwan được truyền từ đời n&agrave;y sang đời kh&aacute;c. Bogiwan l&agrave; một lo&agrave;i quỷ nơi sơn d&atilde;, duy tr&igrave; n&ograve;i giống bằng c&aacute;ch bắt người về n&uacute;i. C&aacute;ch thức của n&oacute; l&agrave; đến gần, gọi t&ecirc;n li&ecirc;n mi&ecirc;n, chỉ cần con mồi đ&aacute;p một tiếng l&agrave; sẽ bị c&acirc;u đi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hideki đang ở c&ocirc;ng ty th&igrave; đồng nghiệp b&aacute;o l&agrave; c&oacute; kh&aacute;ch t&igrave;m dưới sảnh, nhắn rằng muốn b&agrave;n về c&ocirc; con g&aacute;i sắp sinh của anh. Hideki chạy xuống, kh&ocirc;ng thấy kh&aacute;ch đ&acirc;u, nhưng lại thấy n&aacute;ch &aacute;o đồng nghiệp bỗng nhi&ecirc;n loang đầy m&aacute;u, &aacute;o kh&ocirc;ng r&aacute;ch m&agrave; thịt bị nhay n&aacute;t như th&uacute; dữ cắn&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hideki bỗng b&agrave;ng ho&agrave;ng nhận ra rằng, ngo&agrave;i m&igrave;nh v&agrave; vợ, n&agrave;o đ&atilde; ai biết họ c&oacute; con g&aacute;i sắp sinh. Thế nhưng, đấy mới chỉ l&agrave; khởi đầu cho chuỗi ng&agrave;y cả nh&agrave; trốn chạy con quỷ đ&atilde; r&aacute;o riết săn l&ugrave;ng c&aacute;c thế hệ gia đ&igrave;nh họ từ n&ocirc;ng th&ocirc;n ra th&agrave;nh thị&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lồng gh&eacute;p xuất sắc chất liệu d&acirc;n gian v&agrave;o tội &aacute;c gia đ&igrave;nh thời hiện đại, BOGIWAN - QUỶ SĂN NGƯỜI đ&atilde; được vinh danh bằng giải thưởng Tiểu thuyết Kinh dị Nhật Bản năm 2015, v&agrave; được chuyển thể th&agrave;nh bộ phim điện ảnh c&ugrave;ng t&ecirc;n năm 2018.</span></p>\r\n', 105000, 'yes', 23, 'bogiwanbia1HFp83ZgR.jpg', '2022-08-06 02:21:42', 'Quang Nguyễn', NULL, NULL, 'active', 19, 34),
(41, 'Lớp Học Mật Ngữ - Tập 19 - Tặng Kèm 12 Bookmark Lớp Học Mật Ngữ Siêu Xinh (Mẫu Ngẫu Nhiên)', '<p><span style=\"font-size:14px\">&quot;Gieo trồng&quot; niềm vui c&ugrave;ng Lớp Học Mật Ngữ tập 19, tặng k&egrave;m phụ kiện mới cực hấp dẫn!</span></p>\r\n\r\n<p><span style=\"font-size:14px\">HHT - Khi đang &ldquo;n&aacute; thở&rdquo; với đống b&agrave;i vở, h&atilde;y thư gi&atilde;n một ch&uacute;t v&agrave; &ldquo;gieo trồng niềm vui&rdquo; với cuốn truyện tranh mới keng Lớp Học Mật Ngữ tập 19 nh&eacute;!</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Hạt giống bạn b&egrave; trong &ldquo;Khu rừng kim cương&rdquo;</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">R&uacute;t kinh nghiệm năm rồi c&aacute;c bạn bận kh&ocirc;ng dự sinh nhật được, năm nay Xử Nữ ph&aacute;t thiệp mời sớm để lớp Ho&agrave;ng Đạo kịp thu xếp. Nhưng thật ra, kh&ocirc;ng c&oacute; ai bận hết. C&aacute;c bạn chỉ&hellip; trốn th&ocirc;i! V&igrave; Xử Nữ l&agrave;m sinh nhật rất l&agrave; khắt khe, khiến c&aacute;c bạn ngộp thở. M&agrave; c&oacute; c&acirc;u &ldquo;Sống trong bể ngọc kim cương kh&ocirc;ng bằng sống giữa t&igrave;nh thương bạn b&egrave;&rdquo;, Sư Tử quyết định rủ cả lớp đi chơi Khu Rừng Kim Cương, tr&ograve; chơi đang&nbsp;<em>hot</em>&nbsp;ở c&ocirc;ng vi&ecirc;n Ng&acirc;n H&agrave;, vừa mừng sinh nhật Xử Nữ, vừa gi&uacute;p c&aacute;c bạn được thoải m&aacute;i. Nhưng mừng đ&acirc;u thấy, chỉ thấy Xử Nữ tức muốn x&igrave; kh&oacute;i... Mời bạn c&ugrave;ng đến với khu rừng kim cương v&agrave; dự sinh nhật Xử Nữ ở tập 19 n&agrave;y nha!</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Hạt giống may mắn ở &ldquo;Si&ecirc;u Th&uacute; Ng&acirc;n H&agrave;&rdquo;</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi tiết lộ to&agrave;n bộ thảm hoạ cho c&aacute;c bạn, Hội Giả Kim tan biến v&igrave; kh&ocirc;ng c&ograve;n sức lực. Hai lớp Ho&agrave;ng Đạo v&agrave; Ho&agrave;ng Cung tiến h&agrave;nh mở cổng kh&ocirc;ng gian để thu phục si&ecirc;u th&uacute;. Nhưng c&aacute;c cổng n&agrave;y đều cần nạp đ&ugrave;i g&agrave; để d&ograve; si&ecirc;u th&uacute;, m&agrave; cả hai lớp chỉ c&oacute; 12 c&aacute;i đ&ugrave;i g&agrave; - v&ocirc; c&ugrave;ng hạn hẹp! Ma Kết quyết định c&aacute;c bạn sẽ phải &ldquo;solo&rdquo; với si&ecirc;u th&uacute;. Một th&acirc;n một m&igrave;nh đương đầu thử th&aacute;ch. Trong khi đ&oacute;, Song Ngư ở nh&agrave; ph&aacute;t hiện si&ecirc;u th&uacute; đ&atilde; tr&agrave;n xuống Ng&acirc;n H&agrave;... Trong t&igrave;nh huống v&ocirc; c&ugrave;ng cấp b&aacute;ch, mọi người đ&atilde; l&agrave;m g&igrave;, đ&oacute;n xem, đ&oacute;n xem!</span></p>\r\n', 40000, 'yes', 10, 'deep-finance-9781637350270_hrTQwSNgf1.jpg', '2022-08-06 02:37:07', 'Quang Nguyễn', NULL, NULL, 'active', 21, 35),
(44, '365 Chuyện Kể Trước Giờ Đi Ngủ - Những Câu Chuyện Phát Triển Chỉ Số IQ', '<p><span style=\"font-size:14px\">Bộ 365 chuyện kể trước giờ đi ngủ gồm hai tập:&nbsp;Những c&acirc;u chuyện ph&aacute;t triển chỉ sổ th&ocirc;ng minh IQ v&agrave; Những c&acirc;u chuyện ph&aacute;t triển chỉ số t&igrave;nh cảm EQ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mỗi buổi tối trước giờ đi ngủ, c&aacute;c bậc cha mẹ đừng qu&ecirc;n đọc truyện cho con em m&igrave;nh. Những c&acirc;u chuyện c&oacute; nội dung hay, nh&acirc;n vật lại v&ocirc; c&ugrave;ng gần gũi, cuối mỗi truyện lại c&oacute; mục. &ldquo;B&agrave;i học nhỏ&rdquo; - nhẹ nh&agrave;ng m&agrave; s&acirc;u sắc chắc chắn sẽ gi&uacute;p c&aacute;c em trở n&ecirc;n th&ocirc;ng minh hơn, lương thiện hơn, biết quan t&acirc;m, y&ecirc;u thương mọi người, y&ecirc;u cuộc sống v&agrave; c&oacute; sức s&aacute;ng tạo. Mỗi ng&agrave;y cha mẹ c&ugrave;ng trẻ đọc một c&acirc;u chuyện, những điều tiếp thu được sẽ l&agrave; t&agrave;i sản tinh thần to lớn gi&uacute;p &iacute;ch cho c&aacute;c em trong suốt cuộc đời.</span></p>\r\n', 45000, 'yes', 13, '8935212341141PhOY2NqJ.jpg', '2022-08-06 02:50:10', 'Quang Nguyễn', NULL, NULL, 'active', 29, 35),
(45, 'Song Ngữ Việt - Anh - Diary Of A Wimpy Kid - Nhật Ký Chú Bé Nhút Nhát', '<p><span style=\"font-size:14px\">&ldquo;Nhật k&yacute; ch&uacute; b&eacute; nh&uacute;t nh&aacute;t &ndash; T&aacute;c giả: Jeff Kinney&rdquo; &ndash; Bộ s&aacute;ch trụ vững ở vị tr&iacute; Bestseller của New York Times đến hơn 10 năm (từ năm 2007) với số lượng ti&ecirc;u thụ l&ecirc;n tới 170 triệu cuốn tr&ecirc;n to&agrave;n thế giới, xoay quanh cậu b&eacute; Greg th&ocirc;ng minh, tinh qu&aacute;i hiện đ&atilde; cực kỳ nổi tiếng với độc giả Việt Nam từ năm 2008 - nay.<br />\r\nBộ s&aacute;ch đ&atilde; trở th&agrave;nh series d&agrave;nh cho tuổi học tr&ograve; được ưa chuộng nhất thế giới, vượt qua cả bộ truyện đ&igrave;nh đ&aacute;m Harry Potter của nh&agrave; văn J. K. Rowling, đưa t&aacute;c giả Jeff Kinney trở th&agrave;nh một trong 100 người c&oacute; ảnh hưởng nhất thế giới.<br />\r\nVới mong muốn biến bộ s&aacute;ch kh&ocirc;ng chỉ l&agrave; một bộ truyện giải tr&iacute; m&agrave; trở th&agrave;nh c&ocirc;ng cụ hỗ trợ c&aacute;c độc giả, đặc biệt l&agrave; c&aacute;c độc giả nh&iacute; c&oacute; mong muốn đọc v&agrave; trau dồi vốn từ vựng, ngữ ph&aacute;p v&agrave; khả năng đọc hiểu tiếng Anh, H&agrave; Giang Books đ&atilde; in song song hai ng&ocirc;n ngữ Việt-Anh từ tập 1-11, đồng thời cung cấp th&ecirc;m c&aacute;c từ, cụm từ tiếng Anh th&ocirc;ng dụng biến h&oacute;a theo t&igrave;nh huống h&agrave;i hước trong truyện gi&uacute;p độc giả tiện tra cứu.<br />\r\nBộ truyện Song ngữ được xuất bản năm 2020 với những cập nhật về mặt thiết kế hứa hẹn vẫn sẽ l&agrave; những cuốn s&aacute;ch khiến bạn đọc thư gi&atilde;n v&agrave; quan trọng hơn l&agrave; t&iacute;ch lũy được lượng từ vựng tiếng Anh phong ph&uacute; sau khi đọc.</span></p>\r\n', 96000, 'yes', 30, 'image_195509_1_31019Fgyz93CI.jpg', '2022-08-06 02:52:08', 'Quang Nguyễn', NULL, NULL, 'active', 28, 35),
(46, 'Clean Code - Mã Sạch Và Con Đường Trở Thành Lập Trình Viên Giỏi', '<p><span style=\"font-size:14px\">Hiện nay, lập tr&igrave;nh vi&ecirc;n l&agrave; một trong những nghề nghiệp được nhiều người lựa chọn theo đuổi v&agrave; gắn b&oacute;. Đ&acirc;y l&agrave; c&ocirc;ng việc đ&ograve;i hỏi sự tỉ mỉ, nhiều c&ocirc;ng sức nhưng mang lại gi&aacute; trị cao v&agrave; một tương lai c&ocirc;ng việc hứa hẹn. L&agrave; một trong số những chuy&ecirc;n gia gi&agrave;u kinh nghiệm, đi&ecirc;u luyện v&agrave; l&agrave;nh nghề trong ng&agrave;nh, t&aacute;c giả đ&atilde; đ&uacute;c r&uacute;t từ kinh nghiệm của m&igrave;nh, viết về những t&igrave;nh huống trong thực tế, đ&ocirc;i khi c&oacute; thể tr&aacute;i ngược với l&yacute; thuyết để x&acirc;y dựng n&ecirc;n cuốn cẩm nang n&agrave;y, nhằm hỗ trợ những người đang muốn trở th&agrave;nh một lập tr&igrave;nh vi&ecirc;n chuy&ecirc;n nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuốn s&aacute;ch được nhiều lập tr&igrave;nh vi&ecirc;n đ&aacute;nh gi&aacute; l&agrave; quyển s&aacute;ch gi&aacute; trị nhất m&agrave; họ từng đọc trong sự nghiệp của m&igrave;nh. Cuốn s&aacute;ch hướng dẫn c&aacute;ch để viết những đoạn m&atilde; c&oacute; thể hoạt động tốt, cũng như truyền tải được &yacute; định của người viết n&ecirc;n ch&uacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuốn s&aacute;ch được chia th&agrave;nh ba phần lớn. Phần đầu ti&ecirc;n m&ocirc; tả c&aacute;c nguy&ecirc;n tắc, m&ocirc; h&igrave;nh v&agrave; c&aacute;ch thực h&agrave;nh viết m&atilde; sạch. Phần thứ hai gồm nhiều t&igrave;nh huống điển h&igrave;nh với mức độ phức tạp gia tang kh&ocirc;ng ngừng. Mỗi t&igrave;nh huống l&agrave; một b&agrave;i tập gi&uacute;p l&agrave;m sạch m&atilde;, chuyển đổi m&atilde; c&oacute; nhiều vấn đề th&agrave;nh m&atilde; c&oacute; &iacute;t vấn đề hơn. V&agrave; phần cuối l&agrave; tuyển tập rất nhiều những dấu hiệu của m&atilde; c&oacute; vấn đề, những t&igrave;m t&ograve;i, suy nghiệm từ thực tiễn được đ&uacute;c r&uacute;t qua c&aacute;ch t&igrave;nh huống điển h&igrave;nh.</span></p>\r\n', 350000, 'yes', 13, 'cleancode8kw07qYL.jpg', '2022-08-06 03:02:21', 'Quang Nguyễn', NULL, NULL, 'active', 30, 37),
(47, 'Dế Mèn Phiêu Lưu Ký - Tái Bản 2020', '<p><span style=\"font-size:14px\">Ấn bản minh họa m&agrave;u đặc biệt của Dế M&egrave;n phi&ecirc;u lưu k&yacute;, với phần tranh ruột được in hai m&agrave;u xanh - đen ấn tượng, gợi kh&ocirc;ng kh&iacute; đồng thoại.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ldquo;Một con dế đ&atilde; từ tay &ocirc;ng thả ra chu du thế giới t&igrave;m những điều tốt đẹp cho lo&agrave;i người. V&agrave; con dế ấy đ&atilde; mang t&ecirc;n tuổi &ocirc;ng đi c&ugrave;ng tr&ecirc;n những chặng đường phi&ecirc;u lưu đến với cộng đồng những con vật trong văn học thế giới, đến với những xứ sở thi&ecirc;n nhi&ecirc;n v&agrave; văn h&oacute;a của c&aacute;c quốc gia kh&aacute;c. Dế M&egrave;n T&ocirc; Ho&agrave;i đ&atilde; lại sinh ra T&ocirc; Ho&agrave;i Dế M&egrave;n, một nh&agrave; văn trẻ m&atilde;i kh&ocirc;ng gi&agrave; trong văn chương...&rdquo; - Nh&agrave; ph&ecirc; b&igrave;nh Phạm Xu&acirc;n Nguy&ecirc;n</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ldquo;&Ocirc;ng rất hiểu tư duy trẻ thơ, kể với ch&uacute;ng theo c&aacute;ch nghĩ của ch&uacute;ng, l&iacute; giải sự vật theo l&ocirc; g&iacute;ch của trẻ. Hơn thế, với biệt t&agrave;i mi&ecirc;u tả lo&agrave;i vật, T&ocirc; Ho&agrave;i dựng l&ecirc;n một thế giới gần gũi với trẻ thơ. Khi cần, &ocirc;ng biết đem v&agrave;o chất du k&yacute; khiến cho độc giả nhỏ tuổi vừa hồi hộp theo d&otilde;i, vừa th&iacute;ch th&uacute; kh&aacute;m ph&aacute;.&rdquo; - TS Nguyễn Đăng Điệp</span></p>\r\n', 50000, 'yes', 5, 'de-men-50k_1D54VXPng.jpg', '2022-08-06 03:06:24', 'Quang Nguyễn', NULL, NULL, 'active', 31, 35),
(48, 'Câu Chuyện Rừng Xanh - The Jungle Book', '<p><span style=\"font-size:14px\"><strong>The Jungle Book - C&acirc;u chuyện rừng xanh</strong>&nbsp;mở ra trước mắt bạn đọc khung cảnh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ với những s&oacute;ng c&acirc;y trải d&agrave;i t&iacute;t tắp tới tận ch&acirc;n trời, những c&acirc;u chuyện về thế giới động vật kỳ b&iacute; hấp dẫn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Độc giả sẽ lạc v&agrave;o h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; cuộc sống mu&ocirc;n lo&agrave;i th&ocirc;ng qua những chuyến phi&ecirc;u lưu của c&aacute;c nh&acirc;n vật đến nhiều miền đất mới lạ v&ograve;ng quanh thế giới, thỏa tr&iacute; tưởng tượng với những trang văn mi&ecirc;u tả cảnh vật đầy sống động, như cuộc rượt đuổi nghẹt thở của lũ khỉ trong khu rừng gi&agrave; trăm tuổi, những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c m&agrave; hoang t&agrave;n của Ấn Độ xưa hay thế giới biển khơi chưa ai từng biết tới.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mang th&ocirc;ng điệp &yacute; nghĩa về cuộc sống gửi gắm tới độc giả, &quot;The Jungle Book - C&acirc;u chuyện rừng xanh&quot;, hay c&ograve;n được biết tới nhiều hơn ở Việt Nam với t&ecirc;n gọi cũ Cậu b&eacute; rừng xanh, xứng đ&aacute;ng l&agrave; cuốn s&aacute;ch kinh điển d&agrave;nh cho mọi thế hệ độc giả tr&ecirc;n thế giới.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">----<br />\r\nT&aacute;c giả&nbsp;<strong>Rudyard Kipling</strong>&nbsp;(1865 - 1936) l&agrave; nh&agrave; văn, nh&agrave; thơ nổi tiếng người Anh sinh ra tại Ấn Độ. &Ocirc;ng được xem l&agrave; &quot;nh&agrave; c&aacute;ch t&acirc;n lớn cho nghệ thuật truyện ngắn&quot; v&agrave; l&agrave; t&aacute;c giả của c&aacute;c t&aacute;c phẩm văn học kinh điển d&agrave;nh cho thiếu nhi. Năm 1907, &ocirc;ng nhận giải Nobel Văn học khi mới 42 tuổi, cho đến nay &ocirc;ng vẫn l&agrave; người trẻ tuổi nhất đạt giải thưởng n&agrave;y.</span></p>\r\n', 79000, 'yes', 8, 'image_189708pOgtq4Ex.jpg', '2022-08-06 03:11:32', 'Quang Nguyễn', NULL, NULL, 'active', 34, 35),
(49, 'Tập Tô Màu Nhà Trẻ - Chủ Đề Giao Thông', '<p><span style=\"font-size:14px\">Tập T&ocirc; M&agrave;u Nh&agrave; Trẻ - Chủ Đề Giao Th&ocirc;ng<br />\r\nBộ s&aacute;ch B&eacute; tập t&ocirc; m&agrave;u nh&agrave; trẻ với nhiều chủ đề kh&aacute;c nhau, kh&ocirc;ng chỉ r&egrave;n khả năng kh&eacute;o l&eacute;o, sự linh hoạt của đ&ocirc;i tay m&agrave; c&ograve;n h&igrave;nh th&agrave;nh khả năng nhận biết ở trẻ th&ocirc;ng qua việc giới thiệu c&aacute;c sự vật, đối tượng, m&ocirc;i trường sống...<br />\r\nMỗi trang t&ocirc; m&agrave;u gồm c&oacute; 2 phần:<br />\r\n- Phần h&igrave;nh mẫu (in m&agrave;u)<br />\r\n- Phần h&igrave;nh t&ocirc; với k&iacute;ch thước lớn, đường n&eacute;t r&otilde; r&agrave;ng.<br />\r\nMỗi h&igrave;nh ảnh đều c&oacute; phần ch&uacute; th&iacute;ch từ vựng bằng tiếng Việt v&agrave; tiếng Anh, để trẻ l&agrave;m quen với việc gọi t&ecirc;n sự vật.<br />\r\nC&aacute;c bậc phụ huynh cũng n&ecirc;n hỗ trợ c&aacute;c b&eacute; c&aacute;ch thức t&ocirc; m&agrave;u: t&ocirc; từ chi tiết đơn giản đến phức tạp, c&aacute;ch chọn lựa v&agrave; s&aacute;ng tạo m&agrave;u sắc, c&aacute;ch ph&acirc;n biệt đường n&eacute;t, h&igrave;nh khối... C&ugrave;ng tham gia v&agrave; hướng dẫn b&eacute; l&agrave; một trong những phương ph&aacute;p hiệu quả tạo được sự hứng th&uacute; cho trẻ.<br />\r\nVới chủ đề giao th&ocirc;ng, cuốn s&aacute;ch giới thiệu những mẫu t&ocirc; m&agrave;u sinh động, đ&aacute;ng y&ecirc;u: đ&egrave;n giao th&ocirc;ng, &ocirc; t&ocirc;, m&aacute;y bay, t&agrave;u thủy...</span></p>\r\n', 10000, 'no', 5, '8936046599203w6kV9SUc.jpg', '2022-08-06 03:14:26', 'Quang Nguyễn', NULL, NULL, 'active', 37, 35),
(50, 'Thế Giới Động Vật Trong Rừng Nhiệt Đới', '<p><span style=\"font-size:14px\">Ch&acirc;u &Aacute;, ch&acirc;u lục phức tạp v&agrave; đa dạng nhất trong số c&aacute;c ch&acirc;u lục tr&ecirc;n thế giới về mặt địa l&yacute;, c&oacute; hệ động vật v&ocirc; c&ugrave;ng phong ph&uacute;, đa dạng v&agrave; l&agrave; nơi tr&uacute; ngụ của nhiều lo&agrave;i động vật qu&yacute; hiếm. Đ&acirc;y l&agrave; nơi c&oacute; nhiều động vật như voi ch&acirc;u &Aacute;, hổ, heo rừng v&agrave; khỉ sinh sống. Rừng nhiệt đới ở đ&acirc;y đa dạng v&agrave; l&acirc;u đời hơn cả rừng rậm Amazon. Những cuộc chiến sinh tồn nơi hoang d&atilde; hay &aacute;nh mắt sắc b&eacute;n của c&aacute;c vị ch&uacute;a tể rừng xanh sẽ mang đến cho ch&uacute;ng ta một c&aacute;i nh&igrave;n mới mẻ về cuộc sống của những lo&agrave;i động vật trong tự nhi&ecirc;n m&agrave; con người hiếm khi được tận mắt chứng kiến.<br />\r\nATLAT ĐỘNG VẬT gồm 4 cuốn:<br />\r\n1. Thế giới động vật ở vườn th&uacute; Ch&acirc;u Phi<br />\r\n2. Động vật ở Ch&acirc;u &Uacute;c<br />\r\n3. Thế giới động vật trong rừng nhiệt đới<br />\r\n4. Th&aacute;m hiểm Amazon - thế giới động vật</span></p>\r\n', 54000, 'no', 9, 'image_195509_1_51382TGe0xnw.jpg', '2022-08-06 03:16:13', 'Quang Nguyễn', NULL, NULL, 'active', 41, 35),
(51, 'Truyện Cổ Tích Việt Nam Dành Cho Thiếu Nhi - Thánh Gióng', '<p><span style=\"font-size:14px\">Truyện Cổ T&iacute;ch Việt Nam D&agrave;nh Cho Thiếu Nhi - Th&aacute;nh Gi&oacute;ng Việt Nam c&oacute; một kho t&agrave;ng truyện cổ t&iacute;ch v&ocirc; c&ugrave;ng đa dạng v&agrave; phong ph&uacute;. Mỗi truyện cổ t&iacute;ch kh&ocirc;ng đơn thuần mang gi&aacute; trị giải tr&iacute; m&agrave; ẩn chứa trong đ&oacute; l&agrave; những b&agrave;i học bổ &iacute;ch mang t&iacute;nh gi&aacute;o dục cao. </span></p>\r\n\r\n<p><span style=\"font-size:14px\">Do đ&oacute;, hầu hết c&aacute;c b&eacute; thiếu nhi đều được &ocirc;ng b&agrave;, cha mẹ, thầy c&ocirc; kể cho nghe những c&acirc;u chuyện cổ t&iacute;ch. Đ&acirc;y l&agrave; tập truyện tranh in m&agrave;u d&agrave;nh cho thiếu nhi kể về c&acirc;u chuyện cổ t&iacute;ch quen thuộc nhất : Th&aacute;nh Gi&oacute;ng. Nội dung truyện cổ t&iacute;ch được t&oacute;m tắt ngắn gọn, k&egrave;m theo đ&oacute; l&agrave; h&igrave;nh ảnh minh hoạ sinh động c&ugrave;ng m&agrave;u sắc tươi s&aacute;ng thu h&uacute;t sự ch&uacute; &yacute; của c&aacute;c độc giả nh&iacute;. C&aacute;c qu&yacute; phụ huynh c&oacute; thể đọc cho c&aacute;c b&eacute; nghe hoặc đọc c&ugrave;ng với b&eacute;.</span></p>\r\n', 10000, 'yes', 32, '8936046598671_1dOi21tkg.jpg', '2022-08-06 03:17:05', 'Quang Nguyễn', NULL, NULL, 'active', 42, 35),
(52, 'Truyện Cổ Tích Việt Nam Dành Cho Thiếu Nhi - Sự Tích Trầu Cau', '<p><span style=\"font-size:14px\">Truyện Cổ T&iacute;ch Việt Nam D&agrave;nh Cho Thiếu Nhi - Sự T&iacute;ch Trầu Cau Việt Nam c&oacute; một kho t&agrave;ng truyện cổ t&iacute;ch v&ocirc; c&ugrave;ng đa dạng v&agrave; phong ph&uacute;. Mỗi truyện cổ t&iacute;ch kh&ocirc;ng đơn thuần mang gi&aacute; trị giải tr&iacute; m&agrave; ẩn chứa trong đ&oacute; l&agrave; những b&agrave;i học bổ &iacute;ch mang t&iacute;nh gi&aacute;o dục cao. </span></p>\r\n\r\n<p><span style=\"font-size:14px\">Do đ&oacute;, hầu hết c&aacute;c b&eacute; thiếu nhi đều được &ocirc;ng b&agrave;, cha mẹ, thầy c&ocirc; kể cho nghe những c&acirc;u chuyện cổ t&iacute;ch. Đ&acirc;y l&agrave; tập truyện tranh in m&agrave;u d&agrave;nh cho thiếu nhi kể về c&acirc;u chuyện cổ t&iacute;ch quen thuộc nhất : Sự T&iacute;ch Trầu Cau. Nội dung truyện cổ t&iacute;ch được t&oacute;m tắt ngắn gọn, k&egrave;m theo đ&oacute; l&agrave; h&igrave;nh ảnh minh hoạ sinh động c&ugrave;ng m&agrave;u sắc tươi s&aacute;ng thu h&uacute;t sự ch&uacute; &yacute; của c&aacute;c độc giả nh&iacute;. C&aacute;c qu&yacute; phụ huynh c&oacute; thể đọc cho c&aacute;c b&eacute; nghe hoặc đọc c&ugrave;ng với b&eacute;.</span></p>\r\n', 10000, 'no', 6, '8936046598695Mer3FOGU.jpg', '2022-08-06 03:19:16', 'Quang Nguyễn', NULL, NULL, 'active', 45, 35),
(53, 'Cẩm Nang Cấu Trúc Tiếng Anh', '<p><span style=\"font-size:14px\">Cuốn s&aacute;ch&nbsp;<strong>CẨM NANG CẤU TR&Uacute;C TIẾNG ANH</strong>&nbsp;gồm 25 phần, mỗi phần l&agrave; một phạm tr&ugrave; kiến thức trong tiếng Anh được tr&igrave;nh b&agrave;y một c&aacute;ch ngắn gọn, đơn giản, c&ocirc; đọng v&agrave; hệ thống ho&aacute; dưới dạng sơ đồ, bảng biểu nhằm ph&aacute;t triển khả năng tư duy của người học v&agrave; từ đ&oacute; gi&uacute;p người học nhớ kiến thức nhanh hơn v&agrave; s&acirc;u hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau hầu hết c&aacute;c phần l&iacute; thuyết đều c&oacute; 20-30 c&acirc;u b&agrave;i tập &aacute;p dụng để kiểm tra cũng như khắc s&acirc;u kiến thức cho người học.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tuy d&agrave;y chưa đến 250 trang nhưng cuốn s&aacute;ch lại c&oacute; thể bao trọn to&agrave;n bộ kiến thức từ đơn giản đến phức tạp cộng với c&aacute;ch tận dụng tối đa v&agrave; &aacute;p dụng triệt để c&aacute;ch học tiếng Anh bằng sơ đồ tư duy.</span></p>\r\n', 98000, 'yes', 30, 'image_187866MAn9rloC.jpg', '2022-08-06 03:23:27', 'Quang Nguyễn', NULL, NULL, 'active', 46, 36),
(54, 'Động Từ Bất Quy Tắc & Ngữ Pháp Tiếng Anh Căn Bản', '<p><span style=\"font-size:14px\">Động Từ Bất Quy Tắc &amp; Ngữ Ph&aacute;p Tiếng Anh Căn Bản</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Việc học một thứ ng&ocirc;n ngữ kh&aacute;c kh&ocirc;ng phải tiếng mẹ đẻ, bao giờ cũng đem lại cho bạn nhiều kh&oacute; khăn, v&igrave; thế &nbsp;để học tốt tiếng Anh hay bất k&igrave; thứ tiếng n&agrave;o kh&aacute;c đ&ograve;i hỏi bạn phải c&oacute; sự ki&ecirc;n tr&igrave; v&agrave; t&agrave;i liệu học tập tốt.<br />\r\n<br />\r\nĐộng Từ Bất Quy Tắc &amp; Ngữ Ph&aacute;p Tiếng Anh Căn Bản&nbsp;l&agrave; t&agrave;i liệu học Tiếng Anh căn bản d&agrave;nh cho người mới bắt đầu học tiếng Anh.<br />\r\n<br />\r\nCuốn s&aacute;ch gồm hai phần:<br />\r\n<br />\r\nPhần I: &nbsp;Động từ bất quy tắc gồm bảng động từ bất quy tắc được chia theo 4: dạng hiện tại, qu&aacute; khứ, qu&aacute; khứ ph&acirc;n từ, hiện tại ph&acirc;n từ c&oacute; k&egrave;m nghĩa của từ.<br />\r\n<br />\r\nPhần II: Những kiến thức Anh văn cơ bản giới thiệu kiến thức về c&aacute;c thể c&acirc;u.</span></p>\r\n', 15000, 'yes', 35, 'e35dc1524f1b082b13b2913ce8878c76pBtGosMy.jpg', '2022-08-06 03:25:20', 'Quang Nguyễn', '2022-08-06 03:46:22', 'Quang Nguyễn', 'active', 47, 36),
(55, 'Mindmap English Grammar - Ngữ Pháp Tiếng Anh Bằng Sơ Đồ Tư Duy', '<p><span style=\"font-size:14px\">Trước đ&acirc;y, khi tiếng Anh chưa l&agrave; ng&ocirc;n ngữ quốc tế trong m&ocirc;i trường to&agrave;n cầu h&oacute;a, xu hướng việc giảng dạy v&agrave; học tập tiếng Anh tại Việt Nam thi&ecirc;n về hai kỹ năng ĐỌC HIỂU v&agrave; VIẾT. Hệ quả l&agrave; trong c&aacute;c thế hệ trước, nhiều người đọc hiểu v&agrave; viết tiếng Anh th&ocirc;ng thạo kh&ocirc;ng k&eacute;m người bản xứ được học h&agrave;nh tử tế; ngược lại, gặp kh&oacute; khăn khi giao tiếp nghe - n&oacute;i bằng tiếng Anh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Từ thập ni&ecirc;n 90 thế kỷ trước đ&acirc;y, giao tiếp ng&ocirc;n ngữ bao gồm bốn kỹ năng cơ bản: NGHE &ndash; N&Oacute;I - ĐỌC - VIẾT. Do vậy khi n&oacute;i đến tiếng Anh giao tiếp c&oacute; nghĩa l&agrave; cần phải giao tiếp được cả bốn kỹ năng tr&ecirc;n. Phương ph&aacute;p giao tiếp dựa tr&ecirc;n sự ph&aacute;t triển kỹ năng ng&ocirc;n ngữ của trẻ em: giai đoạn đầu tập trung v&agrave;o ph&aacute;t triển NGHE - N&Oacute;I; giai đoạn ph&aacute;t triển tập trung ĐỌC VIẾT. N&oacute;i như vậy kh&ocirc;ng c&oacute; nghĩa l&agrave; khi dạy kỹ năng nghe n&oacute;i th&igrave; kh&ocirc;ng c&oacute; y&ecirc;u cầu ph&aacute;t triển kỹ năng đọc viết v&agrave; ngược lại. Đối với m&ocirc;i trường giao lưu ng&ocirc;n ngữ v&agrave; kinh tế to&agrave;n cầu v&agrave; ph&aacute;t triển khoa học ng&agrave;y nay, kỹ năng dịch thuật v&agrave; phi&ecirc;n dịch cũng nằm trong giao tiếp ng&ocirc;n ngữ bậc cao trong m&ocirc;i trường to&agrave;n cầu h&oacute;a. Vai tr&ograve; của ngữ ph&aacute;p trong ph&aacute;t triển ng&ocirc;n ngữ lu&ocirc;n l&agrave; nền tảng để học v&agrave; ph&aacute;t triển một ng&ocirc;n ngữ. Để ph&aacute;t triển một ng&ocirc;n ngữ, d&ugrave; đ&oacute; l&agrave; ngoại ngữ như tiếng Anh, nền tảng của ng&ocirc;n ngữ l&agrave; ngữ ph&aacute;p, từ vựng v&agrave; ph&aacute;t &acirc;m. Do vậy, vai tr&ograve; ngữ ph&aacute;p l&agrave; để ph&aacute;t triển ng&ocirc;n ngữ đ&uacute;ng v&agrave; đủ để ph&aacute;t triển kỹ năng giao tiếp; ngữ ph&aacute;p kh&ocirc;ng chỉ để hiểu về ng&ocirc;n ngữ m&agrave; để sử dụng đ&uacute;ng ng&ocirc;n ngữ (not knowing about the language, but knowing the language). Điều n&agrave;y cho thấy học ngữ ph&aacute;p tiếng Anh ở Việt Nam l&agrave; đ&uacute;ng v&igrave; tiếng Anh l&agrave; EFL (ngoại ngữ), kh&ocirc;ng phải ESL (ng&ocirc;n ngữ thứ hai), tuy nhi&ecirc;n sự yếu k&eacute;m trong việc học v&agrave; dạy ngữ ph&aacute;p l&agrave; để l&agrave;m b&agrave;i tập, sau đ&oacute; l&agrave; b&agrave;i thi m&agrave; kh&ocirc;ng d&ugrave;ng ngữ ph&aacute;p trong c&aacute;c hoạt động ng&ocirc;n ngữ (activities) v&agrave; thực h&agrave;nh ng&ocirc;n ngữ (tasks). Nhằm g&oacute;p phần hỗ trợ học ngữ ph&aacute;p, MIND MAP ENGLISH GRAMMAR l&agrave; c&ocirc;ng cụ để hiểu biết tiếng Anh dưới h&igrave;nh thức t&oacute;m lược theo tư duy logic. Do vậy người học cần sử dụng hiểu biết về ngữ ph&aacute;p v&agrave; quan trọng hơn l&agrave; ứng dụng v&agrave;o nhiều hoạt động thực h&agrave;nh để ph&aacute;t huy c&aacute;c chủ đề ngữ ph&aacute;p trong cuốn s&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Ngữ Ph&aacute;p Tiếng Anh Bằng Sơ Đồ Tư Duy c&oacute; những đặc điểm sau đ&acirc;y:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Giảng vi&ecirc;n c&oacute; thể hướng dẫn cho người học c&aacute;c chủ đề v&agrave; chủ điểm ngữ ph&aacute;p theo tư duy logic qua c&aacute;c sơ đồ tư duy t&oacute;m tắt dễ nhớ v&agrave; dễ hiểu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Người học dễ d&agrave;ng tự học v&agrave; tham khảo trước khi l&agrave;m c&aacute;c b&agrave;i tập ngữ ph&aacute;p hoặc sử dụng tiếng Anh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Hệ thống b&agrave;i học được tr&igrave;nh b&agrave;y dạng &quot;mind map&quot; c&aacute;ch t&acirc;n từ thấp l&ecirc;n cao n&ecirc;n người học y&ecirc;u th&iacute;ch ngữ ph&aacute;p tiếng Anh hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Sau khi học, hiểu, nắm vững c&aacute;c chủ đề, chủ điểm ngữ ph&aacute;p, người học c&oacute; thể tự vẽ lấy &quot;mind map&quot; của ri&ecirc;ng m&igrave;nh.</span></p>\r\n', 190000, 'yes', 32, '8935246917176E7SC1tgO.jpg', '2022-08-06 03:27:03', 'Quang Nguyễn', NULL, NULL, 'active', 48, 36),
(56, 'Giáo Trình Hán Ngữ 1 - Quyển Thượng Phiên Bản Mới', '<p><span style=\"font-size:14px\"><strong>Gi&aacute;o tr&igrave;nh H&aacute;n ngữ tập 1 phi&ecirc;n bản mới tải app &ndash;&nbsp;Cuốn gi&aacute;o tr&igrave;nh H&aacute;n ngữ phi&ecirc;n bản mới duy nhất tại Việt Nam</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&ldquo;Gi&aacute;o tr&igrave;nh H&aacute;n ngữ phi&ecirc;n bản mới tập 1&rdquo;</strong>&nbsp;l&agrave; cuốn đầu ti&ecirc;n trong bộ, nằm ở tr&igrave;nh độ Sơ cấp d&agrave;nh cho người bắt đầu tiếng Trung.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Trong qu&aacute; tr&igrave;nh học những kh&oacute; khăn tr&ecirc;n c&oacute; đang ngăn cản bạn chinh phục ng&ocirc;n ngữ n&agrave;y kh&ocirc;ng?</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bạn l&agrave; người mới bắt đầu học tiếng Trung, bạn kh&ocirc;ng biết n&ecirc;n tự học như thế n&agrave;o</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trong khi tiếng Việt v&agrave; tiếng Anh được viết bằng chữ c&aacute;i Latinh th&igrave; tiếng Trung được viết bằng chữ tượng h&igrave;nh n&ecirc;n rất kh&oacute; khi bạn tự t&igrave;m hiểu ng&ocirc;n ngữ n&agrave;y. Bạn l&agrave; người mới bắt đầu học tiếng Trung, bạn muốn tự học nhưng kh&ocirc;ng biết n&ecirc;n học những kiến thức n&agrave;o, đ&acirc;u l&agrave; những kiến thức nền tảng ph&ugrave; hợp với tr&igrave;nh độ của những người mới học. V&agrave; quan trọng nhất l&agrave; bạn kh&ocirc;ng biết được n&ecirc;n t&igrave;m học từ những t&agrave;i liệu n&agrave;o. Tr&ecirc;n thị trường t&agrave;i liệu học tiếng Trung th&igrave; nhiều nhưng t&agrave;i liệu vừa b&aacute;m s&aacute;t chương tr&igrave;nh dạy học, vừa cập nhật những điểm đổi mới c&ugrave;ng như được bi&ecirc;n soạn dễ hiểu th&igrave; lại rất &iacute;t.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Ngữ ph&aacute;p v&agrave; vốn từ của bạn bị hạn chế</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngữ ph&aacute;p v&agrave; từ vựng l&agrave; mấu chốt quan trọng nhất trong việc học một ng&ocirc;n ngữ. Nắm chắc ngữ ph&aacute;p v&agrave; từ vựng việc học tập để vượt qua c&aacute;c b&agrave;i kiểm tra hay học giao tiếp đều trở n&ecirc;n dễ d&agrave;ng hơn. Bạn l&agrave; người mới học, tr&igrave;nh độ ngữ ph&aacute;p v&agrave; từ vựng của bạn đang ở đ&acirc;u? Muốn n&acirc;ng cao tr&igrave;nh độ nhanh nhất bạn phải l&agrave;m thế n&agrave;o? Đ&acirc;u l&agrave; c&acirc;u trả lời d&agrave;nh cho bạn?</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bạn &ldquo;loay hoay&rdquo; kh&ocirc;ng biết l&agrave;m thế n&agrave;o để ph&aacute;t triển đồng thời cả 4 kỹ năng Nghe &ndash; n&oacute;i &ndash; đọc &ndash; viết</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đối với rất nhiều bạn việc đọc, viết c&aacute;c văn bản tiếng Trung&nbsp;<em>&ldquo;dễ như ăn ch&aacute;o&rdquo;&nbsp;</em>nhưng khi gặp người n&oacute;i tiếng H&aacute;n th&igrave; lại<em>&ldquo;&aacute; khẩu&rdquo;,&nbsp;</em>kh&ocirc;ng nghe được đối phương n&oacute;i g&igrave;, cũng kh&ocirc;ng biết đ&aacute;p trả ra sao. Học tiếng Trung ai cũng muốn n&acirc;ng cao cả 4 kỹ năng Nghe &ndash; n&oacute;i &ndash; đọc &ndash; viết nhưng kh&ocirc;ng phải ai cũng tự t&igrave;m được giải ph&aacute;p cho bản th&acirc;n. Vậy l&agrave;m thế n&agrave;o, bằng c&aacute;ch n&agrave;o bạn c&oacute; thể ph&aacute;t triển đồng đều 4 kỹ năng n&agrave;y?</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bạn mới bắt đầu học tiếng H&aacute;n nhưng những kh&oacute; khăn n&agrave;y lại l&agrave;m bạn &ldquo;ch&ugrave;n bước&rdquo;, ch&aacute;n nản ngay từ đầu.&nbsp;Để c&oacute; thể duy tr&igrave; v&agrave; trở n&ecirc;n th&agrave;nh thạo tiếng Trung đ&ograve;i hỏi bạn phải c&oacute; sự ki&ecirc;n tr&igrave; v&agrave; quyết t&acirc;m đủ lớn để chinh phục m&ocirc;n ngoại ngữ n&agrave;y. Trong đ&oacute; những bước đi đầu ti&ecirc;n lu&ocirc;n l&agrave; quan trọng nhất, giải quyết được những kh&oacute; khăn n&agrave;y sẽ gi&uacute;p bạn tự tin tiếp tục n&acirc;ng cao tr&igrave;nh độ. C&ocirc;ng cụ gi&uacute;p c&aacute;c bạn l&agrave;m điều đ&oacute; chỉ c&oacute; thể l&agrave;&nbsp;&ldquo;Gi&aacute;o tr&igrave;nh H&aacute;n ngữ tập 1 phi&ecirc;n bản mới&rdquo;.</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>V&igrave; sao cuốn s&aacute;ch n&agrave;y lại cần thiết với những bạn mới bắt đầu học tiếng Trung?</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Tr&igrave;nh b&agrave;y khoa học ph&ugrave; hợp với c&aacute;c bạn tự nghi&ecirc;n cứu</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với những bạn đang tự học tiếng Trung hoặc tự &ocirc;n luyện sau những giờ học tr&ecirc;n lớp th&igrave; cuốn s&aacute;ch n&agrave;y ho&agrave;n to&agrave;n ph&ugrave; hợp. C&aacute;c b&agrave;i học được bi&ecirc;n soạn theo chương tr&igrave;nh thống nhất, ph&ugrave; hợp với tr&igrave;nh độ Sơ cấp, c&aacute;c kiến thức đều ở mức cơ bản nhất. Đ&acirc;y l&agrave; bước đầu ti&ecirc;n của người học tiếng Trung, n&ecirc;n việc tạo sự th&uacute; vị trong b&agrave;i học để người học quan trọng hơn kiến thức. Tập 1 gồm 15 b&agrave;i học với c&aacute;c chủ đề đa dạng. Mỗi b&agrave;i học gồm 5 phần:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&agrave;i kh&oacute;a<br />\r\nTừ mới<br />\r\nCh&uacute; th&iacute;ch<br />\r\nNgữ ph&aacute;p, ngữ &acirc;m<br />\r\nLuyện tập</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Th&ocirc;ng qua mỗi b&agrave;i học c&aacute;c bạn được học đầy đủ c&aacute;c kiến thức, bắt đầu mỗi b&agrave;i c&aacute;c bạn n&ecirc;n tự học từ phần một đến phần 5, kh&ocirc;ng n&ecirc;n nhảy c&oacute;c qua c&aacute;c phần để đạt được hiệu quả cao nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Hệ thống ngữ ph&aacute;p v&agrave; từ mới theo từng b&agrave;i</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&agrave;i liệu cung cấp c&aacute;c hệ thống ngữ ph&aacute;p v&agrave; từ vựng tương ứng với tr&igrave;nh độ sơ cấp. Hệ thống từ vựng b&aacute;m s&aacute;t theo từng chủ đề, mỗi b&agrave;i c&aacute;c bạn được học từ 10 &ndash; 20 từ vựng li&ecirc;n quan đến chủ đề. Ngo&agrave;i ra ở cuối s&aacute;ch c&ograve;n c&oacute; bảng từ vựng cung cấp gần 260 từ vựng đủ cho những người mới học.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Về mặt ngữ ph&aacute;p trong tập 1, từ b&agrave;i 10 đến b&agrave;i 15 sẽ gi&uacute;p người học hiểu r&otilde; những cấu tr&uacute;c ngữ ph&aacute;p cơ bản. C&aacute;c cấu tr&uacute;c lu&ocirc;n c&oacute; v&iacute; dụ đi k&egrave;m, đ&acirc;y đều l&agrave; những v&iacute; dụ minh họa gần gũi, dễ hiểu nhất với những người mới bắt đầu. Với hệ thống ngữ ph&aacute;p v&agrave; từ vựng trong tập 1 người học đ&atilde; c&oacute; thể tự tin chinh phục kỳ thi HSK cấp độ 1.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Gi&uacute;p người học ph&aacute;t triển đồng thời cả 4 kỹ năng</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Từ b&agrave;i 1 đến b&agrave;i 5, s&aacute;ch hệ thống hết c&aacute;c phi&ecirc;n &acirc;m. Học xong 5 b&agrave;i n&agrave;y c&aacute;c bạn đ&atilde; c&oacute; thể đọc hết pinyin của chữ H&aacute;n. Trong 5 b&agrave;i n&agrave;y c&ograve;n dạy người học c&aacute;ch ph&aacute;t &acirc;m tiếng H&aacute;n đ&uacute;ng chuẩn người bản địa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Gi&aacute;o tr&igrave;nh được bi&ecirc;n soạn theo hướng mẫu c&acirc;u để trực tiếp đi v&agrave;o thực h&agrave;nh giao tiếp. Phần b&agrave;i kh&oacute;a v&agrave; nội dung đ&agrave;m thoại c&oacute; quan hệ chủ điểm đồng nhất với b&agrave;i luyện đọc trong phần b&agrave;i tập. Phần b&agrave;i tập cũng ch&uacute; &yacute; tới r&egrave;n luyện kỹ năng giao tiếp, m&ocirc; phỏng v&agrave; bồi dưỡng tr&iacute; nhớ cho sinh vi&ecirc;n. Ngo&agrave;i ra c&aacute;c b&agrave;i nghe đều được c&aacute;c giảng vi&ecirc;n của Đại học Ng&ocirc;n ngữ Bắc Kinh thực hiện, qua đ&oacute; c&aacute;c bạn n&acirc;ng cao được kỹ năng nghe một c&aacute;ch nhanh nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi ho&agrave;n th&agrave;nh tr&igrave;nh độ n&agrave;y người học sẽ c&oacute; khả năng nghe &ndash; n&oacute;i &ndash; đọc &ndash; viết đều ở mức cơ bản, v&agrave; c&oacute; thể sử dụng được trong những t&igrave;nh huống nhất định.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Người học r&egrave;n được kỹ năng viết chữ</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ở cuối mỗi b&agrave;i học sẽ c&oacute; c&aacute;c bảng từ được kẻ &ocirc; sẵn gi&uacute;p c&aacute;c bạn luyện tập viết chữ H&aacute;n đ&uacute;ng chuẩn. Những chữ kh&oacute;, s&aacute;ch đ&atilde; c&oacute; phần kẻ mờ, người học chỉ cần viết lại theo c&aacute;c n&eacute;t đ&atilde; được vạch ra để học được c&aacute;ch viết c&aacute;c n&eacute;t ch&iacute;nh x&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Dịch bằng 2 thứ tiếng</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuốn s&aacute;ch c&oacute; phi&ecirc;n &acirc;m pinyin gi&uacute;p c&aacute;c bạn đọc v&agrave; hiểu được tiếng Trung. B&ecirc;n cạnh đ&oacute; s&aacute;ch c&ograve;n dịch sang cả tiếng Anh v&agrave; tiếng Việt gi&uacute;p c&aacute;c bạn tăng th&ecirc;m hiểu biết về nhiều ng&ocirc;n ngữ c&ugrave;ng l&uacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Tăng th&ecirc;m sự hiểu biết về đất nước Trung Hoa</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chủ điểm b&agrave;i kh&oacute;a bao gồm nhiều lĩnh vực trong đời sống h&agrave;ng ng&agrave;y, giới thiệu những kiến thức về con người v&agrave; đất nước Trung Quốc nhằm tăng sự hiểu biết về đất nước n&agrave;y. C&aacute;c phần ch&uacute; th&iacute;ch chi tiết cũng n&ecirc;u r&otilde; những kiến thức về ng&ocirc;n ngữ v&agrave; bối cảnh văn h&oacute;a Trung Hoa. Từ đ&oacute; c&aacute;c bạn kh&ocirc;ng chỉ học được tiếng Trung m&agrave; c&ograve;n n&acirc;ng cao sự hiểu biết của m&igrave;nh về Trung Quốc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Học tiếng Trung qua ứng dụng tr&ecirc;n điện thoại của MCBooks</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Để tiện lợi hơn trong qu&aacute; tr&igrave;nh học tập, gi&uacute;p bạn tận dụng mọi khoảng thời gian trống để &ocirc;n lại c&aacute;c b&agrave;i nghe, với gi&aacute;o tr&igrave;nh H&aacute;n ngữ 1 phi&ecirc;n bản mới tải APP, chỉ cần tải app của MCBooks qu&eacute;t m&atilde; QR code ở sau cuốn s&aacute;ch. Bạn c&oacute; thể sở hữu cuốn s&aacute;ch ngay trong điện thoại của m&igrave;nh. Hệ thống hỗ trợ tr&ecirc;n cả hai hệ điều h&agrave;nh Android v&agrave; IOS. H&atilde;y thưởng thức c&ocirc;ng nghệ trong một cuốn s&aacute;ch tuyệt vời. Chắc chắn cuốn s&aacute;ch n&agrave;y sẽ l&agrave; bạn đồng h&agrave;nh kh&ocirc;ng thể thiếu tr&ecirc;n chặng đường học tiếng Trung của bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuốn s&aacute;ch n&agrave;y do MCBooks &ndash; chuy&ecirc;n s&aacute;ch ngoại ngữ &ndash; độc quyền ph&aacute;t h&agrave;nh n&ecirc;n chắc chắn người học sẽ kh&ocirc;ng phải lo lắng về chất lượng cuốn s&aacute;ch. Gi&aacute;o tr&igrave;nh được in r&otilde; n&eacute;t tr&ecirc;n chất liệu giấy cực tốt kh&ocirc;ng hề k&eacute;m cạnh thậm ch&iacute; c&ograve;n nhỉnh hơn c&aacute;c cuốn s&aacute;ch gi&aacute;o khoa tr&ecirc;n thị trường hiện nay. Với cuốn s&aacute;ch n&agrave;y c&aacute;c bạn vừa c&oacute; thể tự tin để vượt qua HSK 1, vừa tự tin giao tiếp tiếng Trung trong những t&igrave;nh huống hội thoại h&agrave;ng ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xin n&oacute;i th&ecirc;m, hiện nay tr&ecirc;n thị trường c&oacute; rất nhiều đối tượng sử dụng c&aacute;c sản phẩm nh&aacute;i, photocopy hoặc kh&ocirc;ng phải của MCBooks, đ&acirc;y l&agrave; c&aacute;c h&agrave;nh động vị phạm bản quyền. Để được bảo vệ quyền lợi, người học kh&ocirc;ng n&ecirc;n mua c&aacute;c cuốn s&aacute;ch n&agrave;y. H&atilde;y sử dụng đ&uacute;ng s&aacute;ch của MCBooks, v&igrave; một thế giới kh&ocirc;ng c&oacute; s&aacute;ch giả !</span></p>\r\n', 89000, 'yes', 20, 'hanngu1BPIEqag3.jpg', '2022-08-06 03:29:46', 'Quang Nguyễn', NULL, NULL, 'active', 50, 36),
(57, 'Tiếng Nhật Cho Mọi Người - Sơ Cấp 1 - Bản Dịch Và Giải Thích Ngữ Pháp - Tiếng Việt (Bản Mới)', '<p><span style=\"font-size:14px\">Gi&aacute;o tr&igrave;nh dạy tiếng Nhật Minna no Nihongo bộ mới</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ldquo;Minna no Nihongo&rdquo; l&agrave; bộ gi&aacute;o tr&igrave;nh sơ cấp d&agrave;nh cho những ai muốn t&igrave;m hiểu về tiếng Nhật rất th&ocirc;ng dụng tại Ch&acirc;u &Aacute; v&agrave; Việt Nam.S&aacute;ch gồm 2 tập với 50 b&agrave;i học, mỗi b&agrave;i bao gồm phần Mẫu c&acirc;u, V&iacute; dụ, Hội thoại, Luyện tập v&agrave; c&aacute;c c&acirc;u hỏi đặt ra để trả lời.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bộ gi&aacute;o tr&igrave;nh n&agrave;y ph&ugrave; hợp với nhiều đối tượng: từ học sinh, sinh vi&ecirc;n, v&agrave; cả những ai muốn nắm chắc kiến thức cơ bản về tiếng Nhật để học tập v&agrave; l&agrave;m việc trong m&ocirc;i trường sử dụng tiếng Nhật. NXB Trẻ được ch&iacute;nh thức độc quyền xuất bản bộ gi&aacute;o tr&igrave;nh &ldquo;Minna no Nihongo&rdquo; tại Việt Nam.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">S&aacute;ch được xuất bản với Bản tiếng Nhật v&agrave; Bản dịch v&agrave; giải th&iacute;ch ngữ ph&aacute;p, nhằm đ&aacute;p ứng tốt nhất nhu cầu học v&agrave; dạy tiếng Nhật tại Việt Nam.</span></p>\r\n', 75000, 'yes', 8, '86e1813381e4884119d183bd13592e9aO6Fwoc2i.jpg', '2022-08-06 03:30:55', 'Quang Nguyễn', '2022-08-06 03:44:23', 'Quang Nguyễn', 'active', 52, 36);
INSERT INTO `book` (`id`, `name`, `description`, `price`, `special`, `sale_off`, `picture`, `created`, `created_by`, `modified`, `modified_by`, `status`, `ordering`, `category_id`) VALUES
(58, 'Hackers Ielts: Speaking', '<p><span style=\"font-size:14px\">Bộ s&aacute;ch luyện thi IELTS đầu ti&ecirc;n c&oacute; k&egrave;m giải th&iacute;ch đ&aacute;p &aacute;n chi tiết v&agrave; hướng dẫn c&aacute;ch tự n&acirc;ng band điểm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">IELTS l&agrave; c&aacute;nh cửa gi&uacute;p c&aacute;c bạn th&iacute; sinh hiện thực h&oacute;a ước mơ vươn ra thế giới. Ch&iacute;nh v&igrave; vậy, ngay từ b&acirc;y giờ, h&atilde;y nỗ lực luyện tập v&agrave; chuẩn bị cho b&agrave;i thi quan trọng n&agrave;y. Viện Ng&ocirc;n ngữ Hackers đ&atilde; bi&ecirc;n soạn bộ s&aacute;ch&nbsp;<strong>HACKERS IELTS</strong>&nbsp;gồm 4 cuốn tương đương với 4 kỹ năng nhằm giới thiệu tới c&aacute;c bạn phương ph&aacute;p luyện thi ph&ugrave; hợp nhất để c&oacute; thể đạt điểm cao trong b&agrave;i thi n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Luyện từng dạng b&agrave;i một c&aacute;ch c&oacute; hệ thống!</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bộ s&aacute;ch được thiết kế nhằm gi&uacute;p người học n&acirc;ng cao kỹ năng đọc hiểu th&ocirc;ng qua qu&aacute; tr&igrave;nh luyện tập từng dạng b&agrave;i một c&aacute;ch c&oacute; hệ thống. Ngo&agrave;i ra, đối với từng dạng b&agrave;i, bạn c&ograve;n được cung cấp những chiến lược l&agrave;m b&agrave;i cụ thể.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Luyện tập theo xu hướng ra đề mới nhất!</span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&aacute;c chủ đề trong bộ s&aacute;ch&nbsp;<strong>HACKERS IELTS</strong>&nbsp;được bi&ecirc;n soạn dựa tr&ecirc;n xu hướng ra đề mới nhất, nhờ vậy m&agrave; bạn c&oacute; thể chuẩn bị tốt nhất cho b&agrave;i thi thật. C&aacute;c b&agrave;i Actual Test cũng được bi&ecirc;n soạn ch&iacute;nh x&aacute;c theo h&igrave;nh thức đề thi thật mới nhất. V&igrave; vậy, bạn đ&atilde; c&oacute; trong tay đầy đủ t&agrave;i liệu để c&oacute; thể chuẩn bị kỹ lưỡng cho b&agrave;i thi IELTS.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&agrave;i liệu đặc biệt gi&uacute;p bạn đạt điểm cao!</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đặc biệt,&nbsp;<strong>HACKERS IELTS</strong>&nbsp;c&ograve;n c&oacute; phần tổng kết từ vựng &ldquo;khủng&rdquo; sau từng chương v&agrave; th&ecirc;m phần phụ lục về sự kh&aacute;c biệt giữa tiếng Anh Mỹ v&agrave; tiếng Anh Anh. Ngo&agrave;i ra, file MP3 đi k&egrave;m cung cấp cho người học c&aacute;c từ vựng xuất hiện trong c&aacute;c b&agrave;i đọc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ch&uacute;ng t&ocirc;i hi vọng rằng&nbsp;<strong>HACKERS IELTS</strong>&nbsp;sẽ trở th&agrave;nh cuốn cẩm nang hữu &iacute;ch gi&uacute;p bạn đạt được điểm số mong muốn trong b&agrave;i thi IELTS v&agrave; l&agrave; người bạn đồng h&agrave;nh đ&aacute;ng tin cậy của bạn tr&ecirc;n con đường chinh phục ước mơ.</span></p>\r\n', 199000, 'yes', 30, 'image_195509_1_708xnlNHf3z.jpg', '2022-08-06 03:32:22', 'Quang Nguyễn', NULL, NULL, 'active', 55, 36),
(59, 'Moby Dick Cá Voi Trắng - Văn Học Mỹ', '<p><span style=\"font-size:14px\"><strong>T&aacute;c Phẩm Chọn Lọc - Văn Học Mỹ - Moby Dick C&aacute; Voi Trắng</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng chỉ l&agrave; chuyện kể về thủy thủ Ishmael ch&aacute;n ngấy đất liền v&agrave; &ldquo;ho&agrave;ng tử đen&rdquo; Queequeg x&aacute;ch đầu l&acirc;u rao b&aacute;n tr&ecirc;n hải cảng t&igrave;nh cờ gặp nhau để rồi c&ugrave;ng xuống t&agrave;u mạo hiểm vượt tr&ugrave;ng khơi săn bắt c&aacute; voi..., cuốn tiểu thuyết c&ograve;n m&ocirc; tả tường tận thế giới nội t&acirc;m v&agrave; cuộc chiến hung bạo th&ugrave; hận trong h&agrave;nh tr&igrave;nh truy t&igrave;m một sinh vật khổng lồ nhưng c&ocirc; đơn l&agrave; Moby Dick của l&atilde;o thuyền trưởng Ahab độc đo&aacute;n...</span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&oacute; thể coi t&aacute;c phẩm l&agrave; một phần tự vấn trong qu&aacute; tr&igrave;nh suy ngẫm của t&aacute;c giả về nước Mỹ qu&aacute; khứ, hiện tại v&agrave; tương lai với t&acirc;m thế cứu chuộc tuyệt vời trong đức tin tuyệt đối về t&igrave;nh y&ecirc;u thương của lo&agrave;i người.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nh&agrave; văn Herman Melville (1819 - 1891) sinh ng&agrave;y 1.8.1819 tại Th&agrave;nh phố New York, Hoa Kỳ. &Ocirc;ng l&agrave; t&aacute;c giả của nhiều cuốn tiểu thuyết, truyện ngắn, thơ ca v&agrave; tiểu luận. Khi d&agrave;nh t&acirc;m sức viết Moby Dick - C&aacute; voi trắng, &ocirc;ng đ&atilde; đặt rất nhiều k&igrave; vọng, nhưng rất l&acirc;u sau khi &ocirc;ng qua đời, Moby Dick mới được hậu thế &ldquo;khai quật&rdquo; v&agrave; trở th&agrave;nh một trong những kiệt t&aacute;c h&agrave;ng đầu của văn học Mỹ cũng như thế giới.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dịch giả Hugo Nguyễn: Gi&aacute;o sư, Tiến sĩ Khoa học ng&agrave;nh Vật liệu v&agrave; Vi hệ cơ điện tử, sống nhiều năm ở ch&acirc;u &Acirc;u, giảng dạy v&agrave; nghi&ecirc;n cứu chủ yếu tại Trường Đại học Uppsala - Thụy Điển.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">V&igrave; y&ecirc;u tiếng Việt, &ocirc;ng cộng t&aacute;c với Nh&agrave; xuất bản Kim Đồng trong tư c&aacute;ch l&agrave; dịch giả.</span></p>\r\n', 320000, 'no', 15, '8935244872163bo5ufBVr.jpg', '2022-08-06 03:34:15', 'Quang Nguyễn', NULL, NULL, 'active', 59, 33),
(60, 'Truyện Ngắn Ernest Hemingway', '<p><span style=\"font-size:14px\">Tập truyện n&agrave;y được in lần đầu v&agrave;o năm 1996, được t&aacute;i bản năm 1998, năm t&ocirc;i bảo vệ luận &aacute;n tiến sĩ về nh&acirc;n vật của Ernest Hemingway. Đa số truyện trong tập n&agrave;y l&agrave; do t&ocirc;i dịch để phục vụ đề t&agrave;i nghi&ecirc;n cứu. C&ocirc;ng việc đ&oacute;, giờ nh&igrave;n lại quả qu&aacute; sức tưởng tượng. Thử h&igrave;nh dung, một người thực sự bắt đầu học tiếng Anh từ năm 1992 (hồi phổ th&ocirc;ng chỉ bập b&otilde;m đ&ocirc;i chữ), vừa học cao học, vừa l&agrave;m nghi&ecirc;n cứu sinh, vừa xoay xở đủ c&aacute;ch để kiếm sống, hằng đ&ecirc;m đ&aacute;nh vật với từng con chữ Anh ngữ trong s&aacute;ch của một bậc thầy văn chương để cố chuyển tải cho được hồn cốt của &ocirc;ng quả l&agrave; điều diệu kỳ. Phải n&oacute;i, t&ocirc;i đ&atilde; trưởng th&agrave;nh rất nhiều từ những trang viết của Hemingway. T&ocirc;i y&ecirc;u qu&yacute; v&agrave; ngưỡng mộ &ocirc;ng, một phần như l&agrave; kẻ mang nợ phong c&aacute;ch, tư tưởng &ocirc;ng. Lần in lại n&agrave;y, t&ocirc;i bổ sung th&ecirc;m hai truyện do L&ecirc; Nguy&ecirc;n Phương chuyển ngữ, đưa con số t&aacute;c phẩm l&ecirc;n 60 truyện. Hai mươi năm nay, v&igrave; vấn đề bản quyền m&agrave; s&aacute;ch chưa được in lại. Lần n&agrave;y, nhờ nỗ lực của Nh&agrave; xuất bản Tổng hợp Th&agrave;nh phố Hồ Ch&iacute; Minh m&agrave; s&aacute;ch được ra mắt bạn đọc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phải n&oacute;i, truyện ngắn Hemingway thuộc dạng kh&oacute; đọc v&agrave; kh&oacute; dịch. &Ocirc;ng viết theo lối tối giản v&agrave; rất nghi&ecirc;m ngặt trong c&acirc;u chữ. Tư tưởng của &ocirc;ng th&igrave; cũng rất phức tạp.&nbsp; &nbsp;&Ocirc;ng vừa l&agrave; người v&ocirc; thần vừa l&agrave; người s&ugrave;ng k&iacute;nh Ch&uacute;a. &Ocirc;ng gh&eacute;t Mussolini nhưng lại y&ecirc;u Italy. &Ocirc;ng l&agrave; người Mỹ nhưng lại l&agrave; bạn của Fidel Castro. &Ocirc;ng gần như kh&ocirc;ng sống nhiều ở Mỹ nhưng lu&ocirc;n đau đ&aacute;u nỗi niềm về tổ quốc. &Ocirc;ng l&agrave; tay săn bắn th&uacute; hoang c&oacute; hạng nhưng lại y&ecirc;u thi&ecirc;n nhi&ecirc;n v&agrave; lo&agrave;i vật đến cuồng si. &Ocirc;ng c&oacute; bốn vợ v&agrave; v&ocirc; kể t&igrave;nh nh&acirc;n nhưng c&aacute;c nghi&ecirc;n cứu lại chỉ ra c&oacute; vẻ &ocirc;ng c&oacute; vấn đề về giới&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuộc đời v&agrave; những s&aacute;ng tạo văn chương của &ocirc;ng, ắt hẳn h&agrave;ng trăm năm sau nữa vẫn chưa hết chuyện để luận b&agrave;n. C&ograve;n giảng dạy th&igrave; hầu hết c&aacute;c quốc gia tr&ecirc;n thế giới đều sử dụng s&aacute;ch &ocirc;ng trong gi&aacute;o dục phổ th&ocirc;ng hay đại học, sau đại học. Ở Việt Nam, đ&atilde; c&oacute; tr&ecirc;n 50 luận văn thạc sĩ, 7 luận &aacute;n tiến sĩ, 5 chuy&ecirc;n luận nghi&ecirc;n cứu về Hemingway. Kiệt t&aacute;c &Ocirc;ng gi&agrave; v&agrave; biển cả lu&ocirc;n xuất hiện trong chương tr&igrave;nh gi&aacute;o dục phổ th&ocirc;ng Việt Nam. Người Việt, bất luận l&agrave; trẻ hay gi&agrave;, nếu c&oacute; đến trường th&igrave; đều biết về một &ocirc;ng l&atilde;o ngoan cường chiến đấu với biển khơi h&ugrave;ng vĩ, cả với c&aacute; mập, để mang về đất liền con c&aacute; kiếm, dẫu chỉ c&ograve;n bộ xương. Hemingway gần như đ&atilde; trở th&agrave;nh một phần văn h&oacute;a của người Việt ở &yacute; ch&iacute; v&agrave; nghị lực phấn đấu cho một nền d&acirc;n chủ, tự do, cho một cuộc sống b&igrave;nh đẳng, hạnh ph&uacute;c với mọi người.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lần t&aacute;i bản n&agrave;y cũng chỉ l&agrave; sự tiếp nối. Hy vọng s&aacute;ch sẽ được bổ sung th&ecirc;m nhiều truyện trong tương lai. Ch&acirc;n th&agrave;nh cảm ơn sự đ&oacute;n nhận của bạn đọc. Mong nhận được những g&oacute;p &yacute; ch&acirc;n t&igrave;nh để s&aacute;ch ho&agrave;n thiện hơn khi c&oacute; điều kiện t&aacute;i bản.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>H&agrave; Nội, ng&agrave;y 01 th&aacute;ng 6 năm 2019</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>GS.TS. L&ecirc; Huy Bắc</strong></span></p>\r\n', 215000, 'no', 7, 'image_195509_1_6351BLdfyr5U.jpg', '2022-08-06 03:36:01', 'Quang Nguyễn', NULL, NULL, 'active', 60, 33),
(61, 'Kinh Tế Học Dành Cho Đại Chúng (Tái Bản 2018)', '<p><span style=\"font-size:14px\">&quot;Cuốn s&aacute;ch&nbsp;<strong>Kinh tế học d&agrave;nh cho đại ch&uacute;ng</strong>&nbsp;của Gi&aacute;o sư Steven E.Landsburg thuộc d&ograve;ng những t&aacute;c phẩm th&uacute; vị kiểu &quot;Kinh tế học h&agrave;i hước&quot; của Steven Levitt v&agrave; Steven Dubner. Với c&aacute;ch ph&acirc;n t&iacute;ch vấn đề ho&agrave;n to&agrave;n bằng kinh tế học, cuốn s&aacute;ch đưa ra c&aacute;ch giải th&iacute;ch hợp l&yacute; cho nhiều hiện tượng đ&aacute;ng ngạc nhi&ecirc;n, lẫn những c&acirc;u hỏi hay suy luận g&acirc;y ngạc nhi&ecirc;n cho nhiều hiện tượng vốn dĩ th&ocirc;ng thường như c&acirc;u chuyện gi&aacute; bỏng ng&ocirc; cực cao ở rạp chiếu b&oacute;ng, c&aacute;c buổi nhạc rock hết sạch v&eacute;, cho đến những sự hiểu nhầm về thuế, thất nghiệp hay cả luật chống đa th&ecirc;. Cuốn s&aacute;ch xứng đ&aacute;ng được đọc v&agrave; c&oacute; mặt tr&ecirc;n tủ s&aacute;ch của tất cả những độc giả quan t&acirc;m đến c&aacute;c vấn đề kinh tế - x&atilde; hội.&quot; - Đỗ Quốc Anh, Tiến sĩ Kinh tế Havard.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;Đọc cuốn s&aacute;ch cũng giống như trải nghiệm một bữa trưa từ tốn, nhẹ nh&agrave;ng, th&uacute; vị. Rất nhiều m&oacute;n ăn &ldquo;c&aacute;c c&acirc;u hỏi&rdquo; được b&agrave;y ra. Tại sao trong kinh tế thị trường, c&aacute; nh&acirc;n &ldquo;chỉ kiếm lợi cho ri&ecirc;ng m&igrave;nh&rdquo; lại c&oacute; thể bị dẫn dắt bởi &ldquo;một b&agrave;n tay v&ocirc; h&igrave;nh&rdquo; dẫn đến kết cục đẹp l&agrave; sự thịnh vượng chung của x&atilde; hội? Tại sao &ldquo;c&aacute;c loại thuế đều xấu&rdquo;? Tại sao đối với nh&agrave; kinh tế, &ldquo;ch&iacute;nh s&aacute;ch l&agrave; một sai lầm, nhưng l&agrave; một sai lầm &ldquo;thơm ngon&rdquo;?... Hứng th&uacute; đến bất ngờ v&igrave; ch&uacute;ng ta được nếm trải rất nhiều l&yacute; giải hợp l&yacute; cho những điều tưởng chừng v&ocirc; l&yacute; v&agrave; cả những điều v&ocirc; l&yacute; trong những h&agrave;nh vi dường như rất c&oacute; l&yacute;. V&agrave; để rồi ch&uacute;ng ta sẽ phần n&agrave;o hiểu được v&igrave; sao thị trường cũng &ldquo;tinh vi&rdquo;, &ldquo;diệu kỳ&rdquo; kh&ocirc;ng k&eacute;m Thi&ecirc;n nhi&ecirc;n v&agrave; c&ograve;n hơn nữa, sự tinh vi đ&oacute; &ldquo;thường xuy&ecirc;n gi&agrave;nh được những chiến c&ocirc;ng m&agrave; ngay cả Thi&ecirc;n nhi&ecirc;n cũng kh&ocirc;ng d&aacute;m thử.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;Hy vọng cuốn s&aacute;ch sẽ gi&uacute;p &iacute;ch cho tất cả những ai muốn nắm bắt c&aacute;c nguy&ecirc;n l&yacute; kinh tế học cơ bản, muốn vận dụng ch&uacute;ng v&igrave; một cuộc sống tốt hơn, đẹp hơn cho m&igrave;nh, người th&acirc;n v&agrave; x&atilde; hội.</span></p>\r\n', 149000, 'yes', 23, 'kinh_te_hoc_danh_cho_dai_chung_outline_6.4.2016-028kQJGiKa.jpg', '2022-08-06 03:39:01', 'Quang Nguyễn', '2022-08-06 03:43:09', 'Quang Nguyễn', 'active', 78, 38),
(62, 'Nền Kinh Tế Tăng Trưởng Và Sụp Đổ Như Thế Nào? (Tái Bản 2020)', '<p><span style=\"font-size:14px\">H&agrave;ng ng&agrave;y, tất cả ch&uacute;ng ta đều tham gia v&agrave;o c&aacute;c hoạt động kinh tế. C&aacute;c phương tiện th&ocirc;ng tin đại ch&uacute;ng như truyền h&igrave;nh, b&aacute;o ch&iacute;, internet v.v&hellip; cũng đầy rẫy những b&agrave;i viết, ph&acirc;n t&iacute;ch hay đưa tin về c&aacute;c sự kiện kinh tế.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tuy nhi&ecirc;n, với đa số người d&acirc;n, kinh tế học dường như vẫn l&agrave; điều g&igrave; đ&oacute; kh&oacute; hiểu, trừu tượng, một lĩnh vực m&agrave; chỉ c&aacute;c &ldquo;chuy&ecirc;n gia&rdquo; mới biết v&agrave; d&aacute;m b&agrave;n đến m&agrave; th&ocirc;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ch&iacute;nh v&igrave; lẽ đ&oacute;, trong những năm gần đ&acirc;y nhiều học giả kinh tế nước ngo&agrave;i đ&atilde; bắt đầu bi&ecirc;n soạn những cuốn s&aacute;ch đơn giản, dễ hiểu, nhằm giới thiệu đến độc giả đại ch&uacute;ng những kh&aacute;i niệm cơ bản nhất của k&iacute;nh tế học, hy vọng gi&uacute;p lĩnh vực kh&ocirc; khan n&agrave;y trở n&ecirc;n gần gũi hơn với mọi người. Cuốn s&aacute;ch n&agrave;y cũng nằm trong số đ&oacute;.</span></p>\r\n', 152000, 'yes', 25, 'image_195509_1_40495RBmerlWa.jpg', '2022-08-06 03:41:47', 'Quang Nguyễn', NULL, NULL, 'active', 63, 38),
(63, 'Phân Tích Tài Chính Doanh Nghiệp', '<p><span style=\"font-size:14px\">Ph&acirc;n t&iacute;ch t&agrave;i ch&iacute;nh doanh nghiệp l&agrave; t&agrave;i liệu tham khỏa c&oacute; gi&aacute; trị cả về l&yacute; thuyết v&agrave; ứng dụng thực tiễn. Ngo&agrave;i những l&yacute; thuyết cơ bản, chuy&ecirc;n s&acirc;u về ph&acirc;n t&iacute;ch t&agrave;i ch&iacute;nh doanh nghiệp . Gi&aacute;o tr&igrave;nh c&ograve;n tập hợp c&aacute;c t&igrave;nh huống ph&acirc;n t&iacute;ch tr&ecirc;n cơ sở số liệu thực tế của nhiều loại h&igrave;nh doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trong lần t&aacute;i bản n&agrave;y , ch&uacute;ng t&ocirc;i chỉnh sửa một số nội dung so với lần t&aacute;i bản thứ tư; Tuy nhi&ecirc;n , ch&uacute;ng t&ocirc;i vẫn chưa ho&agrave;n to&agrave;n h&agrave;i l&ograve;ng với những kết quả đạt được v&agrave; sẽ tiếp tục ho&agrave;n thieejnt rong thời gian tới. Rất mong nhận được những &yacute; kiến đ&oacute;ng g&oacute;p ch&acirc;n th&agrave;nh của bạn đọc để cuốn s&aacute;ch được ho&agrave;n thiện hơn.</span></p>\r\n', 159000, 'yes', 37, 'image_231381UqQKLWp1.jpg', '2022-08-06 03:50:58', 'Quang Nguyễn', NULL, NULL, 'active', 72, 38),
(64, '7 Bước Để Tự Do Tài Chính', '<p><span style=\"font-size:14px\">&ldquo;Vấn đề t&agrave;i ch&iacute;nh c&aacute; nh&acirc;n kh&ocirc;ng chỉ phức tạp, m&agrave; c&ograve;n cực kỳ nh&agrave;m ch&aacute;n. Kh&ocirc;ng phải ai cũng c&oacute; thời gian đọc mớ s&aacute;ch t&agrave;i ch&iacute;nh đầy thuật ngữ mang t&iacute;nh h&agrave;n l&acirc;m. Đ&oacute; l&agrave; l&yacute; do t&ocirc;i viết cuốn s&aacute;ch n&agrave;y, nhằm mang đến một phương ph&aacute;p gi&aacute;o dục t&agrave;i ch&iacute;nh mới đơn giản m&agrave; h&oacute;m hỉnh. T&ocirc;i ở đ&acirc;y để k&eacute;o bạn ra khỏi tuyệt vọng, lấy lại nhiệt huyết v&agrave; tự tin, đồng thời c&oacute; thể nắm chắc đồng tiền trong tay. V&agrave; qu&aacute; tr&igrave;nh n&agrave;y sẽ rất vui lu&ocirc;n. T&ocirc;i thề, t&ocirc;i hứa, t&ocirc;i đảm bảo...&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuốn s&aacute;ch tr&igrave;nh b&agrave;y c&aacute;c phương ph&aacute;p quản l&yacute; t&agrave;i ch&iacute;nh c&aacute; nh&acirc;n, bao gồm những vấn đề như cắt giảm chi ti&ecirc;u, kiếm th&ecirc;m thu nhập thụ động, đầu tư &amp; bảo hiểm... qua g&oacute;c nh&igrave;n h&agrave;i hước, bằng những c&acirc;u văn d&iacute; dỏm vui nhộn. V&agrave; rất th&iacute;ch hợp cho những người trẻ đang muốn tiết kiệm tiền để mua nh&agrave;, x&acirc;y dựng gia đ&igrave;nh hay đầu tư cho tương lai.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&aacute;c giả sẽ cung cấp cho bạn tất cả những kiến thức cần biết về chủ đề n&agrave;y. C&oacute; thể n&oacute;i về rất nhiều chủ đề, bởi t&ocirc;i đ&atilde; gạt bỏ hết những phần r&acirc;u ria v&agrave; đi thẳng v&agrave;o vấn đề. Điều tuyệt vời của cuốn s&aacute;ch n&agrave;y l&agrave; bạn c&oacute; thể mở ngay tới phần m&agrave; m&igrave;nh muốn t&igrave;m hiểu. Nếu bạn thắc mắc xem n&ecirc;n đầu tư v&agrave;o loại h&igrave;nh chứng kho&aacute;n n&agrave;o, h&atilde;y giở lu&ocirc;n tới chương 12. Nếu bạn muốn t&igrave;m phương ph&aacute;p tiết kiệm cho con bạn đi học, h&atilde;y tiến thẳng tới Chương 6. Cuốn s&aacute;ch n&agrave;y d&agrave;nh cho c&aacute;c độc giả ở mọi cấp độ th&ocirc;ng hiểu về t&agrave;i ch&iacute;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bảy bước trong cuốn s&aacute;ch sẽ hướng dẫn bạn c&aacute;ch lập t&agrave;i khoản tiết kiệm, gồm khoản d&agrave;nh cho trường hợp khẩn cấp v&agrave; khoản ngắn hạn cho l&uacute;c về hưu. Bạn cũng sẽ được học v&agrave; thực h&agrave;nh lu&ocirc;n một kế hoạch nh&agrave;n tản để trả hết nợ. Bạn sẽ biết m&igrave;nh cần bao nhi&ecirc;u th&aacute;ng để thực hiện được hết c&aacute;c mục ti&ecirc;u t&agrave;i ch&iacute;nh nhằm nắm chặt được t&uacute;i tiền của m&igrave;nh. Bạn sẽ bắt đầu cảm thấy dễ chịu khi nghĩ đến chuyện tiền nong v&agrave; gấp đ&ocirc;i niềm vui khi quản l&yacute; tiền. H&atilde;y ch&agrave;o tạm biệt cuộc sống vất vưởng chạy ăn từng ng&agrave;y đi, v&igrave; n&oacute; đ&atilde; ch&iacute;nh thức tr&ocirc;i v&agrave;o dĩ v&atilde;ng. H&atilde;y ch&agrave;o đ&oacute;n cuộc sống mới, nơi bạn kh&ocirc;ng c&ograve;n phải trăn trở về vấn đề tiền bạc.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\">Những mẹo hay bạn sắp đọc được chắc chắn sẽ dẫn đến con đường tự chủ 100% về t&agrave;i ch&iacute;nh. Tất cả những g&igrave; bạn cần l&agrave;m để bắt đầu kiểm so&aacute;t tốt t&igrave;nh h&igrave;nh t&agrave;i ch&iacute;nh của m&igrave;nh l&agrave; cứ đọc th&ocirc;i. Mỗi chương sẽ cung cấp cho bạn những kiến thức mới để bắt tay v&agrave;o l&agrave;m ngay v&agrave; c&oacute; thể trả hết nợ nần, tiết kiệm được nhiều hơn v&agrave; kh&ocirc;ng vướng phải bất cứ vấn đề t&agrave;i ch&iacute;nh n&agrave;o. H&atilde;y bắt đầu kiểm so&aacute;t t&igrave;nh h&igrave;nh của bản th&acirc;n ngay bằng việc cố gắng đọc một chương mỗi ng&agrave;y để c&oacute; được cuộc sống sung t&uacute;c vẫn hằng ao ước.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bạn đ&atilde; sẵn s&agrave;ng để tư duy v&agrave; h&agrave;nh động như giới thượng lưu chưa? Đi lấy đồ ăn vặt ra nhấm nh&aacute;p v&agrave; bắt đầu đọc s&aacute;ch n&agrave;o. C&oacute; cả một ch&acirc;n trời mới rộng lớn đang vẫy gọi ch&uacute;ng ta đ&oacute;.</span></p>\r\n', 139000, 'no', 28, 'image_1959021PT0WRui.jpg', '2022-08-06 03:52:37', 'Quang Nguyễn', NULL, NULL, 'active', 82, 38),
(65, 'Tiền Tệ Và Chuyện Làm Giàu An Toàn', '<p><span style=\"font-size:14px\">Hằng ng&agrave;y, qua phim ảnh v&agrave; tin tức truyền th&ocirc;ng, ch&uacute;ng ta thấy rất nhiều chuyện đau l&ograve;ng do sự t&uacute;ng thiếu tiền bạc g&acirc;y n&ecirc;n: Nhiều người đ&atilde; phải vay nặng l&atilde;i, rồi l&acirc;m v&agrave;o t&igrave;nh huống buộc chấp nhận l&agrave;m những việc phi ph&aacute;p, l&agrave;m ảnh hưởng đến phẩm gi&aacute; của bản th&acirc;n, thậm ch&iacute; c&oacute; thể bị mất mạng. Hoặc, nhiều người nghĩ rằng m&igrave;nh cần nhanh ch&oacute;ng tho&aacute;t khỏi cảnh ngh&egrave;o bằng mọi gi&aacute; n&ecirc;n họ c&agrave;ng dễ bị lừa gạt bởi những tr&ograve; bịp bợm, giăng bẫy ng&agrave;y c&agrave;ng tinh vi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trước niềm tin cho rằng người ta kh&ocirc;ng thể n&agrave;o sống an vui khi l&acirc;m v&agrave;o cảnh t&uacute;ng thiếu triền mi&ecirc;n, n&ecirc;n t&aacute;c giả cuốn s&aacute;ch n&agrave;y &ndash;&nbsp;<strong>GS. TS. Huỳnh Ngọc Phi&ecirc;n</strong>&nbsp;vốn l&agrave; một nh&agrave; gi&aacute;o c&oacute; &iacute;t nhiều kinh nghiệm trong việc đầu tư kinh doanh &ndash; cảm thấy cần phải tổng hợp c&aacute;c &yacute; tưởng v&agrave; kinh nghiệm từ những tư liệu qu&yacute; gi&aacute; để bi&ecirc;n soạn n&ecirc;n cuốn&nbsp;<strong>Tiền tệ v&agrave; chuyện l&agrave;m gi&agrave;u an to&agrave;n</strong>, với mục đ&iacute;ch truyền lại những phương ph&aacute;p l&agrave;m gi&agrave;u th&agrave;nh c&ocirc;ng cho thế hệ sau.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Như tựa s&aacute;ch đ&atilde; ghi r&otilde;, c&aacute;ch l&agrave;m gi&agrave;u ở đ&acirc;y l&agrave; an to&agrave;n v&agrave; chắc chắn v&igrave; n&oacute; dựa tr&ecirc;n những nguy&ecirc;n tắc đ&atilde; được kiểm nghiệm qua thực tế, những nguy&ecirc;n tắc kh&ocirc;ng nhắm v&agrave;o việc lợi dụng hoặc khai th&aacute;c người kh&aacute;c để l&agrave;m lợi cho m&igrave;nh. Tr&aacute;i lại, ch&uacute;ng gần như những c&aacute;ch tu tập v&agrave; thực hiện kỷ luật cho ch&iacute;nh bản th&acirc;n mỗi người. Bằng c&aacute;ch &aacute;p dụng những nguy&ecirc;n tắc đ&oacute;, mỗi người ch&uacute;ng ta sẽ trở n&ecirc;n &ldquo;gi&agrave;u c&oacute;&rdquo; hơn, &ldquo;tốt&rdquo; hơn về nhiều mặt v&agrave; hữu &iacute;ch hơn cho nhiều người kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuốn s&aacute;ch gồm hai phần.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&bull; Phần I &ndash; C&aacute;c kh&aacute;i niệm căn bản:</strong>&nbsp;Tr&igrave;nh b&agrave;y những kh&aacute;i niệm căn bản li&ecirc;n quan đến tiền tệ, cho bạn sự hiểu biết nền tảng về c&aacute;ch thức hoạt động của tiền tệ, về vai tr&ograve; của ch&iacute;nh&nbsp;phủ v&agrave; c&aacute;c định chế t&agrave;i ch&iacute;nh như ng&acirc;n h&agrave;ng trung ương.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&bull; Phần II &ndash; T&agrave;i ch&iacute;nh c&aacute; nh&acirc;n v&agrave; L&agrave;m gi&agrave;u:</strong>&nbsp;Đ&acirc;y l&agrave; phần quan trọng của cuốn s&aacute;ch với mục đ&iacute;ch ch&iacute;nh l&agrave; truyền đạt những c&aacute;ch l&agrave;m gi&agrave;u an to&agrave;n. &ldquo;Gi&agrave;u&rdquo; ở đ&acirc;y c&oacute; &yacute; nghĩa tương đối, kh&ocirc;ng thể hiện bằng một con số cụ thể m&agrave; bằng mức độ của sự độc lập t&agrave;i ch&iacute;nh theo nghĩa: Một người đạt được sự độc lập t&agrave;i ch&iacute;nh khi của cải đ&atilde; t&iacute;ch lũy được c&oacute; thể sản sinh ra mức thu nhập &ldquo;đủ&rdquo; cho m&igrave;nh (v&agrave; gia đ&igrave;nh) sống đến cuối đời m&agrave; kh&ocirc;ng cần phải l&agrave;m việc để kiếm tiền.</span></p>\r\n', 150000, 'no', 45, 'image_196895CcQOaGF0.jpg', '2022-08-06 03:53:51', 'Quang Nguyễn', NULL, NULL, 'active', 83, 38),
(66, 'Rón Rén Gửi Tiền Tiết Kiệm Không Bằng Mạnh Dạn Đầu Tư', '<p><span style=\"font-size:14px\"><strong>R&oacute;n r&eacute;n gửi tiền tiết kiệm kh&ocirc;ng bằng mạnh dạn đầu tư&nbsp;</strong>&ndash;&nbsp;<em>Nghệ thuật quản l&yacute; t&agrave;i ch&iacute;nh gi&uacute;p bạn đầu tư đ&acirc;u tr&uacute;ng đ&oacute;</em>, l&agrave; chia sẻ về đầu tư v&agrave; quản l&yacute; t&agrave;i ch&iacute;nh của t&aacute;c giả Trần Phi Quy&ecirc;n &ndash; một ph&oacute;ng vi&ecirc;n, từng l&agrave; người dẫn chương tr&igrave;nh đ&agrave;i TTV, bản tin t&agrave;i ch&iacute;nh nổi tiếng của Đ&agrave;i Loan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Từ trải nghiệm bản th&acirc;n v&agrave; kinh nghiệm về đầu tư t&agrave;i ch&iacute;nh khi b&agrave; l&agrave; dẫn chương tr&igrave;nh bản tin t&agrave;i ch&iacute;nh của đ&agrave;i truyền h&igrave;nh Đ&agrave;i Loan, t&aacute;c giả chia sẻ kinh nghiệm quản l&yacute; t&agrave;i ch&iacute;nh v&agrave; đầu tư t&agrave;i ch&iacute;nh d&agrave;nh cho những người c&oacute; nguồn vốn nhỏ, đặc biệt l&agrave; phụ nữ để c&oacute; được sự độc lập v&agrave; tự chủ về t&agrave;i ch&iacute;nh. Kh&ocirc;ng cần thiết phải trở n&ecirc;n gi&agrave;u c&oacute; mới đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuốn s&aacute;ch gồm 3 chương, tr&igrave;nh b&agrave;y về c&aacute;c vấn đề: quản l&yacute; t&agrave;i ch&iacute;nh c&aacute; nh&acirc;n, đầu tư bất động sản, đầu tư cổ phiếu v&agrave; chứng kho&aacute;n hay bảo hiểm. B&agrave; ph&acirc;n t&iacute;ch một c&aacute;ch cụ thể kh&ocirc;ng giấu giếm về những ưu nhược điểm của c&aacute;c loại h&igrave;nh đầu tư đ&oacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Quản l&yacute; t&agrave;i ch&iacute;nh c&aacute; nh&acirc;n, nếu ngay từ nhỏ đ&atilde; được sống trong m&ocirc;i trường cha mẹ c&oacute; th&oacute;i quen quản l&yacute; t&agrave;i ch&iacute;nh khoa học, cũng c&oacute; thể coi con c&aacute;i được hưởng l&agrave; t&agrave;i sản thừa kế v&ocirc; c&ugrave;ng qu&yacute; gi&aacute; từ cha mẹ. Tự chủ được quản l&yacute; t&agrave;i ch&iacute;nh c&aacute; nh&acirc;n gi&uacute;p cho ch&uacute;ng ta kh&ocirc;ng bị rơi v&agrave;o ho&agrave;n cảnh bất ngờ. Theo b&agrave; quản l&yacute; t&agrave;i ch&iacute;nh giỏi kh&ocirc;ng phải l&agrave; &ldquo;giữ tiền giỏi&rdquo; hay &ldquo;tiết kiệm tiền giỏi&rdquo; m&agrave; cần đầu tư để sinh lời. Theo b&agrave;, quản l&yacute; t&agrave;i ch&iacute;nh gi&uacute;p bạn c&oacute; quyền lựa chọn v&agrave; gi&uacute;p bạn thực hiện được ước mơ của m&igrave;nh.&nbsp;<em>&ldquo;Khi c&oacute; tiền, nếu h&ocirc;n nh&acirc;n kh&ocirc;ng hạnh ph&uacute;c, bạn c&oacute; quyền quyết định bỏ đi, cũng c&oacute; thể quyết định giữ lại; nhưng n&ecirc;u kh&ocirc;ng c&oacute; tiền, th&igrave; thường thường chỉ c&oacute; một lựa chọn l&agrave; ở lại. Khi kh&ocirc;ng c&oacute; tiền, nếu sếp qu&aacute; đ&aacute;ng gh&eacute;t, bạn c&oacute; thể bỏ đi,cũng c&oacute; thể nhẫn nại chịu lấy; nhưng kh&ocirc;ng c&oacute; tiền, th&igrave; chỉ c&oacute; một lựa chọn l&agrave; nhẫn nại m&agrave; th&ocirc;i. Quyền lựa chọn cực kỳ quan trọng trong cuộc đời.&rdquo;&nbsp;</em>Quản l&yacute; t&agrave;i ch&iacute;nh để thực hiện ước mơ của m&igrave;nh, chứ quản l&yacute; t&agrave;i ch&iacute;nh kh&ocirc;ng phải học kỹ xảo để l&agrave;m gi&agrave;u. Khi bạn biết ước mơ của m&igrave;nh đang nằm ở đ&acirc;u, th&igrave; bạn sẽ kh&ocirc;ng v&igrave; chăm ch&uacute; quản l&yacute; t&agrave;i ch&iacute;nh m&agrave; bỏ lỡ qu&atilde;ng thời gian đẹp nhất của cuộc đời.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, việc đầu tư quan trọng nhất theo b&agrave; l&agrave; lu&ocirc;n lu&ocirc;n tu&acirc;n thủ nguy&ecirc;n tắc, gi&agrave;nh 10% thu nhập để đầu tư cho bản th&acirc;n, c&oacute; thể l&agrave; mua một v&agrave;i cuốn s&aacute;ch, hay tham gia c&aacute;c kh&oacute;a học ph&aacute;t triển bản th&acirc;n, hoặc c&aacute;c kh&oacute;a học n&acirc;ng cao sức khỏe... Khi bản th&acirc;n c&oacute; bước tiến mới, chắc chắn thu nhập sẽ gia tăng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đối với việc đầu tư bảo hiểm, b&agrave; cho rằng bảo hiểm phải t&iacute;nh đến l&agrave; &ldquo;bao nhi&ecirc;u l&agrave; đủ&rdquo; chứ kh&ocirc;ng phải &ldquo;c&agrave;ng nhiều c&agrave;ng tốt&rdquo;, cũng kh&ocirc;ng n&ecirc;n coi bảo hiểm l&agrave; một c&ocirc;ng cụ t&iacute;ch lũy hay một c&ocirc;ng cụ kiếm tiền.&nbsp;<em>&ldquo;Mua bảo hiểm cũng giống t&ocirc;i như mua một v&agrave;i vi&ecirc;n thuốc mang theo người khi đi du lịch, để ph&ograve;ng vấn đề sức khỏe khi đang ở nước ngo&agrave;i, nhưng t&ocirc;i kh&ocirc;ng bao giờ d&aacute;m nghĩ đến chuyện v&igrave; sợ l&atilde;ng ph&iacute; m&agrave; uống cho hết thuốc. Bản chất của bảo hiểm l&agrave; mua c&aacute;i &ldquo;nhỡ đ&acirc;u&rdquo; chứ kh&ocirc;ng phải để lĩnh tiền.</em>&nbsp;V&igrave; vậy khi mua bảo hiểm n&ecirc;n mua bảo hiểm tai nạn, bảo hiểm nh&acirc;n thọ, bảo hiểm y tế định k&igrave;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đầu tư bất động sản đừng vội tin v&agrave;o những căn nh&agrave; mẫu hoặc phố cảnh lung linh trong th&ocirc;ng tin rao b&aacute;n. Đầu tư v&agrave;o thị trường cổ phiếu nếu bạn muốn đầu tư chỉ dựa tr&ecirc;n th&ocirc;ng tin tr&ecirc;n ti vi hoặc b&aacute;o ch&iacute;, th&igrave; thường bạn sẽ l&agrave; người bị m&oacute;c sạch t&uacute;i. Bởi ngay cả ph&oacute;ng vi&ecirc;n đưa tin cũng chỉ tiếp cận th&ocirc;ng tin đ&atilde; truyền đi lần thứ N-1 rồi, ngoại trừ một số ph&oacute;ng vi&ecirc;n kết hợp với những c&ocirc;ng ty c&ograve; mồi, họ kh&ocirc;ng phải l&agrave; người đưa tin, m&agrave; l&agrave; người tạo ra tin tức.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&oacute;m lại theo b&agrave;:&nbsp;<em>Kiếm tiền quan trọng hơn giữ tiền v&agrave; nếu chỉ chăm chăm kiếm tiền th&igrave; chưa thể gọi l&agrave; gi&agrave;u c&oacute;, biết ti&ecirc;u tiền mới l&agrave; người sẽ kiếm được tiền.</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>NHẬN X&Eacute;T VỀ T&Aacute;C PHẨM</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuốn s&aacute;ch của Trần Phi Quy&ecirc;n nội dung s&aacute;t thực, phong ph&uacute;, cung cấp tri thức v&agrave; khơi gợi l&ograve;ng can đảm để mọi người đầu tư l&agrave;m gi&agrave;u khi chỉ c&oacute; số vốn nhỏ. Quả thực kiếm tiền bằng c&aacute;ch đầu tư v&agrave; t&iacute;ch lũy l&agrave; một c&aacute;ch l&agrave;m tương đối nhanh! Đọc s&aacute;ch của c&ocirc; ấy ch&uacute;ng ta sẽ thấy đầu tư kiếm tiền kh&ocirc;ng phải l&agrave; qu&aacute; kh&oacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Nhan Bỉnh Lập</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Gi&aacute;m đốc điều h&agrave;nh c&ocirc;ng ty TNHH DTZ Hồng K&ocirc;ng</em></span></p>\r\n', 95000, 'yes', 32, 'image_244718_1_4402C2fWb4LP.jpg', '2022-08-06 03:55:03', 'Quang Nguyễn', NULL, NULL, 'active', 85, 38);

